INSERT INTO pdf_pages VALUES(23,'pdfs/Übungsklausur Buchführung Jahresabschluss SoSe23 Lösung Text.pdf',6,replace('MODULNAME: BUCHFÜHRUNG UND JAHRESABSCHLUSS  \nMODULKÜRZEL: DSBFJA01 \nÜBUNGSKLAUSUR \n\n9.  Erstelle die Buchungssätze zu folg. Geschäftsvorfall. Bitte dabei 19% USt berücksichtigen. \n\n3 Punkte \n\nVerkauf von Waren im Nettowert von 12.000 EUR. Wir vereinbaren mit dem Käufer, auf diesen Betrag noch 10% \nRabatt einzuräumen. \n\nKonto \n\nBetrag \n\nAN \n\nKonto \n\nForderungen L/L \n\n12.852,00 \n\nUmsatz (Warenverkauf) \n\nUSt \n\nBetrag \n\n10.800,00 \n\n2.052,00 \n\nLösung: Lehrbuch Döring, S.66 Fall B. \n\n10.  Erstelle die Buchungssätze zu folg. Geschäftsvorfall. Bitte dabei 19% USt berücksichtigen. \n\n4 Punkte \n\nUnser Warenkonto beim Lieferanten „L“ weist nach Abschluss des Geschäftsjahres einen Nettoumsatz von \n80.000 EUR auf. Lt. Vereinbarung erhalten wir auf diesen Umsatz einen Bonus von 3% per Banküberweisung. \nEs sind 19% USt zu berücksichtigen. \n\nKonto \n\nBetrag \n\nAN \n\nKonto \n\nWareneinkauf \n\nVorsteuer \n\n80.000,00 \n\n15.200,00 \n\nBank \n\nBetrag \n\n95.200,00 \n\nKonto \n\nBetrag \n\nAN \n\nKonto \n\nBetrag \n\nBank \n\n2.856,00 \n\nLieferantenboni \n\nVorsteuer \n\nKonto \n\nBetrag \n\nAN \n\nKonto \n\nLieferantenboni \n\n2.400,00 \n\nWareneinkauf \n\n2.400,00 \n\n456,00 \n\nBetrag \n\n2.400,00 \n\nSeite 6 von 8 \n\n \n \n \n \n \n \n \n \n \n \n \n \n \n \n  \n \n \n \n \n \n \n \n \n \n \n \n  \n \n \n \n \n \n \n \n \n \n  \n \n \n \n \n \n \n \n \n \n \n \n  \n \n \n \n \n \n \n','\n',char(10)),'imgs/Übungsklausur Buchführung Jahresabschluss SoSe23 Lösung Text_6.png');
