INSERT INTO pdf_pages VALUES(25,'pdfs/Übungsklausur Buchführung Jahresabschluss SoSe23 Lösung Text.pdf',8,replace('MODULNAME: BUCHFÜHRUNG UND JAHRESABSCHLUSS  \nMODULKÜRZEL: DSBFJA01 \nÜBUNGSKLAUSUR \n\nBlock „Buchungsübung“ \n\nGesamtaufgabe 42 Punkte. \n\n12.  Gegeben sei für die Firma „Tim und Ute OHG“ die beigefügte Bilanz zum 31.12.2020 (=Eröffnungsbilanz). \n\nFühre folgende Buchungen im Grundbuch und Hauptbuch aus. Nutze hierfür die beigefügten Formblätter. \n\nA. \n\nB. \n\nEröffne die Bestandskonten (3 Punkte) \n\nBuche folgende Geschäftsvorfälle (je 2 Punkte = 28 Punkte) \n\na)  Tim zahlt 2.000 EUR auf das Bankkonto der OHG ein \n\nb)  Ute legt 3.000 EUR in bar ein \n\nc)  Verkauf von Dienstleistungen für 2.000 EUR + 19% USt gegen ÜW in 30 Tagen \n\nd)  Beauftragung einer Reparatur von 500 EUR incl. USt \n\ne)  Zahlungseingang 2.308,60 EUR  \n\nfür c) per ÜW, nach Abzug von 3% Skonto \n\nf)  Kauf Fahrrad für den Fuhr-park für 100 EUR zzgl. Ust mit sofort. ÜW \n\ng)  Zahlung von 100 EUR Zinsen per ÜW \n\nh)  Ute entnimmt 1.500 EUR vom Bankkonto per ÜW \n\ni)  Rückzahlung des Bankdarlehens 3.000 EUR durch ÜW \n\nj)  Einforderung des Skontos aus e) vom Kunden \n\nk)  Rechnung für die Reparatur von 500 EUR incl. USt erhalten. \n\nl)  Erhalt des Skontos aus e) über 71,40 EUR vom Kunden \n\nm)  Zahlung der Reparaturrechnung über 500 EUR incl. USt \n\nC. \n\nD. \n\nE. \n\nF. \n\nn)  Überweisung einer Anzahlung von 1.500 EUR zzgl. USt für eine Warenbestellung \n\nSchließe die Erfolgskonten über das GuV-Konto ab und ermittle das Ergebnis. (3 Punkte) \n\nSchließe das Ergebnis in das Eigenkapital ab. (2 Punkte) \n\nSchließe die Bestandskonten in das Schlussbilanzkonto (SBK) ab. (3 Punkte) \n\nStelle die Bilanz und GuV auf. (3 Punkte) \nÜbertrage dazu die SBK-Werte in die Bilanz und die GuV auf den beigefügten Formblättern. \n\nLösung: Siehe sep. Datei. \n\nSeite 8 von 8 \n\n \n \n \n \n \n \n \n \n \n \n','\n',char(10)),'imgs/Übungsklausur Buchführung Jahresabschlus