INSERT INTO pdf_pages VALUES(149,'pdfs/DSBITCDM01_Vorlesung_Lektion1.pdf',7,replace('LERNZIELE\n\nNach erfolgreichem Abschluss sind die Studierenden in der Lage,\n− die volkswirtschaftliche Relevanz des Dienstleistungssektors sowie seine Entwicklungs-\n\ndynamik zu diskutieren und zu analysieren.\n\n− verhaltenstheoretischer Konzepte zum Verständnis des Kauf- und Konsumverhaltens \n\nvon Dienstleistungs- und IT-Kunden vorzustellen.\n\n− die konstitutiven Dienstleistungsmerkmale und grundlegende Dienstleistungstypologien \n\nzum Verständnis von Dienstleistungs- und IT-bezogene Märkten zu erläutern.\n− die grundlegenden Managementanforderungen für die Planung, Organisation, \n\nSteuerung und Kontrolle von Dienstleistungsbetrieben zu analysieren.\n\n− grundsätzliche Anforderungen an ein kundenorientiertes IT-Consulting zu verstehen und \n\numzusetzen.\n\n− strategische, IT-bezogene Entscheidungen in Dienstleistungsunternehmen zu verstehen.\n\n29.04.2024\n\n7\n\nIT-Consulting & Dienstleistungsmanagement – SoSe 2024 - Dozent Patrick Schmidt, M.Sc.\n\n','\n',char(10)),'imgs/DSBITCDM01_Vorlesung_Lektion1_7.png');
