INSERT INTO pdf_pages VALUES(165,'pdfs/DSBITCDM01_Vorlesung_Lektion1.pdf',23,replace('VOLKSWIRTSCHAFTLICHE BETRACHTUNG\n\n−Global liegt der Anteil des Dienstleistungssektors an der Wertschöpfung bei ca. 65% des \nBIP (= Bruttoinlandsprodukts)\n−Aufgrund des weltweit schnell wachsenden Dienstleistungssektors werden neue \nArbeitsplätze hauptsächlich im Bereich Dienstleistungen geschaffen (v.a. Bildung, \nGesundheitswesen etc.)\n−Bei der Entwicklung von Volkswirtschaften bilden Dienstleistungen somit den größten \nTeil der Wirtschaftsleistung\n−Zur Vertiefung dieser volkswirtschaftlichen Zusammenhänge eignet sich die Einteilung \nder Volkswirtschaften in Sektoren (sog. Drei-Sektoren-Theorie)\n\nQuelle: Wirtz, 2016, S. 7ff.;   nach Haller et al. 2021, S. 2\n\n29.04.2024\n\n23\n\nIT-Consulting & Dienstleistungsmanagement – SoSe 2024 - Dozent Patrick Schmidt, M.Sc.\n\n','\n',char(10)),'imgs/DSBITCDM01_Vorlesung_Lektion1_23.png');
