INSERT INTO pdf_pages VALUES(178,'pdfs/DSBITCDM01_Vorlesung_Lektion1.pdf',36,replace('ÜBUNG\n\nAUFGABEN ZUR LÜNENDONKLISTE (1)\n\nErläutern Sie den Unterschied zwischen\n−IT-Beratung und Systemintegration\n−IT-Service-Unternehmen\n\n29.04.2024\n\n36\n\nIT-Consulting & Dienstleistungsmanagement – SoSe 2024 - Dozent Patrick Schmidt, M.Sc\n\n','\n',char(10)),'imgs/DSBITCDM01_Vorlesung_Lektion1_36.png');
INSERT INTO pdf_pages VALUES(179,'pdfs/DSBITCDM01_Vorlesung_Lektion1.pdf',37,replace('ÜBUNG\n\nAUFGABEN ZUR LÜNENDONKLISTE (2)\n\nAnalysieren Sie jeweils\n\n−ein großes Beratungshaus\n−ein mittelständisches Beratungshaus\n−ein IT-Service-Unternehmen\n\nUnd beantworten Sie im Zuge dessen insbesondere folgende Frage:\n\n−Welche Arten von IT-Leistungen stellen diese Unternehmen bereit?\n\n29.04.2024\n\n37\n\nIT-Consulting & Dienstleistungsmanagement – SoSe 2024 - Dozent Patrick Schmidt, M.Sc.\n\n','\n',char(10)),'imgs/DSBITCDM01_Vorlesung_Lektion1_37.png');
INSERT INTO pdf_pages VALUES(180,'pdfs/DSBITCDM01_Vorlesung_Lektion1.pdf',38,replace('ÜBUNG\n\nAUFGABEN ZUR LÜNENDONKLISTE (3)\n\nErstellen Sie eine Präsentation (je Thema: 1-2 Screenshots + Stichworte). \nBehandeln Sie dabei folgende Themen zum IT-Markt:\n\n−Branchen (S. 40)\n−Near/Offshore (S. 46)\n−Nachfragethemen (S. 49, auch S. 75ff.)\n−Vertragsmodelle (S. 51)\n−Honorarsätze (S. 53)\n−Interne Dienstleister (S. 60)\n−IT der Zukunft (S. 80)\n−Fachkräftebedarf (S. 85) \n\n29.04.2024\n\n38\n\nIT-Consulting & Dienstleistungsmanagement – SoSe 2024 - Dozent Patrick Schmidt, M.Sc.\n\n','\n',char(10)),'imgs/DSBITCDM01_Vorlesung_Lektion1_38.png');
