INSERT INTO pdf_pages VALUES(254,'pdfs/se_SoSe24_Stand240404.pdf',35,replace('ORGA I\n\n1. Gruppenfindung (3 – 7 Studierende)\n\n2. eigener Themenvorschlag oder Themenwahl\n\n3. Weitere Planung und (Selbst-)Organisation\n\n04.04.2024\n\n35\n\nFallstudie Software Engineering, Prof. Dr.-Ing. Ulrich John, SoSe 2024\n\n','\n',char(10)),'imgs/se_SoSe24_Stand240404_35.png');
