INSERT INTO pdf_pages VALUES(294,'pdfs/DSBDSITS01_3_Grundlagen der IT-Sicherheit.pdf',38,replace('MELDEPFLICHT\nBeispiele\n\nBenennung eines IT-Sicherheitsbeauftragten\nSelbstentwickeltes Werkzeug zur Dokumentation von Sicherheitsvorfällen mit \nMeldefunktion gemäß IT-Sicherheitsgesetz\nTicket-System zum Melden/Analysieren von Vorfällen/Schwachstellen\nAbwicklung über den Helpdesk\nÜbergreifender Incident-/Krisenmanagement-Prozess\n\n\n\n\n\n\n\n …\n\nQuelle: DLBISIC01 Einführung in Datenschutz und IT-Sicherheit, Fernstudiumsskript Version: 002-2023-0206, IU Internationale Hochschule GmbH\n\n','\n',char(10)),'imgs/DSBDSITS01_3_Grundlagen der IT-Sicherheit_38.png');
INSERT INTO pdf_pages VALUES(295,'pdfs/DSBDSITS01_3_Grundlagen der IT-Sicherheit.pdf',39,replace('RECHTLICHE VORGABEN\n\nÜbung: Kritische Infrastrukturen\n\n(Password: DaSchu_ITS_24) \n\n Bilden Sie zwei Gruppen\n Wählen Sie je Gruppe eine Fallstudie aus dem open-access-Buch „Fallstudien zur IT-Sicherheit in Kritischen \n\nInfrastrukturen“ von U. Lechner et. Al. (abrufbar unter: Case Kritis - Fallstudien zur IT-Sicherheit in Kritischen \nInfrastrukturen (oapen.org)) \n\n Hinterlegen Sie Ihre Wahl in Miro  - Fallstudien dürfen nicht von zwei Gruppen bearbeitet werden.\n Stimmen Sie innerhalb der Gruppe ab, wie Sie sich die Fallstudie erschließen und eine Präsentation von \n\nfünf Minuten für das Plenum erstellen.\n\n Erstellen Sie eine Präsentation, die die wichtigsten Aspekte der gewählten Fallstudie darstellen. \n Stellen Sie  die Fallstudie dem Plenum vor\n\n17.05.2024\n\n39\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_3_Grundlagen der IT-Sicherheit_39.png');
