INSERT INTO pdf_pages VALUES(312,'pdfs/KLR Klausur WiSe2023.pdf',16,replace('Kurskürzel: DSKLR01 \nPrüfende:r: Prof. Dr. Silko Pfeil \n\nAufgabe 10: Produktionsoptimierung mit Engpass \n\nErreichbare Punkte: 7 \n\nDie Sedlmayer OHG aus Augsburg produziert  Lederrucksäcke. Eine Spezialnähmaschine, die \nvon allen Produkten durchlaufen werden muss, stellt mit 400 Fertigungsstunden pro Monat \neinen  Produktionsengpass  dar.  Ermitteln  Sie  mithilfe  nachfolgender  Daten  das  optimale \nProduktionsprogramm. \n\nProdukt \n\nTyp 1 \nTyp 2 \nTyp 3 \nTyp 4 \n\nNettoverkaufs-\npreis \n(in Euro pro Stück) \n\n160,00 \n180,00 \n240,00 \n290,00 \n\npro \n\nMaximale \nAbsatzmenge \n(in \nStück \nMonat) \n300 \n300 \n200 \n200 \n\nVariable \nStückkosten  \n(in Euro pro Stück) \n\n96,00 \n126,00 \n120,00 \n174,00 \n\nEngpasszeit  \n\n(in  Stunden  pro \nStück) \n0,3 \n0,4 \n0,6 \n0,8 \n\nErmittle \nProduktionsprogramm für die Sedlmayer OHG! \n\nrelativen \n\neiner \n\nBasis \n\nauf \n\nDeckungsbeitragsanalyse \n\ndas \n\noptimale \n\nProdukt \n\nTyp 1 \nTyp 2 \nTyp 3 \nTyp 4 \n\nAbsoluter  Stück-\nDeckungsbeitrag \n(in Euro pro Stück) \n\nEngpasszeit \n\n(in  Stunden  pro \nStück) \n0,3 \n0,4 \n0,6 \n0,8 \n\nrelativer  Stück-\nDeckungsbeitrag \n(in Euro pro Stunde) \n\nRang \n\nRang \n\nProdukt \n\nMaximale \nAbsatzmenge \n(in \nStück  pro \nMonat) \n\nEngpasszeit  \n\n(in  Stunden  pro \nStück) \n\nGesamteng-\npasszeit  \n(in  Stunden  pro \nMonat) \n\nProduktions-\nmenge  \n(in \nMonat) \n\nStück  pro \n\n1 \n2 \n3 \n4 \n\n400 max! \n\nSeite 13 von 26  \n\n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n \n','\n',char(10)),'imgs/KLR Klausur WiSe2023_16.png');
