INSERT INTO pdf_pages VALUES(315,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',3,replace('“Am Ende liegt es an uns selbst: Es ist nicht die Aufgabe oder die Verantwortung von \njemand anders, sondern die der Bürger unseres Landes und der Bürger der Welt, \nden Kurs der Geschichte hin zur Gerechtigkeit zu lenken. […] Deshalb ist das \nwichtigste Amt in jedem Land nicht das des Präsidenten oder Regierungschefs, der \nwichtigste Titel ist der des Bürgers. […] In allen unseren Ländern werden es immer \nunsere Bürger sein, die darüber entscheiden, was für Länder wir sind, welche Ideale \nwir anstreben und welche Werte uns prägen”\n\n- Barack Obama\n(am 16.11.2016 in einer Grundsatzrede in Griechenland)\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_3.png');
