INSERT INTO pdf_pages VALUES(318,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',6,replace('LERNZIELE\n\nNach der Bearbeitung dieser Lektion werden Sie wissen, …\n\n … welche Konzepte und Prinzipien im Datenschutz Anwendung finden\n\n … welche rechtlichen Vorgaben zum Datenschutz in Deutschland zu beachten sind\n\n … was die wichtigsten gesetzlichen Grundlagen des Datenschutzes sind\n\n … wie Datenschutz in der praktischen Umsetzung anzuwenden ist\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_6.png');
INSERT INTO pdf_pages VALUES(319,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',7,replace('EINFÜHRUNG\n\nÜbung: Beispiel\n\n(Password: DaSchu_ITS_24) \n\n Eine Frau wehrt sich gegen die Absage einer Sicherheitsfirma wegen fehlender charakterlicher \nEignung, die mit Verweis auf anonym zugespielte Bilder und Texte aus einem Internetforum \nbegründet wurde.\n\n In dem Internetforum beschrieb die Frau, dass sie sich regelmäßig an Glücksspielen beteiligt, teilweise \nauch um große Summen. Die Frau erwähnte in diesem Internetforum unzutreffender Weise auch, \ndass sie bereits Mitarbeiterin der Sicherheitsfirma sei.\n\n Wurde der Frau zu Unrecht abgesagt?\n\n Ein Unternehmen speichert den gesamten E-Mail-Verkehr mit seinen Kunden im Rahmen der \nBearbeitung der Bestellungen. Er wird später von einem Kunden wegen fehlerhafter Lieferung \nverklagt. Das Unternehmen legt den E-Mail-Verkehr mit diesem Kunden im Prozess zu seiner \nVerteidigung vor.\n\n12.04.2024\n\n7\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_7.png');
