INSERT INTO pdf_pages VALUES(321,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',9,replace('“Datenschutz ist Machtkontrolle, Datenschutz ist \nSchutz des Individuums, Datenschutz ist Schutz \nder Freiheit, Datenschutz ist Schutz der \ninformationellen Selbstbestimmung.“\n\n- Karl Michael Betzl\n(Deutscher Jurist, Landesdatenschutzbeauftragter des Freistaats Bayern 2006)\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_9.png');
INSERT INTO pdf_pages VALUES(322,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',10,replace('DEFINITION: \nPERSONENBEZOGENE DATEN & BETROFFENE PERSON\n\n„personenbezogene\nalle\nDaten“\nInformationen, die sich auf eine identifizierte oder\nidentifizierbare natürliche Person\n\n[bezeichnet]\n\noder\n\nindirekt,\n\n(im Folgenden „betroffene Person“) beziehen; als\nidentifizierbar wird eine natürliche Person angesehen,\ninsbesondere mittels\ndie direkt\nZuordnung zu einer Kennung wie einem Namen, zu\neiner Kennnummer, zu Standortdaten, zu einer Online-\nKennung oder zu einem oder mehreren besonderen\nphysischen,\nAusdruck\nMerkmalen,\nphysiologischen,\npsychischen,\ngenetischen,\nwirtschaftlichen, kulturellen oder sozialen Identität\ndieser natürlichen Person sind,\nidentifiziert werden\nkann;\n\nder\n\ndie\n\nQuelle: EU-DSGVO Artikel 4\n\n12.04.2024\n\n10\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_10.png');
INSERT INTO pdf_pages VALUES(323,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',11,replace('DEFINITION: VERARBEITUNG\n\nVerfahren\n\nautomatisierter\n\n„Verarbeitung“ [bezeichnet] jeden mit oder ohne\nausgeführten\nHilfe\nVorgang oder\njede solche Vorgangsreihe im\nZusammenhang mit personenbezogenen Daten wie\ndas Erheben, das Erfassen, die Organisation, das\nOrdnen, die Speicherung, die Anpassung oder\nVeränderung, das Auslesen, das Abfragen, die\nVerwendung, die Offenlegung durch Übermittlung,\nForm der\neine\nVerbreitung\nBereitstellung, den Abgleich oder die Verknüpfung,\ndie\ndas\ndie\nVernichtung.\n\nEinschränkung,\n\nLöschen\n\nandere\n\noder\n\noder\n\nQuelle: EU-DSGVO Artikel 4\n\n12.04.2024\n\n11\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_11.png');
INSERT INTO pdf_pages VALUES(324,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',12,replace('DEFINITION: VERANTWORTLICHER\n\n„Verantwortlicher“ die natürliche oder juristische\nPerson, Behörde, Einrichtung oder andere Stelle, die\nallein oder gemeinsam mit anderen über die\nvon\nZwecke\npersonenbezogenen Daten entscheidet.\n\nVerarbeitung\n\nund Mittel\n\nder\n\nQuelle: EU-DSGVO Artikel 4\n\n12.04.2024\n\n12\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_12.png');
INSERT INTO pdf_pages VALUES(325,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',13,replace('DEFINITION: AUFTRAGSVERARBEITER\n\n„Auftragsverarbeiter“ [bezeichnet] eine natürliche\noder juristische Person, Behörde, Einrichtung oder\nandere Stelle, die personenbezogene Daten im\nAuftrag des Verantwortlichen verarbeitet.\n\nQuelle: EU-DSGVO Artikel 4\n\n12.04.2024\n\n13\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_13.png');
INSERT INTO pdf_pages VALUES(326,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',14,replace('DEFINITION: DRITTER\n\neine\n\nnatürliche\n\n[bezeichnet]\n\n„Dritter“\noder\njuristische Person, Behörde, Einrichtung oder\nandere Stelle, außer der betroffenen Person, dem\nVerantwortlichen, dem Auftragsverarbeiter und den\nunmittelbaren\nPersonen,\nVerantwortung des Verantwortlichen oder des\ndie\nsind,\nAuftragsverarbeiters\npersonenbezogenen Daten zu verarbeiten;\n\nbefugt\n\nunter\n\nder\n\ndie\n\nQuelle: EU-DSGVO Artikel 4\n\n12.04.2024\n\n14\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_14.png');
INSERT INTO pdf_pages VALUES(327,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',15,replace('DEFINITION: EMPFÄNGER\n\n„Empfänger“ [bezeichnet] eine natürliche oder\njuristische Person, Behörde, Einrichtung oder\nandere\nStelle, der personenbezogene Daten\noffengelegt werden, unabhängig davon, ob es sich\nbei ihr um einen Dritten handelt oder nicht.\n\nQuelle: EU-DSGVO Artikel 4\n\n12.04.2024\n\n15\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_15.png');
INSERT INTO pdf_pages VALUES(328,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',16,replace('DATENSCHUTZ\n\nÜbung: Personenbezogene Daten (PBD)\n\n(Password: DaSchu_ITS_24) \n\n1) Nennen Sie fünf Beispiele von personenbezogenen Daten (die wenn möglich nicht auf der nächsten \nFolie zu finden sind)\n\n2) Visualisieren Sie das Zusammenspiel von Verantwortlichem, Auftragsverarbeiter, Betroffenem und \nDrittem (in Miro).\n\n3) Nutzen Sie eins Ihrer Beispiele von personenbezogenen Daten und entwerfen ein Fall, in dem alle \nvorgenannten Akteure vorkommen. \n\n4) Vorstellung im Plenum (zufällige Gruppe)\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_16.png');
INSERT INTO pdf_pages VALUES(329,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',17,replace('BEISPIELE FÜR PERSONENBEZOGENE DATEN\n\nName\n\nAdresse\n\nE-Mail-Adresse\n\nTelefonnummer\n\nGeburtstag\n\nKontodaten\n\nKfz-\nKennzeichen\n\nStandortdaten\n\nIP-Adressen\n\nCookies\n\n12.04.2024\n\n17\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_17.png');
INSERT INTO pdf_pages VALUES(330,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',18,replace('DEFINITION: PROFILING\n\nArt\n\ndie\n\njede\n\ndass\n\ndarin\n\nbesteht,\n\n[bezeichnet]\n\n„Profiling“\nder\nautomatisierten Verarbeitung personenbezogener\nDaten,\ndiese\npersonenbezogenen Daten verwendet werden, um\nbestimmte persönliche Aspekte, die sich auf eine\nnatürliche\nbewerten,\ninsbesondere um Aspekte bezüglich Arbeitsleistung,\nwirtschaftliche\npersönliche\nInteressen, Zuverlässigkeit, Verhalten,\nVorlieben,\nAufenthaltsort oder Ortswechsel dieser natürlichen\nPerson zu analysieren oder vorherzusagen.\n\nLage, Gesundheit,\n\nbeziehen,\n\nPerson\n\nzu\n\nQuelle: EU-DSGVO Artikel 4\n\n12.04.2024\n\n18\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_18.png');
INSERT INTO pdf_pages VALUES(331,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',19,replace('DEFINITION: PSEUDONYMISISERUNG\n\n[bezeichnet]\n\n„Pseudonymisierung“\ndie\nVerarbeitung personenbezogener Daten in einer\nWeise, dass die personenbezogenen Daten ohne\nHinzuziehung zusätzlicher Informationen nicht mehr\neiner spezifischen betroffenen Person zugeordnet\n\ndiese\n\nkönnen,\n\nwerden\nzusätzlichen\nsofern\nInformationen gesondert aufbewahrt werden und\ntechnischen und organisatorischen Maßnahmen\ndie\nunterliegen,\neiner\npersonenbezogenen\nidentifizierten oder\nidentifizierbaren natürlichen\nPerson zugewiesen werden.\n\ngewährleisten,\n\nDaten\n\nnicht\n\ndass\n\ndie\n\nQuelle: EU-DSGVO Artikel 4\n\n12.04.2024\n\n19\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_19.png');
INSERT INTO pdf_pages VALUES(332,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',20,replace('DEFINITION: STAND DER TECHNIK\n\nEntwicklungsstand technischer Systeme, der zur\n(vorsorgenden) Abwehr der (im zugrunde liegenden\nGesetz beschriebenen) Gefahren geeignet und der\nverantwortlichen Stelle zumutbar ist.\n\nQuelle: C. Witt (2010)\n\n12.04.2024\n\n20\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_20.png');
INSERT INTO pdf_pages VALUES(333,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',21,replace('DEFINITION: EFFEKTIVITÄT\n\nAusmaß, indem geplante Tätigkeiten verwirklicht\nund geplante Ergebnisse erreicht werden.\n\nQuelle: C. Witt (2010), nach ISO 9000\n\n12.04.2024\n\n21\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_21.png');
INSERT INTO pdf_pages VALUES(334,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',22,replace('DEFINITION: EFFIZIENZ\n\nVerhältnis zwischen erzieltem Ergebnis gegenüber\nden eingesetzten Mitteln.\n\nQuelle: C. Witt (2010) nach ISO 9000\n\n12.04.2024\n\n22\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_22.png');
INSERT INTO pdf_pages VALUES(335,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',23,replace('DATENSCHUTZRECHTLICHE KONZEPTE\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_23.png');
INSERT INTO pdf_pages VALUES(336,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',24,replace('GRUNDSÄTZE DES DATENSCHUTZES\n\n Datenschutz ist in Deutschland und der EU ein Grundrecht, nämlich das Recht auf \n\ninformationelle Selbstbestimmung.\n Recht des Einzelnen, selbst über die Erhebung und die Verwendung der ihn persönlich \n\nbetreffenden Daten zu bestimmen\n\n jedem selbst überlassen, ob er Informationen über sich im Internet veröffentlicht oder nicht\n Werden gegen seinen Willen solche Veröffentlichungen gemacht, kann er dagegen vorgehen, \n\nda sein Recht auf informationelle Selbstbestimmung verletzt wurde\n\n Hauptziel des Datenschutzes ist demnach, das Recht des Einzelnen auf informationelle \n\nSelbstbestimmung zu garantieren\n Bedeutet nicht, dass etwa jegliche Weitergabe von Daten über Personen verboten wird\n\n Gefährdung, die mit dem Datenschutzrecht geregelt werden soll, ist insbesondere diejenige, \n\nbei der Muster gebildet werden können  Gewinnung zusätzlicher Informationen\n\n12.04.2024\n\n24\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_24.png');
INSERT INTO pdf_pages VALUES(337,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',25,replace('GRUNDSÄTZE DES DATENSCHUTZES - GRUNDPRINZIPIEN\n\nInformelle \nSelbstbestimmung\n\nRisiko-\nbasierter \nAnsatz\n\nBetroffenen-\nzentriertheit\n\nVerbot \n[…] mit \nErlaubnis\nvorbehalt\n\nTechnologieneutralität\n\nDer risikobasierte Ansatz in der DSGVO verlangt\nvon Unternehmen und Organisationen, dass sie\nDatenschutzmaßnahmen basierend auf dem\nRisiko implementieren, das die Verarbeitung\npersonenbezogener Daten für die Rechte und\nFreiheiten der betroffenen Personen darstellt.\nund\nDieser\nangemessene\nvon\nindem er sicherstellt,\nDatenschutzvorschriften,\ndass Ressourcen\nauf\ndiejenigen Verarbeitungsaktivitäten fokussiert\nwerden, die ein höheres Risiko für die\nbetroffenen Personen darstellen.\n\neine\nAnwendung\n\nund Bemühungen\n\nflexible\n\nerlaubt\n\nAnsatz\n\nDas Verbot der Verarbeitung personenbezogener\nDaten mit Erlaubnisvorbehalt\nin der DSGVO\nbesagt, dass die Verarbeitung solcher Daten\ngrundsätzlich untersagt ist, es sei denn, es liegt\neine ausdrückliche gesetzliche Erlaubnis oder die\nbetroffenen\nausdrückliche\nPerson vor. Dieses Prinzip dient dem Schutz der\nPrivatsphäre und der Grundrechte\nvon\nPersonen, indem es sicherstellt, dass ihre Daten\nnur\ndefinierten\nBedingungen verarbeitet werden dürfen.\n\nbestimmten,\n\nEinwilligung\n\nunter\n\nklar\n\nder\n\n12.04.2024\n\n25\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_25.png');
INSERT INTO pdf_pages VALUES(338,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',26,replace('GRUNDSÄTZE DES DATENSCHUTZES \nPRINZIP RECHTMÄßIGKEIT\n\n• Rechtmäßigkeit: Verarbeitung personenbezogener \nDaten muss auf einer gesetzlichen Grundlage oder \nder Einwilligung der betroffenen Person beruhen.\n\n• Verarbeitung nach Treu und Glauben: \n\nDatenverarbeiter müssen in gutem Glauben \nhandeln und die Interessen der betroffenen \nPersonen respektieren.\n\n• Transparenz: Die betroffene Person muss über die \nVerarbeitung ihrer Daten klar und verständlich \ninformiert werden.\nInformationspflicht: Datenverarbeiter sind \nverpflichtet, betroffene Personen über ihre Rechte \nund die Verarbeitung ihrer Daten zu informieren.\n\n•\n\nQuelle: EU-DSGVO Artikel 5\n\n12.04.2024\n\n26\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_26.png');
INSERT INTO pdf_pages VALUES(339,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',27,replace('GRUNDSÄTZE DES DATENSCHUTZES \nPRINZIP ZWECKBINDUNG\n\n• Zweckbindung: Personendaten müssen für klar definierte \n\nund legitime Zwecke erhoben werden.\n\n• Keine inkompatible Weiterverarbeitung: Daten dürfen \n\nnicht für Zwecke verarbeitet werden, die mit den \nursprünglichen Zwecken unvereinbar sind.\n\n• Ausnahmen: Weiterverarbeitung für Archivzwecke, \n\nwissenschaftliche oder historische Forschungszwecke \noder statistische Zwecke ist zulässig, wenn sie im \nöffentlichen Interesse liegt (gemäß Artikel 89 Absatz 1).\n• Schutzmechanismen: Bei der Weiterverarbeitung sind \ngeeignete Sicherheitsmaßnahmen und Garantien zum \nSchutz der betroffenen Personen erforderlich.\n\n• Verantwortung: Datenverarbeiter müssen sicherstellen, \n\ndass die Zweckbindung eingehalten wird und die \nVerarbeitung der Daten im Einklang mit den \nursprünglichen Zwecken steht.\n\nQuelle: EU-DSGVO Artikel 5\n\n12.04.2024\n\n27\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_27.png');
INSERT INTO pdf_pages VALUES(340,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',28,replace('GRUNDSÄTZE DES DATENSCHUTZES \nPRINZIP ZWECKBINDUNG\n\n• Datenminimierung: Datenverarbeitung sollte nur in dem \n\nUmfang erfolgen, der für den festgelegten Zweck \nnotwendig ist.\n\n• Angemessenheit: Verarbeitete Daten sollten relevant und \n\ngeeignet für den beabsichtigten Zweck sein.\n• Begrenzung: Die Menge der erhobenen und \n\nverarbeiteten Daten sollte auf das notwendige Minimum \nreduziert werden.\n\n• Speicherdauer: Personenbezogene Daten sollten nicht \nlänger als für den Verarbeitungszweck erforderlich \naufbewahrt werden.\n\n• Verantwortung: Datenverarbeiter müssen sicherstellen, \n\ndass die Datenminimierung bei der Erhebung, \nVerarbeitung und Speicherung personenbezogener Daten \neingehalten wird.\n\nQuelle: EU-DSGVO Artikel 5\n\n12.04.2024\n\n28\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_28.png');
INSERT INTO pdf_pages VALUES(341,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',29,replace('GRUNDSÄTZE DES DATENSCHUTZES \nPRINZIP RICHTIGKEIT\n\n• Richtigkeit: Personenbezogene Daten müssen sachlich \n\nkorrekt und aktuell sein.\n\n• Aktualisierung: Datenverarbeiter sind verpflichtet, \n\nveraltete oder ungenaue Daten zu aktualisieren, wenn \nerforderlich.\nLöschung/Berichtigung: Unrichtige Daten müssen \nunverzüglich gelöscht oder berichtigt werden.\n\n•\n\n• Verantwortung: Datenverarbeiter müssen geeignete \n\nMaßnahmen ergreifen, um die Richtigkeit der \nverarbeiteten Daten sicherzustellen.\n\n• Betroffenenrechte: Betroffene Personen haben das Recht \nauf Berichtigung unrichtiger Daten und auf Löschung \nihrer personenbezogenen Daten, wenn entsprechende \nVoraussetzungen erfüllt sind.\n\nQuelle: EU-DSGVO Artikel 5\n\n12.04.2024\n\n29\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_29.png');
INSERT INTO pdf_pages VALUES(342,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',30,replace('GRUNDSÄTZE DES DATENSCHUTZES \nPRINZIP SPEICHERBEGRENZUNG\n\n• Speicherbegrenzung: Personenbezogene Daten dürfen \n\n•\n\n•\n\nnur so lange gespeichert werden, wie es für den \nVerarbeitungszweck erforderlich ist.\nIdentifizierung: Daten sollten in einer Form gespeichert \nwerden, die eine Identifizierung der betroffenen \nPersonen nur für die erforderliche Dauer ermöglicht.\nLängere Speicherung: Eine längere Speicherung ist \nzulässig, wenn die Daten für Archivzwecke, \nwissenschaftliche/historische Forschung oder statistische \nZwecke im öffentlichen Interesse verwendet werden \n(gemäß Artikel 89 Absatz 1).\n\n• Umsetzung: Datenverarbeiter müssen sicherstellen, dass \n\ndie Speicherbegrenzung eingehalten wird.\n\nQuelle: EU-DSGVO Artikel 5\n\n12.04.2024\n\n30\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_30.png');
INSERT INTO pdf_pages VALUES(343,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',31,replace('GRUNDSÄTZE DES DATENSCHUTZES \nPRINZIP INTEGRITÄT UND VERTRAULICHKEIT\n\n•\n\nIntegrität und Vertraulichkeit: Personenbezogene Daten \nmüssen sicher und vor unbefugtem Zugriff oder \nMissbrauch geschützt verarbeitet werden.\n\n• Technische Maßnahmen: Einsatz von geeigneten \n\nTechnologien, z. B. Verschlüsselung und Firewall, um die \nSicherheit der Daten zu gewährleisten.\n\n• Organisatorische Maßnahmen: Implementierung von \n\ninternen Richtlinien, Verfahren und Schulungen, um das \nBewusstsein für Datenschutz zu fördern und den Schutz \npersonenbezogener Daten sicherzustellen.\n\n• Schutz vor Verlust und Schädigung: Datenverarbeiter \nmüssen Vorkehrungen treffen, um unbeabsichtigten \nVerlust, Zerstörung oder Schädigung der \npersonenbezogenen Daten zu verhindern.\n\n• Umsetzung: Datenverarbeiter sind verpflichtet, die \nIntegrität und Vertraulichkeit der verarbeiteten \npersonenbezogenen Daten kontinuierlich zu überprüfen \nund aufrechtzuerhalten.\n\nQuelle: EU-DSGVO Artikel 5\n\n12.04.2024\n\n31\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_31.png');
INSERT INTO pdf_pages VALUES(344,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',32,replace('GRUNDSÄTZE DES DATENSCHUTZES \nEXKURS PRIVACY BY DESIGN\n\n1. Proaktiver und nicht reaktiver Datenschutz\n2. Datenschutz als Standard\n3. Datenschutz in die Technik einbetten\n4. Vollständige Funktionalität – positives \n\nNutzererlebnis\n\n5. Datensicherheit über den gesamten Lebenszyklus\n6. Sichtbarkeit und Transparenz\n7. Respekt vor der Privatsphäre der Nutzer\n\nQuelle: EU-DSGVO Artikel 5\n\n12.04.2024\n\n32\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_32.png');
INSERT INTO pdf_pages VALUES(345,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',33,replace('GRUNDSÄTZE DES DATENSCHUTZES \nPRINZIP RECHENSCHAFTSPFLICHT\n\n• Rechenschaftspflicht: Der Verantwortliche trägt die \n\nVerantwortung für die Einhaltung der \nDatenschutzprinzipien und muss deren Einhaltung \nnachweisen können.\n\n• Dokumentation: Der Verantwortliche muss \n\nDatenschutzmaßnahmen und -verfahren dokumentieren, \num die Einhaltung der DSGVO zu belegen.\n\n• Risikobewertung: Durchführung von Datenschutz-\n\nFolgenabschätzungen, um Risiken in der \nDatenverarbeitung zu identifizieren und angemessene \nSchutzmaßnahmen zu ergreifen.\n\n• Mitarbeiterschulung: Sicherstellen, dass alle Mitarbeiter \nüber die Datenschutzanforderungen informiert sind und \ndiese in der täglichen Arbeit einhalten.\n\n• Zusammenarbeit mit Datenschutzbehörden: Der \n\nVerantwortliche muss bei Bedarf mit den zuständigen \nDatenschutzbehörden zusammenarbeiten und deren \nAnweisungen befolgen, um die Einhaltung der DSGVO \nnachzuweisen.\n\nQuelle: EU-DSGVO Artikel 5\n\n12.04.2024\n\n33\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_33.png');
INSERT INTO pdf_pages VALUES(346,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',34,replace('PRINZIPIEN DES DATENSCHUTZES\n\nDatengeheimnis\n\nEs ist untersagt, personenbezogene Daten unbefugt zu erheben oder zu \nverwenden\n\nDatenvermeidung\n\nZiel auszurichten, so wenig personenbezogene Daten wie möglich zu erheben \noder zu verwenden.\n\nErforderlichkeit\n\nErforderlich sind personenbezogene Daten nur dann, wenn die Aufgabe sonst \nnicht, nicht vollständig oder nicht in rechtmäßiger Weise erfüllt werden kann.\n\nInteressenabwägung\n\nDie berechtigten Interessen der erhebenden bzw. verwendenden Stelle sind \nimmer gegenüber den schutzwürdigen Interessen Betroffener abzuwägen.\n\nSchutzbedarf\n\nEs sind technische und organisatorische Maßnahmen zu treffen, um die \nSicherheit der eingesetzten IT im Interesse des Schutzes des \nPersönlichkeitsrechtes zu gewährleisten.\n\nQuelle: BDSG\n\n12.04.2024\n\n34\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_34.png');
INSERT INTO pdf_pages VALUES(347,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',35,replace('PRINZIPIEN DES DATENSCHUTZES\n\nSelbstauskunft\n\nDie Erhebung personenbezogener Daten muss – soweit möglich – beim \nBetroffenen erfolgen.\n\nTransparenz\n\nEs ist Pflicht, den Betroffenen (dessen Daten gespeichert werden) über die Daten, \ndie Zweckbestimmung der Erhebung und Verwendung und die Identität der \nverantwortlichen Stelle zu informieren.\n\nVerhältnismäßigkeit\n\nNicht mehr Personenbezogene Daten erheben oder verwenden als notwendig \n(„Übermaßverbot“).\n\nZweckbindung\n\nBei jeder Erhebung oder Verwendung PBD ist zwingend ein hinreichend präziser \nVerwendungszweck festzulegen, von dem nur in wohl definierten \nAusnahmefällen abgewichen werden kann.\n\nQuelle: BDSG\n\n12.04.2024\n\n35\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_35.png');
INSERT INTO pdf_pages VALUES(348,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',36,replace('DATENSCHUTZ\n\nÜbung: Prinzipien\n\n(Password: DaSchu_ITS_24) \n\n1) Finden Sie in der Gruppe für jedes der genannten Datenschutzprinzipien mindestens ein konkretes \nBeispiel  aus der täglichen Arbeit (bei Ihrem Praxispartner). \n\n2) Entwickeln Sie in der Gruppe ein Konzept für eine mobile Anwendung, die den Datenschutz der \nNutzerinnen und Nutzer sicherstellt. Die Anwendung soll einen Ortungsdienst (z.B. für Freunde oder \nFamilienmitglieder) bieten, um den Aufenthaltsort der Nutzerinnen und Nutzer in Echtzeit zu verfolgen und \nsie mit anderen zu teilen. \nBerücksichtigen Sie bei der Konzeption der Anwendung die sieben Grundprinzipien von Privacy by Design.\nIdentifizieren Sie spezifische Maßnahmen und Funktionen, die in der Anwendung implementiert werden \nsollen, um die Privatsphäre der Nutzerinnen und Nutzer zu schützen.\n\n3) Präsentieren Sie Ihr Konzept in einem kurzen Vortrag (5-7 Minuten) im Plenum und diskutieren Sie die \nVor- und Nachteile der vorgeschlagenen Maßnahmen. (zufällige Gruppe)\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_36.png');
INSERT INTO pdf_pages VALUES(349,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',37,replace('BETROFFENENRECHTE\nKOMMUNIKATION UND MODALITÄTEN\n\n•\n\n• Transparenz: Der Datenverantwortliche muss betroffene \nPersonen klar und verständlich über die Verarbeitung \nihrer personenbezogenen Daten informieren.\nInformationspflicht: Bereitstellung von Informationen \nüber Verarbeitungszwecke, Empfänger, Speicherdauer \nund Betroffenenrechte gemäß Artikel 13 und 14 der \nDSGVO.\n\n• Kommunikation: Schnelle und effektive Kommunikation \nmit betroffenen Personen bei Anfragen zur Ausübung \nihrer Datenschutzrechte (z.B. Zugang, Berichtigung, \nLöschung).\n\n• Modalitäten: Bereitstellung von einfachen und \n\nzugänglichen Mitteln für die betroffene Person, um ihre \nRechte auszuüben, einschließlich elektronischer \nFormulare und Kontaktmöglichkeiten.\n\n• Kostenfreiheit: Die Ausübung der Betroffenenrechte \n\nsollte in der Regel kostenlos sein, es sei denn, Anfragen \nsind offensichtlich unbegründet oder übermäßig häufig.\n\nQuelle: EU-DSGVO Artikel 12\n\n12.04.2024\n\n37\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_37.png');
INSERT INTO pdf_pages VALUES(350,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',38,replace('BETROFFENENRECHTE\nINFORMATIONSPFLICHT (ERHEBUNG BEI BETROFFENER PERSON)\n\n•\n\nInformationspflicht: Der Datenverantwortliche muss \nbetroffene Personen aktiv informieren, wenn ihre \npersonenbezogenen Daten erhoben werden.\n\n• Verarbeitungszwecke: Die betroffene Person muss über \n\ndie Zwecke der Datenverarbeitung und die \nRechtsgrundlage für die Verarbeitung informiert werden.\n\n• Empfänger: Informationen über Empfänger oder \n\nKategorien von Empfängern, an die personenbezogene \nDaten weitergegeben werden, müssen bereitgestellt \nwerden.\n\n• Speicherdauer: Die betroffene Person muss über die \nvorgesehene Speicherdauer der personenbezogenen \nDaten oder die Kriterien für die Festlegung dieser Dauer \ninformiert werden.\n\n• Betroffenenrechte: Die betroffene Person muss über ihre \nDatenschutzrechte informiert werden, einschließlich des \nRechts auf Zugang, Berichtigung, Löschung, \nEinschränkung der Verarbeitung, Widerspruchsrecht und \ndas Recht auf Datenübertragbarkeit.\n\nQuelle: EU-DSGVO Artikel 13\n\n12.04.2024\n\n38\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_38.png');
INSERT INTO pdf_pages VALUES(351,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',39,replace('BETROFFENENRECHTE INFORMATIONSPFLICHT \n(ERHEBUNG BEI NICHT-BETROFFENER PERSON)\n\n•\n\n•\n\nInformationspflicht: Der Verantwortliche muss \nbetroffene Personen informieren, wenn \npersonenbezogene Daten nicht direkt von ihnen erhoben \nwerden, sondern aus anderen Quellen stammen.\nInformationsquellen: Die betroffene Person muss über \ndie Herkunft der Daten und, wenn möglich, die genaue \nInformationsquelle informiert werden.\n\n• Verarbeitungszwecke und Rechtsgrundlage: Die \nbetroffene Person muss über die Zwecke der \nDatenverarbeitung und die Rechtsgrundlage für die \nVerarbeitung informiert werden.\n\n• Zeit. der Information: Die betroffene Person sollte \n\ninnerhalb einer angemessenen Frist, spätestens jedoch \ninnerhalb eines Monats nach der Erhebung der Daten, \ninformiert werden.\n\n• Ausnahmen: In bestimmten Fällen, wie z.B. wenn die \n\nInformationsbereitstellung unmöglich oder \nunverhältnismäßig aufwendig ist, kann die \nInformationspflicht entfallen oder eingeschränkt sein.\n\nQuelle: EU-DSGVO Artikel 14\n\n12.04.2024\n\n39\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_39.png');
INSERT INTO pdf_pages VALUES(352,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',40,replace('BETROFFENENRECHTE\nAUSKUNFTSRECHT DER BETROFFENEN PERSON\n\n• Auskunftsrecht: Betroffene Personen haben das Recht, von dem \nVerantwortlichen eine Bestätigung darüber zu erhalten, ob ihre \npersonenbezogenen Daten verarbeitet werden.\n\n• Zugang zu Informationen: Bei einer Verarbeitung haben \n\nbetroffene Personen das Recht, Zugang zu ihren \npersonenbezogenen Daten und Informationen über die \nVerarbeitungszwecke, Empfänger und Speicherdauer zu erhalten.\n\n• Recht auf Kopie: Betroffene Personen haben das Recht, eine \n\nKopie der verarbeiteten personenbezogenen Daten zu erhalten, \nsolange dies die Rechte und Freiheiten anderer nicht \nbeeinträchtigt.\n\n• Reaktionszeit: Der Verantwortliche muss betroffenen Personen \ninnerhalb eines Monats nach Eingang des Antrags auf Auskunft \nantworten, wobei diese Frist unter bestimmten Umständen \nverlängert werden kann.\n\n• Beschwerderecht: Der Verantwortliche muss die betroffene \n\nPerson über das Beschwerderecht bei einer Aufsichtsbehörde \ninformieren.\n\nQuelle: EU-DSGVO Artikel 15\n\n12.04.2024\n\n40\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_40.png');
INSERT INTO pdf_pages VALUES(353,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',41,replace('BETROFFENENRECHTE\nRECHT AUF BERICHTIGUNG\n\nQuelle: EU-DSGVO Artikel 16 &19\n\n12.04.2024\n\n41\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n• Recht auf Berichtigung: Betroffene Personen haben das Recht, \n\nvon dem Datenverantwortlichen die Berichtigung unrichtiger oder \nunvollständiger personenbezogener Daten zu verlangen.\n\n• Unverzügliche Berichtigung: Der Verantwortliche muss unrichtige \noder unvollständige Daten unverzüglich berichtigen, sobald er von \nder Unrichtigkeit oder Unvollständigkeit Kenntnis erlangt.\n• Ergänzung fehlender Daten: Betroffene Personen können die \nErgänzung unvollständiger Daten verlangen, auch durch eine \nergänzende Erklärung.\n\n• Mitteilung an Empfänger: Wenn personenbezogene Daten \n\noffengelegt wurden, muss der Verantwortliche die Berichtigung in \nder Regel auch den Empfängern der Daten mitteilen, es sei denn, \ndies erweist sich als unmöglich oder ist mit einem \nunverhältnismäßigen Aufwand verbunden.\n\n• Reaktionszeit: Der Verantwortliche muss betroffenen Personen \n\ninnerhalb eines Monats nach Eingang des Antrags auf \nBerichtigung antworten, wobei diese Frist unter bestimmten \nUmständen verlängert werden kann.\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_41.png');
INSERT INTO pdf_pages VALUES(354,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',42,replace('BETROFFENENRECHTE\nRECHT AUF VERGESSENWERDEN\n\n• Recht auf Löschung: Betroffene Personen haben das Recht, von dem \n\n•\n\nDatenverantwortlichen die Löschung ihrer personenbezogenen Daten zu \nverlangen, wenn bestimmte Voraussetzungen erfüllt sind.\nLöschungsgründe: Gründe für eine Löschung können sein: Daten sind für \nden ursprünglichen Zweck nicht mehr erforderlich, Widerruf der \nEinwilligung, berechtigter Widerspruch, unrechtmäßige Verarbeitung oder \ngesetzliche Löschungspflichten.\n\n• Recht auf Vergessenwerden: Bei öffentlich gemachten Daten muss der \nVerantwortliche angemessene Maßnahmen ergreifen, um andere \nVerantwortliche zu informieren, dass die betroffene Person die Löschung \naller Links, Kopien oder Replikationen ihrer Daten verlangt hat.\n\n• Ausnahmen: Das Recht auf Löschung besteht nicht, wenn die Verarbeitung \nzur Ausübung des Rechts auf freie Meinungsäußerung und Information, \nzur Erfüllung einer rechtlichen Verpflichtung oder zur Wahrung \nöffentlicher Interessen (z.B. Archivierung, Forschung) erforderlich ist.\n• Reaktionszeit: Der Verantwortliche muss betroffenen Personen innerhalb \neines Monats nach Eingang des Antrags auf Löschung antworten, wobei \ndiese Frist unter bestimmten Umständen verlängert werden kann.\n\nQuelle: EU-DSGVO Artikel 17 & 19\n\n12.04.2024\n\n42\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_42.png');
INSERT INTO pdf_pages VALUES(355,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',43,replace('BETROFFENENRECHTE\nRECHT AUF EINSCHRÄNKUNG DER VERARBEITUNG\n\n• Recht auf Einschränkung: Betroffene Personen haben das Recht, von dem \n\n•\n\n•\n\nVerantwortlichen die Einschränkung der Verarbeitung ihrer \npersonenbezogenen Daten zu verlangen, wenn bestimmte Bedingungen \nerfüllt sind.\nEinschränkungsgründe: E. g. Überprüfung der Richtigkeit der Daten, \nunrechtmäßige Verarbeitung mit Ablehnung der Löschung, Speicherung \nder Daten für die Geltendmachung, Ausübung oder Verteidigung von \nRechtsansprüchen oder Prüfung eines Widerspruchs gegen die \nVerarbeitung.\nEingeschränkte Verarbeitung: Bei eingeschränkter Verarbeitung dürfen \npersonenbezogene Daten nur mit Einwilligung der betroffenen Person, zur \nGeltendmachung, Ausübung oder Verteidigung von Rechtsansprüchen, \nzum Schutz der Rechte anderer Personen oder aus Gründen eines \nwichtigen öffentlichen Interesses verarbeitet werden.\n\n• Mitteilung an Empfänger: Der Verantwortliche muss die Empfänger der \nDaten über die Einschränkung der Verarbeitung informieren, es sei denn, \ndies erweist sich als unmöglich oder ist mit einem unverhältnismäßigen \nAufwand verbunden.\n\n• Reaktionszeit: Der Datenverantwortliche muss betroffenen Personen \n\ninnerhalb eines Monats nach Eingang des Antrags auf Einschränkung der \nVerarbeitung antworten, wobei diese Frist unter bestimmten Umständen \nverlängert werden kann.\n\nQuelle: EU-DSGVO Artikel 18 & 19\n\n12.04.2024\n\n43\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_43.png');
INSERT INTO pdf_pages VALUES(356,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',44,replace('BETROFFENENRECHTE\nRECHT AUF DATENÜBERTRAGBARKEIT\n\n• Recht auf Datenübertragbarkeit: Betroffene Personen haben das Recht, \n\nihre personenbezogenen Daten, die sie dem Verantwortlichen \nbereitgestellt haben, in einem strukturierten, gängigen und \nmaschinenlesbaren Format zu erhalten.\n\n• Direkte Übermittlung: Betroffene Personen haben das Recht, die direkte \nÜbermittlung ihrer Daten von einem Verantwortlichen zu einem anderen \nzu verlangen, sofern dies technisch machbar ist.\n\n• Anwendbarkeit: Dieses Recht gilt nur, wenn die Verarbeitung auf einer \n\nEinwilligung oder auf einem Vertrag beruht und die Verarbeitung mithilfe \nautomatisierter Verfahren durchgeführt wird.\n\n• Keine Beeinträchtigung: Die Ausübung des Rechts auf \n\nDatenübertragbarkeit darf die Rechte und Freiheiten anderer Personen \nnicht beeinträchtigen, insbesondere nicht das Schutzbedürfnis der \npersonenbezogenen Daten anderer betroffener Personen.\n\n• Reaktionszeit: Der Verantwortliche muss betroffenen Personen innerhalb \n\neines Monats nach Eingang des Antrags auf Datenübertragbarkeit \nantworten, wobei diese Frist unter bestimmten Umständen verlängert \nwerden kann.\n\nQuelle: EU-DSGVO Artikel 20\n\n12.04.2024\n\n44\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_44.png');
INSERT INTO pdf_pages VALUES(357,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',45,replace('BETROFFENENRECHTE\nWIDERSPRUCHSRECHT\n\nQuelle: EU-DSGVO Artikel 21\n\n12.04.2024\n\n45\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n• Widerspruchsrecht: Betroffene Personen haben das Recht, jederzeit gegen die \nVerarbeitung ihrer personenbezogenen Daten Widerspruch einzulegen, wenn \ndiese aufgrund von berechtigten Interessen des Verantwortlichen oder eines \nDritten oder im Rahmen einer Aufgabe im öffentlichen Interesse erfolgt.\n• Direktmarketing: Bei Verarbeitung für Zwecke des Direktmarketings können \n\nbetroffene Personen jederzeit Widerspruch einlegen, woraufhin ihre \npersonenbezogenen Daten nicht mehr für diese Zwecke verarbeitet werden \ndürfen.\n\n• Begründung: Der Widerspruch muss auf Gründe gestützt werden, die sich auf \ndie besondere Situation der betroffenen Person beziehen, es sei denn, es \nhandelt sich um Direktmarketing.\n\n• Prüfung des Widerspruchs: Der Verantwortliche muss den Widerspruch prüfen \nund die Verarbeitung einstellen, es sei denn, er kann zwingende schutzwürdige \nGründe für die Verarbeitung nachweisen, die die Interessen, Rechte und \nFreiheiten der betroffenen Person überwiegen, oder die Verarbeitung dient der \nGeltendmachung, Ausübung oder Verteidigung von Rechtsansprüchen.\n\n• Unterrichtungspflicht: Betroffene Personen müssen bei der ersten \nKommunikation mit dem Verantwortlichen ausdrücklich auf ihr \nWiderspruchsrecht hingewiesen werden und dieser Hinweis muss verständlich \nund von anderen Informationen getrennt erfolgen.\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_45.png');
INSERT INTO pdf_pages VALUES(358,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',46,replace('BETROFFENENRECHTE - AUTOMATISIERTE ENTSCHEIDUNGEN \nIM EINZELFALL EINSCHLIEßLICH PROFILING\n\n• Automatisierte Entscheidungen: Betroffene Personen haben das Recht, \n\nnicht einer ausschließlich auf einer automatisierten Verarbeitung, \neinschließlich Profiling, beruhenden Entscheidung unterworfen zu \nwerden, die ihnen gegenüber rechtliche Wirkung entfaltet oder sie in \nähnlicher Weise erheblich beeinträchtigt.\n\n• Ausnahmen: Automatisierte Entscheidungen sind zulässig, wenn sie für \n\n•\n\nden Abschluss oder die Erfüllung eines Vertrags zwischen der \nbetroffenen Person und dem Verantwortlichen erforderlich sind, \naufgrund von rechtlichen Vorschriften zulässig sind, oder wenn die \nbetroffene Person ausdrücklich eingewilligt hat.\nSchutzmaßnahmen: Bei zulässigen automatisierten Entscheidungen \nmüssen angemessene Schutzmaßnahmen getroffen werden, um die \nRechte, Freiheiten und berechtigten Interessen der betroffenen \nPersonen zu wahren, einschließlich des Rechts auf menschliches \nEingreifen seitens des Verantwortlichen, des Rechts, den eigenen Stand. \ndarzulegen, und des Rechts, die Entscheidung anzufechten.\n• Verbot diskriminierender Entscheidungen: Automatisierte \n\nEntscheidungen dürfen keine Diskriminierung aufgrund von besonderen \nKategorien personenbezogener Daten, bewirken, es sei denn, dies ist \ndurch Rechtsvorschriften zugelassen und angemessene \nSchutzmaßnahmen wurden getroffen.\n\nQuelle: EU-DSGVO Artikel 22\n\n12.04.2024\n\n46\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_46.png');
INSERT INTO pdf_pages VALUES(359,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',47,replace('BETROFFENENRECHTE \nBESCHRÄNKUNGEN\n\nQuelle: EU-DSGVO Artikel 23\n\n12.04.2024\n\n47\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n• Beschränkungen: Mitgliedstaaten können durch Rechtsvorschriften \n\n•\n\nEinschränkungen der Datenschutzrechte und -pflichten vorsehen, wenn \ndiese zur Wahrung wichtiger öffentlicher oder privater Interessen \nerforderlich sind.\nSchutzbedürftige Interessen: Zu den schutzwürdigen Interessen zählen \nnationale Sicherheit, Landesverteidigung, öffentliche Sicherheit, \nStrafverfolgung, Schutz vor schwerwiegenden Bedrohungen, wichtige \nwirtschaftliche oder finanzielle Interessen, Schutz der Unabhängigkeit \nder Justiz und Rechtsdurchsetzung sowie Schutz von Rechten und \nFreiheiten anderer Personen.\n\n• Verhältnismäßigkeit: Jede Einschränkung der Datenschutzrechte und -\n\npflichten muss verhältnismäßig und notwendig sein, um die \nschutzbedürftigen Interessen zu wahren, und darf nicht über das \nerforderliche Maß hinausgehen.\n\n• Verfahrensgarantien: Die Rechtsvorschriften müssen angemessene \n\nVerfahrens- und Rechtsschutzgarantien gewährleisten, um die Rechte der \nbetroffenen Personen, insbesondere das Recht auf wirksamen \ngerichtlichen Rechtsschutz, zu schützen.\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_47.png');
INSERT INTO pdf_pages VALUES(360,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',48,replace('DATENSCHUTZ\n\nÜbung: Betroffenenrechte\n\n1) Wählen Sie ein Unternehmen Ihrer Wahl (e.g. Ihren Praxispartner), beschreiben Sie einen typischen \nBetroffenen und recherchieren, wie dieser seine Rechte durchsetzen kann. \n\n2) Visualisieren Sie Ihre Ergebnisse farblich in Miro (rot = keine Information vorhanden oder fehlerhaft, gelb \n= keine „überzeugende“ Information, grün = kommunizierte Möglichkeit transparent und passend). \n\n3) Geben Sie an wie oft Sie in den letzten fünf Jahren (bewusst) von Ihren Betroffenenrechten Gebrauch \ngemacht haben.\n\n(Password: DaSchu_ITS_24) \n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_48.png');
INSERT INTO pdf_pages VALUES(361,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',49,replace('“Damit die Verarbeitung rechtmäßig ist, müssen personenbezogene Daten mit \nEinwilligung der betroffenen Person oder auf einer sonstigen \nzulässigen Rechtsgrundlage verarbeitet werden, die sich aus dieser \nVerordnung oder – wann immer in dieser Verordnung darauf Bezug genommen wird – aus dem \nsonstigen Unionsrecht oder dem Recht der Mitgliedstaaten ergibt, so unter anderem auf der \nGrundlage, dass sie zur Erfüllung der rechtlichen Verpflichtung, der der Verantwortliche unterliegt, \noder zur Erfüllung eines Vertrags, dessen Vertragspartei die betroffene Person ist, oder für die \nDurchführung vorvertraglicher Maßnahmen, die auf Anfrage der betroffenen Person erfolgen, \nerforderlich ist.“\n\n- Erwägungsgrund 40 EU-DSGVO\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_49.png');
INSERT INTO pdf_pages VALUES(362,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',50,replace('RECHTMÄßIGKEIT DER VERARBEITUNG\nEINWILLIGUNG\n\n•\n\n• Nachweis der Einwilligung: Verantwortliche müssen nachweisen können, \ndass die betroffene Person in die Verarbeitung ihrer personenbezogenen \nDaten eingewilligt hat.\nEinwilligungserklärung: Bei schriftlichen Erklärungen muss das Ersuchen \num Einwilligung klar von anderen Sachverhalten getrennt, verständlich \nund leicht zugänglich sein. Unzulässige Teile der Erklärung sind nicht \nbindend.\n\n• Widerrufsrecht: Betroffene Personen haben das Recht, ihre Einwilligung \n\njederzeit zu widerrufen, ohne die Rechtmäßigkeit der vorherigen \nVerarbeitung zu beeinträchtigen. Sie müssen über dieses Recht \ninformiert werden und der Widerruf soll so einfach wie die Erteilung der \nEinwilligung sein.\nFreiwilligkeit der Einwilligung: Bei der Beurteilung der Freiwilligkeit muss \nberücksichtigt werden, ob die Einwilligung zur Verarbeitung von Daten \nals Bedingung für einen Vertrag oder eine Dienstleistung erforderlich ist, \nobwohl sie nicht notwendig für die Erfüllung des Vertrags sind.\nSchutz der betroffenen Person: Einwilligungen müssen freiwillig, \ninformiert und unmissverständlich erfolgen, um die Rechte der \nbetroffenen Personen zu schützen und die datenschutzrechtlichen \nAnforderungen zu erfüllen.\n\n•\n\n•\n\nQuelle: EU-DSGVO Artikel 7\n\n12.04.2024\n\n50\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_50.png');
INSERT INTO pdf_pages VALUES(363,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',51,replace('RECHTMÄßIGKEIT DER VERARBEITUNG\nVERTRAG ODER VORVERTRAGLICHE MAßNAHMEN\n\n• Beschränkungen: Gilt nur, wenn die betroffene Person Vertragspartner \n\nist. Insbesondere findet die Regelung keine Anwendung auf die \nVerarbeitung personenbezogener Daten im Rahmen von B2B-Verträgen.\n• Vorvertragliche Maßnahmen: Vorvertragliche Maßnahmen müssen auf \n\nAntrag der betroffenen Person erfolgt sein. Hierzu gehören \nbeispielsweise Zusendung von Angebotsschreiben, Antrag auf Ratenkauf, \noder auch Bewerbungsverfahren.\n\nQuelle: EU-DSGVO Artikel 6\n\n12.04.2024\n\n51\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_51.png');
INSERT INTO pdf_pages VALUES(364,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',52,replace('RECHTMÄßIGKEIT DER VERARBEITUNG\nRECHTLICHE VERPFLICHTUNG\n\n• Beschränkungen: die Regelung umfasst rechtliche Verpflichtungen im \nSinne einer gesetzlichen Vorgabe jedoch nicht für vertraglich bedingte \nVerpflichtungen.\n\n• Anforderungen: Es muss eine Prüfung für jede Rechtsnorm durchgeführt \n\nwerden:\n\n• Die Rechtsnorm muss tatsächlich die Verpflichtung beinhalten, dass \ndie Verarbeitung für die Erfüllung der Verpflichtung erforderlich ist.\n\n• Aufbewahrungsfristen müssen sich aus der Rechtsnorm ableiten \n\nlassen.\n\n• Beispiele:\n\n• Aufbewahrungspflichten von Geschäftsunterlagen\n• Aufbewahrungspflichten im Gesundheits- und Sozialwesen\n• Meldepflichten im Zusammenhang mit Infektionen und \n\nErkrankungen\n\n• Meldepflichten von Unterkünften\n\nQuelle: EU-DSGVO Artikel 6\n\n12.04.2024\n\n52\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_52.png');
INSERT INTO pdf_pages VALUES(365,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',53,replace('RECHTMÄßIGKEIT DER VERARBEITUNG\nLEBENSWICHTIGE INTERESSEN\n\n• Beschränkungen: was eine Datenverarbeitung ist zulässig Komma wenn \nsie dem Schutz lebenswichtiger Interessen der von der Verarbeitung \nbetroffenen oder einer anderen natürlichen Person dient.\nErforderlichkeit: Die Verarbeitung ist für den Schutz tatsächlich \nerforderlich sein.\n\n•\n\n• Praktische Bedeutung: dieser Erlaubnistatbestand hat in der Praxis eine \nuntergeordnete Bedeutung da die Hürde der lebenswichtigen Interessen \nhoch ist und in der Regel personenbezogene Daten besonderer Art in \ndiesem Falle Gesundheitsdaten betroffen sind. Für Gesundheitsdaten \ngelten nach der DSGVO gesonderte Erlaubnistatbestände (vgl. DSGVO \nArt. 9 Abs. 2).\n\nQuelle: EU-DSGVO Artikel 6\n\n12.04.2024\n\n53\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_53.png');
INSERT INTO pdf_pages VALUES(366,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',54,replace('RECHTMÄßIGKEIT DER VERARBEITUNG -ÖFFENTLICHES INTERESSE \nODER AUSÜBUNG ÖFFENTLICHER GEWALT\n\n• Anwendung: Zulässig, wenn sie zur Erfüllung einer \n\n•\n\nöffentlichen Aufgabe oder zur Ausübung öffentlicher Gewalt \nerforderlich ist, die dem Verantwortlichen übertragen \nwurde.\nLegitimation von Ämtern und Behörden: Die Regelung \ndient der Legitimation der Arbeit von Ämtern und Behörden.\n• Rechtsgrundlage: Erforderlich nach Unionsrecht oder dem \nRecht der Mitgliedstaaten gemäß Artikel 6 Absatz 3 DSGVO.\n\n• Keine generelle Berufung auf berechtigte Interessen: \n\nBehörden können sich bei der Erfüllung ihrer Aufgaben nicht \nauf berechtigte Interessen berufen, da eine entsprechende \nRechtsgrundlage erforderlich ist.\n\nQuelle: EU-DSGVO Artikel 6\n\n12.04.2024\n\n54\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_54.png');
INSERT INTO pdf_pages VALUES(367,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',55,replace('RECHTMÄßIGKEIT DER VERARBEITUNG - BERECHTIGTES \nINTERESSE DES VERANTWORTLICHEN ODER EINES DRITTEN\n\n•\n\n• Beschränkungen: Da betroffene Personen keine Dritte sein können, ist \neine Verarbeitung auf Grundlage des mutmaßlichen Interesses der \nbetroffenen Person hiermit nicht abgebildet.\nIntressensidentifikation: Zur Anwendung dieser Rechtsgrundlage muss \ndas berechtigte Interesse identifiziert sein.\nInteressenabwägung: Im Rahmen der Anwendung dieser \nRechtsgrundlage muss eine allgemeine und dokumentierte \nInteressenabwägung stattgefunden haben.\nInformationspflicht: Die berechtigten Interessen müssen im Rahmen der \nInformationspflichten angegeben werden.\n\n•\n\n•\n\n• Widerspruchsrecht: Die die betroffene Person besitzt ein \n\nWiderspruchsrecht. Im Rahmen eines tatsächlichen Widerspruchs muss \neine für die einzelne betroffene Person spezifische Interessenabwägung \ndurchgeführt werden.\n\nQuelle: EU-DSGVO Artikel 6\n\n12.04.2024\n\n55\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_55.png');
INSERT INTO pdf_pages VALUES(368,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',56,replace('RECHTMÄßIGKEIT DER VERARBEITUNG\n\nEinführungsbeispiel\n\n Eine Frau wehrt sich gegen die Absage einer Sicherheitsfirma wegen fehlender charakterlicher \nEignung, die mit Verweis auf anonym zugespielte Bilder und Texte aus einem Internetforum \nbegründet wurde.\n\n In dem Internetforum beschrieb die Frau, dass sie sich regelmäßig an Glücksspielen beteiligt, teilweise \nauch um große Summen. Die Frau erwähnte in diesem Internetforum unzutreffender Weise auch, \ndass sie bereits Mitarbeiterin der Sicherheitsfirma sei.\n\n Wurde der Frau zu Unrecht abgesagt?\n\n Ein Unternehmen speichert den gesamten E-Mail-Verkehr mit seinen Kunden im Rahmen der \nBearbeitung der Bestellungen. Er wird später von einem Kunden wegen fehlerhafter Lieferung \nverklagt. Das Unternehmen legt den E-Mail-Verkehr mit diesem Kunden im Prozess zu seiner \nVerteidigung vor.\n\nQuelle: R. Kneuper (2021)\n\n12.04.2024\n\n56\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_56.png');
INSERT INTO pdf_pages VALUES(369,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',57,replace('RECHTMÄßIGKEIT DER VERARBEITUNG\n\nEinführungsbeispiel - Bewertung\n\nDer Frau wird zu Recht abgesagt:\n\nZum einen hat nämlich die Sicherheitsfirma  ein  überwiegendes  Interesse  daran,  die  \n\ncharakterliche  Eignung  von  Bewerbern genau  zu  kennen.  \n\nZum  anderen  hat  die  Frau  die  verwendeten  Informationen  selbst preisgegeben. \nDass sie dabei auch noch unwahre Angaben gemacht hat, unterstreicht nur ihre \nmangelnde charakterliche Eignung.\n\nAuch darf das Unternehmen den E-Mail-Verkehr zu seiner Entlastung im Prozess  \n\nvorlegen,  da  es  sich  nur  so  gegen  den  Vorwurf  der  falschen  Lieferung  wehren kann.\n\nQuelle: R. Kneuper (2021) \n\n12.04.2024\n\n57\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_57.png');
INSERT INTO pdf_pages VALUES(370,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',58,replace('RECHTMÄßIGKEIT DER VERARBEITUNG\n\nOptionale Übung\n\n1) Bildet Gruppen von 3-4 Personen und bearbeitet gemeinsam die folgende Fallstudie. Diskutiert und analysiert die \nRechtmäßigkeit der Datenverarbeitung gemäß der DSGVO und identifiziert mögliche Verstöße oder \nVerbesserungspotenziale.\n2) Fallstudie: Ein lokales Fitnessstudio möchte seinen Mitgliedern personalisierte Trainingspläne und \nErnährungsempfehlungen anbieten. Dafür erhebt das Fitnessstudio folgende personenbezogene Daten von seinen \nMitgliedern:\n\n• Name, Adresse, Geburtsdatum, Geschlecht\n• Größe, Gewicht, Körperfettanteil\n• Trainingsziele (z.B. Gewichtsverlust, Muskelaufbau)\n• Aktuelle körperliche Beschwerden oder Verletzungen\n• Allergien und Unverträglichkeiten\n• Fotos zur Dokumentation des Fortschritts\n\nDie Mitglieder werden um ihre Einwilligung gebeten und ihnen wird erklärt, dass die erhobenen Daten ausschließlich \nzur Erstellung der personalisierten Trainingspläne und Ernährungsempfehlungen verwendet werden. Die Daten \nwerden in einer Datenbank gespeichert, auf die nur autorisierte Trainer Zugriff haben.\n3) Präsentiert eure Ergebnisse anschließend in der Gesamtgruppe und diskutiert gemeinsam, welche Maßnahmen \ndas Fitnessstudio ergreifen sollte, um die Rechtmäßigkeit der Datenverarbeitung nach DSGVO sicherzustellen.\n\n12.04.2024\n\n58\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_58.png');
INSERT INTO pdf_pages VALUES(371,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',59,replace('RECHTMÄßIGKEIT DER VERARBEITUNG\n\nÜbung - Diskussionspunkte\n\nDiskussionspunkte:\n Welche Rechtsgrundlagen könnten für die Datenverarbeitung in diesem Fall relevant sein? (z.B. \n\nEinwilligung, Vertragserfüllung)\n\n Wurde der Grundsatz der Datenminimierung beachtet? Gibt es Daten, die nicht notwendig für den \n\nangegebenen Zweck sind?\n\n Wurden die Grundsätze der Transparenz und Informationspflicht eingehalten? Wie könnte das \n\nFitnessstudio die Mitglieder besser informieren?\n\n Wie könnte das Fitnessstudio sicherstellen, dass die Mitglieder ihre Rechte wahrnehmen können, z.B. \n\nWiderruf der Einwilligung, Auskunftsrecht, Berichtigung, Löschung?\n\n Welche technischen und organisatorischen Maßnahmen könnten das Fitnessstudio ergreifen, um die \n\nIntegrität und Vertraulichkeit der personenbezogenen Daten zu gewährleisten?\n\n12.04.2024\n\n59\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_59.png');
INSERT INTO pdf_pages VALUES(372,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',60,replace('ARBEITSTEILIGE VERARBEITUNG\nARTEN\n\n• Übermittlung zwischen (eigenständig) Verantwortlichen: was jeder \n\nVerantwortliche legt die Zwecke der jeweiligen Datenvereinbarung frei \nfest und trägt die Hauptverantwortung für die Einhaltung der DSGVO. Die \nDatenverarbeitung erfolgt jeweils auf eigener Rechtsgrundlage\n• Gemeinsam Verantwortliche: Nach Artikel 26 DSGVO ist eine \n\ngemeinsame Verantwortlichkeit von zwei oder mehr Verantwortlichen \ndie gemeinsam die Zwecke und Mittel der Verarbeitung festlegen \nmöglich. Diese Form der gemeinsamen Verantwortlichkeit wird auch als \nJoint Controllership bezeichnet. Die Beteiligten müssen in einer \nVereinbarung festlegen, wer von ihnen welche Verpflichtungen aus der \nDSGVO erfüllt.\n\n• Auftragsverarbeiter: Erfolgt eine Verarbeitung im Auftrag eines \n\nVerantwortlichen, so arbeitet dieser nur mit Auftragsverarbeitern, die \nhinreichend Garantien dafür bieten, dass geeignete technische und \norganisatorische Maßnahmen so durchgeführt werden, dass die \nVerarbeitung im Einklang mit den Anforderungen dieser Verordnung \nerfolgt und den Schutz der Rechte der betroffenen Person gewährleistet. \n(Art. 28 DSGVO)\n\n12.04.2024\n\n60\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_60.png');
INSERT INTO pdf_pages VALUES(373,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',61,replace('ARBEITSTEILIGE VERARBEITUNG\nEXKURS: AUFTRAGSVERARBEITUNGSVERTRAG (AVV)\n\nVerantwortung des Verantwortlichen\n\nAuswahlverantwortung\n\nKontrollverantwortung\n\nVor-Ort-\nKontrolle\n\nSelbstauskunft\n\nKontrolle durch \nunabhängige \nDritte\n\nQuelle: EU-DSGVO Artikel 28\n\n12.04.2024\n\n61\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\nStand und Dauer der Verarbeitung Art und Zweck der Verarbeitung\n\nIm Rahmen des Vertrags zu regeln\n•\n• Art der personenbezogenen Daten\n•\nKategorien der betroffenen Personen\n• Rechte und Pflichten des Verantwortlichen\n• Verarbeitung darf nur entsprechend dokumentierter Weisung des \n\nVerantwortlichen erfolgen\n\nEinhaltung der TOM\n\n• Verpflichtung auf Vertraulichkeit\n•\n• Bedingungen für die Inanspruchnahme weiterer Auftragsverarbeiter\n• Unterstützung des Verantwortlichen bei der Einhaltung von \n\nBetroffenenrechten\n\n• Unterstützung des Verantwortlichen bei seinen Verpflichtungen\n• Rückgabe beziehungsweise Löschung der Daten nach Ende der \n\nLeistungserbringung\n\n• Bereitstellung von Nachweisen der Pflichteinhaltung und Zulassen von \n\nInspektion\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_61.png');
INSERT INTO pdf_pages VALUES(374,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',62,replace('DSGVO – ART DES RECHTSAKTS\n\nDie DSGVO ist eine europäische Verordnung\n\nFür die Ausübung der Zuständigkeiten der Union \nnehmen die Organe Verordnungen, Richtlinien, \nBeschlüsse, Empfehlungen und Stellungnahmen an.\nDie Verordnung hat allgemeine Geltung. Sie ist in \nallen ihren Teilen verbindlich und gilt unmittelbar \nin jedem Mitgliedstaat.\nDie Richtlinie ist für jeden Mitgliedstaat, an den sie \ngerichtet wird, hinsichtlich des zu erreichenden Ziels \nverbindlich, überlässt jedoch den innerstaatlichen \nStellen die Wahl der Form und der Mittel.\nBeschlüsse sind in allen ihren Teilen verbindlich. \nSind sie an bestimmte Adressaten gerichtet, so sind \nsie nur für diese verbindlich.\nDie Empfehlungen und Stellungnahmen sind nicht \nverbindlich. (Art. 288 AEUV)\n\nÜber siebzig sogenannte Öffnungsklauseln\neröffnen die Möglichkeit der nationalen \nAusgestaltung über Bundes- und \nLandesgesetze.\n\nProminente Gesetzte umfassen: \n\n Bundesdatenschutzgesetz (BDSG)\n\n Telekommunikationsgesetz (TKG)\n\n Telemediengesetz (TMG)\n\n Landesdatenschutzgesetze\n\n Gesetz über den Kirchlichen Datenschutz \n\n(KDG)\n\n Telekommunikations-Telemedien-\nDatenschutz-Gesetz (TTDSG)\n\n Patientendatenschutzgesetz (PDSG)\n\n12.04.2024\n\n62\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_62.png');
INSERT INTO pdf_pages VALUES(375,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',63,replace('DSGVO – SCHUTZZIEL\n\nQuelle: EU-DSGVO Artikel 1\n\n12.04.2024\n\n63\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\nDiese Verordnung enthält Vorschriften zum Schutz \nnatürlicher Personen bei der Verarbeitung \npersonenbezogener Daten und zum freien Verkehr \nsolcher Daten.\n\nDiese Verordnung schützt die Grundrechte und \nGrundfreiheiten natürlicher Personen und \ninsbesondere deren Recht auf Schutz \npersonenbezogener Daten.\n\nDer freie Verkehr personenbezogener Daten in \nder Union darf aus Gründen des Schutzes \nnatürlicher Personen bei der Verarbeitung \npersonenbezogener Daten weder eingeschränkt \nnoch verboten werden.\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_63.png');
INSERT INTO pdf_pages VALUES(376,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',64,replace('DSGVO – ABWÄGUNG MIT ANDEREN RECHTEN\n\nDie Verarbeitung personenbezogener Daten sollte im Dienste der \nMenschheit stehen. \n\nDas Recht auf Schutz der personenbezogenen Daten ist kein \nuneingeschränktes Recht; es muss im Hinblick auf seine \ngesellschaftliche Funktion gesehen und unter Wahrung des \nVerhältnismäßigkeitsprinzips gegen andere Grundrechte \nabgewogen werden. \n\nDiese Verordnung steht im Einklang mit allen Grundrechten und \nachtet alle Freiheiten und Grundsätze, die mit der Charta \nanerkannt wurden und in den Europäischen Verträgen verankert \nsind, insbesondere Achtung des Privat- und Familienlebens, der \nWohnung und der Kommunikation, Schutz personenbezogener \nDaten, Gedanken-, Gewissens- und Religionsfreiheit, Freiheit der \nMeinungsäußerung und Informationsfreiheit, unternehmerische \nFreiheit, Recht auf einen wirksamen Rechtsbehelf und ein faires \nVerfahren und Vielfalt der Kulturen, Religionen und Sprachen.\n\nQuelle: EU-DSGVO Erwägungsgrund 4\n\n12.04.2024\n\n64\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_64.png');
INSERT INTO pdf_pages VALUES(377,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',65,replace('DSGVO – GLEICHWERTIGES SCHUTZNIVEAU TROTZ \nNATIONALER SPIELRÄUME\n\nUm ein gleichmäßiges und hohes Datenschutzniveau für natürliche Personen zu gewährleisten und die Hemmnisse \nfür den Verkehr personenbezogener Daten in der Union zu beseitigen, sollte das Schutzniveau für die Rechte und \nFreiheiten von natürlichen Personen bei der Verarbeitung dieser Daten in allen Mitgliedstaaten gleichwertig sein.\n\nDie Vorschriften zum Schutz der Grundrechte und Grundfreiheiten von natürlichen Personen bei der Verarbeitung \npersonenbezogener Daten sollten unionsweit gleichmäßig und einheitlich angewandt werden. \n\nHinsichtlich der Verarbeitung personenbezogener Daten zur Erfüllung einer rechtlichen Verpflichtung oder zur \nWahrnehmung einer Aufgabe, die im öffentlichen Interesse liegt oder in Ausübung öffentlicher Gewalt erfolgt, die \ndem Verantwortlichen übertragen wurde, sollten die Mitgliedstaaten die Möglichkeit haben, nationale \nBestimmungen, mit denen die Anwendung der Vorschriften dieser Verordnung genauer festgelegt wird, \nbeizubehalten oder einzuführen. \n\nIn Verbindung mit den allgemeinen und horizontalen Rechtsvorschriften über den Datenschutz zur Umsetzung der \nRichtlinie 95/46/EG gibt es in den Mitgliedstaaten mehrere sektorspezifische Rechtsvorschriften in Bereichen, \ndie spezifischere Bestimmungen erfordern. \n\nDiese Verordnung bietet den Mitgliedstaaten zudem einen Spielraum für die Spezifizierung ihrer Vorschriften, \nauch für die Verarbeitung besonderer Kategorien von personenbezogenen Daten (im Folgenden „sensible Daten“). \n\nDiesbezüglich schließt diese Verordnung nicht Rechtsvorschriften der Mitgliedstaaten aus, in denen die Umstände \nbesonderer Verarbeitungssituationen festgelegt werden, einschließlich einer genaueren Bestimmung der \nVoraussetzungen, unter denen die Verarbeitung personenbezogener Daten rechtmäßig ist.\n\nQuelle: EU-DSGVO Erwägungsgrund 10\n\n12.04.2024\n\n65\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_65.png');
INSERT INTO pdf_pages VALUES(378,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',66,replace('DSGVO – SACHLICHER ANWENDUNGSBEREICH\n\nDiese Verordnung gilt für die ganz oder teilweise \nautomatisierte Verarbeitung personenbezogener \nDaten sowie für die nichtautomatisierte Verarbeitung \npersonenbezogener Daten, die in einem \nDateisystem gespeichert sind oder gespeichert \nwerden sollen.\n\nQuelle: EU-DSGVO Artikel 2, Abs. 1\n\n12.04.2024\n\n66\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_66.png');
INSERT INTO pdf_pages VALUES(379,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',67,replace('DSGVO – SACHLICHER ANWENDUNGSBEREICH\n\nDiese Verordnung findet keine Anwendung auf die \nVerarbeitung personenbezogener Daten:\n\n im Rahmen einer Tätigkeit, die nicht in den \nAnwendungsbereich des Unionsrechts fällt,\n\n durch die Mitgliedstaaten im Rahmen von Tätigkeiten, \ndie in den Anwendungsbereich von Titel V Kapitel 2 \nEUV fallen,\n\n durch natürliche Personen zur Ausübung ausschließlich \n\npersönlicher oder familiärer Tätigkeiten,\n\n durch die zuständigen Behörden zum Zwecke der \n\nVerhütung, Ermittlung, Aufdeckung oder Verfolgung \nvon Straftaten oder der Strafvollstreckung, \neinschließlich des Schutzes vor und der Abwehr von \nGefahren für die öffentliche Sicherheit.\n\nQuelle: EU-DSGVO Artikel 2, Abs. 2\n\n12.04.2024\n\n67\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_67.png');
INSERT INTO pdf_pages VALUES(380,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',68,replace('DSGVO – SACHLICHER ANWENDUNGSBEREICH\n\n[…] Grundsätze des Datenschutzes sollten daher nicht \nfür anonyme Informationen gelten, d.h. für \nInformationen, die sich nicht auf eine identifizierte oder \nidentifizierbare natürliche Person beziehen, oder \npersonenbezogene Daten, die in einer Weise anonymisiert \nworden sind, dass die betroffene Person nicht oder nicht \nmehr identifiziert werden kann. \n\nDiese Verordnung betrifft somit nicht die Verarbeitung \nsolcher anonymer Daten, auch für statistische oder für \nForschungszwecke.\n\nQuelle: EU-DSGVO Erwägungsgrund 26\n\n12.04.2024\n\n68\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_68.png');
INSERT INTO pdf_pages VALUES(381,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',69,replace('DSGVO – SACHLICHER ANWENDUNGSBEREICH\nBESONDERER SCHUTZ\n\nRassische \nEthnische \nHerkunft\n\nPolitische \nMeinung\n\nSexualleben/\nsexuelle\nOrientierung\n\nGesundheits-\ndaten\n\nBesondere\nKategorie\npersonenbez.\nDaten\n\nReligion\nWeltan-\nschauung\n\nBesondere Kategorien personenbezogener \nDaten sind Datenkategorien, die durch das \nGesetz einen besonderen Schutz erfahren \n(sowohl juristisch als auch technisch-\norganisatorisch)\n\nBiometrische\nDaten\n\nGe-\nwerkschafts-\nzugehörig\n\nGenetische\nDaten\n\nQuelle: EU-DSGVO Art. 9 Abs. 1\n\n12.04.2024\n\n69\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_69.png');
INSERT INTO pdf_pages VALUES(382,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',70,replace('DSGVO – SACHLICHER ANWENDUNGSBEREICH\nBESONDERER SCHUTZ\n\nWeiterhin sind Daten über strafrechtliche \nVerurteilungen und Straftaten, sowie damit \nzusammenhängende Sicherungsmaßregeln \nbesonders geschützt.\n\nDeren Verarbeitung darf nur unter behördlicher \nAufsicht erfolgen oder wenn dies nach Unionsrecht \noder dem Recht der Mitgliedsstaaten zulässig ist.\n\nQuelle: EU-DSGVO Art. 10\n\n12.04.2024\n\n70\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_70.png');
INSERT INTO pdf_pages VALUES(383,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',71,replace('DSGVO – RÄUMLICHER ANWENDUNGSBEREICH\n\nDiese Verordnung findet Anwendung auf die Verarbeitung \npersonenbezogener Daten, soweit diese im Rahmen der Tätigkeiten einer \nNiederlassung eines Verantwortlichen oder eines Auftragsverarbeiters in \nder Union erfolgt, unabhängig davon, ob die Verarbeitung in der Union \nstattfindet.\n\nDiese Verordnung findet Anwendung auf die Verarbeitung \npersonenbezogener Daten von betroffenen Personen, die sich in der Union \nbefinden, durch einen nicht in der Union niedergelassenen \nVerantwortlichen oder Auftragsverarbeiter, wenn die Datenverarbeitung \nim Zusammenhang damit steht\n\n betroffenen Personen in der Union Waren oder Dienstleistungen \nanzubieten, unabhängig davon, ob von diesen betroffenen Personen \neine Zahlung zu leisten ist;\n\n das Verhalten betroffener Personen zu beobachten, soweit ihr \n\nVerhalten in der Union erfolgt.\n\nDiese Verordnung findet Anwendung auf die Verarbeitung \npersonenbezogener Daten durch einen nicht in der Union \nniedergelassenen Verantwortlichen an einem Ort, der aufgrund \nVölkerrechts dem Recht eines Mitgliedstaats unterliegt.\n\nQuelle: EU-DSGVO Art. 3\n\n12.04.2024\n\n71\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_71.png');
INSERT INTO pdf_pages VALUES(384,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',72,replace('DSGVO – ANGRENZENDE RECHTSREGIME\nEU-GRUNDRECHTECHARTA\n\nArt. 1: Die Würde des Menschen ist unantastbar. Sie ist zu achten und zu \nschützen.\n\nArt. 8: \n\n(1) Jede Person hat das Recht auf Schutz der sie betreffenden \npersonenbezogenen Daten.\n\n(2) Diese Daten dürfen nur nach Treu und Glauben für festgelegte \nZwecke und mit Einwilligung der betroffenen Person oder auf einer \nsonstigen gesetzlich geregelten legitimen Grundlage verarbeitet werden. \nJede Person hat das Recht, Auskunft über die sie betreffenden erhobenen \nDaten zu erhalten und die Berichtigung der Daten zu erwirken.\n\n(3) Die Einhaltung dieser Vorschriften wird von einer unabhängigen Stelle \nüberwacht.\n\nQuelle: EU-Grundrechtecharta Art. 1 und 8\n\n12.04.2024\n\n72\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_72.png');
INSERT INTO pdf_pages VALUES(385,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',73,replace('DSGVO – ANGRENZENDE RECHTSREGIME\nGRUNDGESETZ\n\nArtikel 1\n\n(1) Die Würde des Menschen ist unantastbar. Sie zu achten und zu \nschützen ist Verpflichtung aller staatlichen Gewalt.\n\n(2) Das Deutsche Volk bekennt sich darum zu unverletzlichen und \nunveräußerlichen Menschenrechten als Grundlage jeder menschlichen \nGemeinschaft, des Friedens und der Gerechtigkeit in der Welt.\n\n(3) Die nachfolgenden Grundrechte binden Gesetzgebung, vollziehende \nGewalt und Rechtsprechung als unmittelbar geltendes Recht.\n\nArtikel 2:\n\n(1) Jeder hat das Recht auf die freie Entfaltung seiner Persönlichkeit, \nsoweit er nicht die Rechte anderer verletzt und nicht gegen die \nverfassungsmäßige Ordnung oder das Sittengesetz verstößt.\n\n(2) Jeder hat das Recht auf Leben und körperliche Unversehrtheit. Die \nFreiheit der Person ist unverletzlich. In diese Rechte darf nur auf Grund \neines Gesetzes eingegriffen werden.\n\nQuelle: Grundgesetz der BRD Art. 1\n\n12.04.2024\n\n73\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_73.png');
INSERT INTO pdf_pages VALUES(386,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',74,replace('DSGVO – ANGRENZENDE RECHTSREGIME\nRICHTLINIE 680 / 2016\n\nRICHTLINIE (EU) 2016/680 DES EUROPÄISCHEN PARLAMENTS UND \nDES RATES \n\nvom 27. April 2016 \n\nzum Schutz natürlicher Personen bei der Verarbeitung \npersonenbezogener Daten durch die \n\nzuständigen Behörden zum Zwecke der Verhütung, Ermittlung, \nAufdeckung oder Verfolgung von \n\nStraftaten oder der Strafvollstreckung sowie zum freien Datenverkehr\n\nQuelle: Richtlinie 680 / 2016\n\n12.04.2024\n\n74\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_74.png');
INSERT INTO pdf_pages VALUES(387,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',75,replace('DSGVO – ANGRENZENDE RECHTSREGIME\nKASKADE\n\n• Europäische Ebene\n\nDSGVO\n\nAndere Verordnungen\n(e.g. ePrivacy)\n\n• Nationale Ebene\n\nSGB I / SGB X\n\nDatenschutzregelungen in \nSpezialgesetzen (e.g. TMG)\n\nBDSG\n\nLandesdatenschutzgesetz\n\n• Sonderregelungen mit\nGesetzescharakter\n\nTarifvertrag\n\nBetriebsvereinbarung/\nDienstvereinbarung\n\n12.04.2024\n\n75\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_75.png');
INSERT INTO pdf_pages VALUES(388,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',76,replace('DSGVO – ANGRENZENDE RECHTSREGIME\nBDSG\n\nTeil 1\n\nTeil 2\n\n• Gemeinsame Regelungen für öffentliche und \n\nnicht-öffentliche Stellen, \ninsb. Rechtsgrundlagen für öffentliche Stellen und \nVideoüberwachung und zum Bundesbeauftragten \nfür Datenschutz und die Informationsfreiheit.\n\n• Durchführungsbestimmungen zur DSGVO, \ninsb. Rechtsgrundlagen der Verarbeitung pbD, \nRechte der betroffenen Person, Pflichten der \nVerantwortlichen und Auftragsverarbeiter, \nAufsichtsbehörden sowie Sanktionen und \nRechtsbehelfe\n\nTeil 3\n\nTeil 4\n\n• Datenschutz für Polizei und Justiz\n\ninsb. Anwendungsbereich, Rechtsgrundlagen, \nBetroffenenrechte, Pflichten der Verantwortlichen \nund Auftragsverarbeiter, Datenübermittlung an \nDrittstaaten, Zusammenarbeit mit den \nAufsichtsbehörden, Haftung und Sanktionen\n\nQuelle: Bundesdatenschutzgesetz\n\n12.04.2024\n\n76\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n• Besondere Bestimmungen für Tätigkeiten \naußerhalb der Anwendungsbereiche der \nDSGVO und der JI-Richtlinie \n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_76.png');
INSERT INTO pdf_pages VALUES(389,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',77,replace('DSGVO – AUFBAU\n\n• Einleitung\n\n• Erwägungsgründe\n\n• Überleitung\n\n• Rechtsnormen\n\n[…] in Erwägung \nnachstehender\nGründe […]\n\n173 Erwägungsgründe, die \ndarstellen, welche \nZielstellungen mit den \nverschiedenen Rechtsnormen \nverfolgt werden sollen.\n\n[ …] haben \nfolgende \nVerordnung \nerlassen […]\n\n99 Artikel in 11 Kapiteln\n\n• Kapitel I\n\nAllg. \nBestimmungen\n\n• Kapitel II\n\n• Kapitel III\n\n• Kapitel IV\n\nGrundsätze und \nRechtmäßigkeit\n\nRechte der \nbetroffenen Pers.\n\nVerantwortlicher & \nAuftragsverarbeiter\n\n• Kapitel V\n\nÜbermittlung \nDrittländer\n\n• Kapitel VI / VII\n\n• Kapitel VIII\n\n• Kapitel IX\n\nAufsicht\n\nRechtsbehelfe & \nSanktionen\n\nBes. Verarbeitungs-\nsituationen\n\n• Kapitel X\n\nDeligierte und \nDurchführungs-\nrechtsakte\n\n• Kapitel XI\n\nSchluss-\nbestimmungen\n\nQuelle: DSGVO\n\n12.04.2024\n\n77\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_77.png');
INSERT INTO pdf_pages VALUES(390,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',78,replace('DSGVO – RECHTMÄßIGKEIT DER VERARBEITUNG\n\nDie Verarbeitung ist nur rechtmäßig, wenn mindestens eine der \nnachstehenden Bedingungen erfüllt ist:\n\n Die betroffene Person hat ihre Einwilligung zu der Verarbeitung der sie \n\nbetreffenden personenbezogenen Daten für einen oder mehrere bestimmte \nZwecke gegeben;\n\n die Verarbeitung ist für die Erfüllung eines Vertrags, dessen Vertragspartei die \n\nbetroffene Person ist, oder zur Durchführung vorvertraglicher Maßnahmen \nerforderlich, die auf Anfrage der betroffenen Person erfolgen;\n\n die Verarbeitung ist zur Erfüllung einer rechtlichen Verpflichtung erforderlich, \n\nder der Verantwortliche unterliegt;\n\n die Verarbeitung ist erforderlich, um lebenswichtige Interessen der \n\nbetroffenen Person oder einer anderen natürlichen Person zu schützen;\n\n die Verarbeitung ist für die Wahrnehmung einer Aufgabe erforderlich, die im \n\nöffentlichen Interesse liegt oder in Ausübung öffentlicher Gewalt erfolgt, die \ndem Verantwortlichen übertragen wurde;\n\n die Verarbeitung ist zur Wahrung der berechtigten Interessen des \n\nVerantwortlichen oder eines Dritten erforderlich, sofern nicht die Interessen \noder Grundrechte und Grundfreiheiten der betroffenen Person, die den Schutz \npersonenbezogener Daten erfordern, überwiegen, insbesondere dann, wenn es \nsich bei der betroffenen Person um ein Kind handelt.   […]\n\nQuelle: DSGVO Art. 9\n\n12.04.2024\n\n78\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_78.png');
INSERT INTO pdf_pages VALUES(391,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',79,replace('DSGVO – RECHTMÄßIGKEIT DER VERARBEITUNG\n\nDie Verarbeitung personenbezogener Daten, aus denen die rassische und \nethnische Herkunft, […] ist untersagt.\nAbsatz 1 gilt nicht in folgenden Fällen:\n\n Die betroffene Person hat […] festgelegte Zwecke ausdrücklich eingewilligt\n\n die Verarbeitung ist erforderlich, damit […] Arbeitsrecht und dem Recht der sozialen Sicherheit und des \n\nSozialschutzes erwachsenden Rechte ausüben\n\n die Verarbeitung ist zum Schutz lebenswichtiger Interessen der betroffenen Person […],\n\n die Verarbeitung erfolgt auf der Grundlage geeigneter Garantien durch eine […] Stiftung, Vereinigung \n\noder sonstige Organisation ohne Gewinnerzielungsabsicht […]\n\n die Verarbeitung bezieht sich auf personenbezogene Daten, die die betroffene Person offensichtlich \n\nöffentlich gemacht hat,\n\n die Verarbeitung ist zur Geltendmachung, Ausübung oder Verteidigung von Rechtsansprüchen oder bei \n\nHandlungen der Gerichte […] erforderlich,\n\n die Verarbeitung ist auf der Grundlage des Unionsrechts oder des Rechts eines Mitgliedstaats […] eines \n\nerheblichen öffentlichen Interesses erforderlich,\n\n die Verarbeitung ist für Zwecke der Gesundheitsvorsorge oder der Arbeitsmedizin, für die \n\nBeurteilung der Arbeitsfähigkeit des Beschäftigten […]\n\n die Verarbeitung ist aus Gründen des öffentlichen Interesses im Bereich der öffentlichen Gesundheit \n\n[…]\n\n die Verarbeitung ist auf der Grundlage des Unionsrechts oder des Rechts eines Mitgliedstaats, […] \n\nspezifische Maßnahmen zur Wahrung der Grundrechte\n\nQuelle: DSGVO Art. 9\n\n12.04.2024\n\n79\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_79.png');
INSERT INTO pdf_pages VALUES(392,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',80,replace('DSGVO\n\nÜbungen\n\n(Password: DaSchu_ITS_24) \n\n1. Sie betreiben einen Webshop für seltene Kaugummi-Sorten. Bei der Bestellung erheben Sie \nVorname, Nachname, Geburtsdatum, Adresse, Telefonnummer und Kontodaten. Ist die zur \nVertragsabwicklung zu gerechtfertigten (i. S. von Art. 5 Abs. 1 DSGVO)?\n\n2. Sie versenden als Mitarbeiter*in des obenstehenden Webshops eine E-Mail bzgl. aktueller \n\nRestposten und Sonderangebote an sämtliche Geschäfts- und Privatkunden. Die Empfängeradresse \nhat größtenteils die Form: vorname.nachname@unternehmen.de. Sie führen alle Adressen in CC \nauf. Ist der Versand (i.S. Art 5 Abs. 1) rechtmäßig?\n\n3. Ein Kunde wendet sich an Ihr Unternehmen bzw. den DSB, da er in den Medien gehört hat, dass Sie \nDaten aus Social Media und von Werbepartnern aggregieren, um zugeschnittene Werbung zu \nversenden. Der Kunde möchte darüber informiert werden, welche Informationen von ihm im \nUnternehmen genutzt und verarbeitet werden. Wie sollte Ihr Unternehmen (i. S. Art 15 DSGVO) \nreagieren?\n\n12.04.2024\n\n80\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_80.png');
INSERT INTO pdf_pages VALUES(393,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',81,replace('DSGVO\n\nÜbungen\n\n(Password: DaSchu_ITS_24) \n\n5. Was ist mit Unionsbezug der Datenverarbeitung gemeint?\n6. Nennen Sie fünf Inhalte von Öffnungsklauseln der DSGVO.\n7. Nennen Sie drei Bedingungen, die eine Rechtmäßigkeit der Verarbeitung legitimieren.\n8. Nennen Sie drei Beispiele für besondere Kategorien personenbezogener Daten und Situationen in \n\ndenen diese (legal) verarbeitet werden.\n\n9. Was ist der Unterschied zwischen Pseudonymisierung und Anonymisierung? Erläutern Sie Ihre \n\nAntwort an einem Beispiel.\n\n10.Erläutern Sie „lex specialis derogat legi generali“ anhand eines Beispiels im Bereich des \n\nDatenschutzes\n\n12.04.2024\n\n81\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_81.png');
INSERT INTO pdf_pages VALUES(394,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',82,replace('FOKUS PRAXIS\n\nAufsichtsbehörden\n\nDer Datenschutzbeauftragte\n\nPflichten des Verantwortlichen\n\nAuftragsverarbeiter \n\nKonsequenzen aus Verstößen \n\nRisikobegriff im Datenschutz und \nDatenschutzfolgeabschätzung\n\n12.04.2024\n\n82\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n01\n\n02\n\n03\n\n04\n\n05\n\n06\n\nBis 10:30 Uhr\n\nCa. 20 Minuten\n\nBis 12:15 Uhr\n\nCa. 30 Minuten\n\nCa. 5 Minuten\n\nBis 14:30 Uhr\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_82.png');
INSERT INTO pdf_pages VALUES(395,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',83,replace('DSGVO – AUFSICHTSBEHÖRDE\nZUSTÄNDIGKEIT (NATIONAL)\n\nJede Aufsichtsbehörde ist für die Erfüllung der Aufgaben und die \nAusübung der Befugnisse, die ihr mit dieser Verordnung übertragen \nwurden, im Hoheitsgebiet ihres eigenen Mitgliedstaats zuständig.\nErfolgt die Verarbeitung durch Behörden oder private Stellen auf der Grundlage von Artikel 6 \nAbsatz 1 Buchstabe c oder e, so ist die Aufsichtsbehörde des betroffenen Mitgliedstaats \nzuständig. In diesem Fall findet Artikel 56 keine Anwendung.\n\nDie Aufsichtsbehörden sind nicht zuständig für die Aufsicht über die von Gerichten im \nRahmen ihrer justiziellen Tätigkeit vorgenommenen Verarbeitungen.\n\nQuelle: DSGVO Art. 55\n\n12.04.2024\n\n83\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_83.png');
INSERT INTO pdf_pages VALUES(396,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',84,replace('DSGVO – AUFSICHTSBEHÖRDE\nZUSTÄNDIGKEIT (NATIONAL)\n\nBundesdatenschutzbeauftragter\n\nLandesdatenschutzbeauftragte(r)\n\nZuständigkeit\n\n• nichtöffentlichen Stellen\n\n• Landesbehörden\n\n• Öffentliche Stellen des Landes\n\n• Konkrete Festlegungen im \n\njeweiligen \nLandesdatenschutzrecht\n\n• Bundesbehörden\n\n• Öffentliche Stellen des Bundes\n\n• Verarbeitungen, die zu den \n\nErbringungen von \nTelekommunikationsdiensten \noder Postdienstleistungen \ndurchgeführt werden\n\n• Bundesgerichte im Rahmen der \n\nVerwaltungstätigkeiten\n\n12.04.2024\n\n84\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_84.png');
INSERT INTO pdf_pages VALUES(397,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',85,replace('DSGVO – AUFSICHTSBEHÖRDE\nZUSTÄNDIGKEIT (INTERNATIONAL)\n\nUnbeschadet des Artikels 55 ist die Aufsichtsbehörde der Hauptniederlassung oder der \neinzigen Niederlassung des Verantwortlichen oder des Auftragsverarbeiters gemäß \ndem Verfahren nach Artikel 60 die zuständige federführende Aufsichtsbehörde für die \nvon diesem Verantwortlichen oder diesem Auftragsverarbeiter durchgeführte \ngrenzüberschreitende Verarbeitung.\nAbweichend von Absatz 1 ist jede Aufsichtsbehörde dafür zuständig, sich mit einer bei ihr eingereichten Beschwerde oder \neinem etwaigen Verstoß gegen diese Verordnung zu befassen, wenn der Gegenstand nur mit einer Niederlassung in \nihrem Mitgliedstaat zusammenhängt oder betroffene Personen nur ihres Mitgliedstaats erheblich beeinträchtigt.\n\nIn den in Absatz 2 des vorliegenden Artikels genannten Fällen unterrichtet die Aufsichtsbehörde unverzüglich die \nfederführende Aufsichtsbehörde über diese Angelegenheit. Innerhalb einer Frist von drei Wochen nach der Unterrichtung \nentscheidet die federführende Aufsichtsbehörde, ob sie sich mit dem Fall gemäß dem Verfahren nach Artikel 60 befasst \noder nicht, wobei sie berücksichtigt, ob der Verantwortliche oder der Auftragsverarbeiter in dem Mitgliedstaat, dessen \nAufsichtsbehörde sie unterrichtet hat, eine Niederlassung hat oder nicht.\n\nEntscheidet die federführende Aufsichtsbehörde, sich mit dem Fall zu befassen, so findet das Verfahren nach Artikel 60 \nAnwendung. Die Aufsichtsbehörde, die die federführende Aufsichtsbehörde unterrichtet hat, kann dieser einen \nBeschlussentwurf vorlegen. Die federführende Aufsichtsbehörde trägt diesem Entwurf bei der Ausarbeitung des \nBeschlussentwurfs nach Artikel 60 Absatz 3 weitestgehend Rechnung.\n\nEntscheidet die federführende Aufsichtsbehörde, sich mit dem Fall nicht selbst zu befassen, so befasst die \nAufsichtsbehörde, die die federführende Aufsichtsbehörde unterrichtet hat, sich mit dem Fall gemäß den Artikeln 61 und \n62.\n\nDie federführende Aufsichtsbehörde ist der einzige Ansprechpartner der Verantwortlichen oder der Auftragsverarbeiter für \nFragen der von diesem Verantwortlichen oder diesem Auftragsverarbeiter durchgeführten grenzüberschreitenden \nVerarbeitung.\n\nQuelle: DSGVO Art. 56\n\n12.04.2024\n\n85\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_85.png');
INSERT INTO pdf_pages VALUES(398,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',86,replace('DSGVO – AUFSICHTSBEHÖRDE\nZUSTÄNDIGKEIT - HAUPTNIEDERLASSUNG\n\n„Hauptniederlassung“\n\nim Falle eines Verantwortlichen mit Niederlassungen in mehr als einem Mitgliedstaat den Ort \nseiner Hauptverwaltung in der Union, es sei denn, die Entscheidungen hinsichtlich der \nZwecke und Mittel der Verarbeitung personenbezogener Daten werden in einer \nanderen Niederlassung des Verantwortlichen in der Union getroffen und diese \nNiederlassung ist befugt, diese Entscheidungen umsetzen zu lassen; in diesem Fall gilt die \nNiederlassung, die derartige Entscheidungen trifft, als Hauptniederlassung;\n\nim Falle eines Auftragsverarbeiters mit Niederlassungen in mehr als einem Mitgliedstaat den \nOrt seiner Hauptverwaltung in der Union oder, sofern der Auftragsverarbeiter keine \nHauptverwaltung in der Union hat, die Niederlassung des Auftragsverarbeiters in der Union, \nin der die Verarbeitungstätigkeiten im Rahmen der Tätigkeiten einer Niederlassung eines \nAuftragsverarbeiters hauptsächlich stattfinden, soweit der Auftragsverarbeiter spezifischen \nPflichten aus dieser Verordnung unterliegt;\n\nQuelle: DSGVO Art. 4 Abs. 16\n\n12.04.2024\n\n86\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_86.png');
INSERT INTO pdf_pages VALUES(399,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',87,replace('DSGVO – AUFSICHTSBEHÖRDE\nAUFGABEN\n\nUnbeschadet anderer in dieser Verordnung dargelegter Aufgaben muss jede Aufsichtsbehörde in ihrem \nHoheitsgebiet\n\n die Anwendung dieser Verordnung überwachen und durchsetzen;\n die Öffentlichkeit für die Risiken, Vorschriften, Garantien und Rechte im Zusammenhang mit der \n\nVerarbeitung sensibilisieren und sie darüber aufklären. Besondere Beachtung finden dabei spezifische \nMaßnahmen für Kinder;\n\n im Einklang mit dem Recht des Mitgliedsstaats das nationale Parlament, die Regierung und andere \nEinrichtungen und Gremien über legislative und administrative Maßnahmen zum Schutz der Rechte \nund Freiheiten natürlicher Personen in Bezug auf die Verarbeitung beraten;\n\n die Verantwortlichen und die Auftragsverarbeiter für die ihnen aus dieser Verordnung entstehenden \n\nPflichten sensibilisieren;\n\n auf Anfrage jeder betroffenen Person Informationen über die Ausübung ihrer Rechte aufgrund dieser \nVerordnung zur Verfügung stellen und gegebenenfalls zu diesem Zweck mit den Aufsichtsbehörden in \nanderen Mitgliedstaaten zusammenarbeiten;\n\nQuelle: DSGVO Art. 57\n\n12.04.2024\n\n87\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_87.png');
INSERT INTO pdf_pages VALUES(400,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',88,replace('DSGVO – AUFSICHTSBEHÖRDE\nAUFGABEN\n\nUnbeschadet anderer in dieser Verordnung dargelegter Aufgaben muss jede Aufsichtsbehörde in ihrem \nHoheitsgebiet\n\n sich mit Beschwerden einer betroffenen Person oder Beschwerden einer Stelle, einer Organisation oder \n\neines Verbandes gemäß Artikel 80 befassen, den Gegenstand der Beschwerde in angemessenem \nUmfang untersuchen und den Beschwerdeführer innerhalb einer angemessenen Frist über den \nFortgang und das Ergebnis der Untersuchung unterrichten, insbesondere, wenn eine weitere \nUntersuchung oder Koordinierung mit einer anderen Aufsichtsbehörde notwendig ist;\n\n mit anderen Aufsichtsbehörden zusammenarbeiten, auch durch Informationsaustausch, und ihnen \n\nAmtshilfe leisten, um die einheitliche Anwendung und Durchsetzung dieser Verordnung zu \ngewährleisten;\n\n Untersuchungen über die Anwendung dieser Verordnung durchführen, auch auf der Grundlage von \n\nInformationen einer anderen Aufsichtsbehörde oder einer anderen Behörde;\n\n maßgebliche Entwicklungen verfolgen, soweit sie sich auf den Schutz personenbezogener Daten \n\nauswirken, insbesondere die Entwicklung der Informations- und Kommunikationstechnologie und der \nGeschäftspraktiken;\n\n Standardvertragsklauseln im Sinne des Artikels 28 Absatz 8 und des Artikels 46 Absatz 2 Buchstabe d \n\nfestlegen;\n\n eine Liste der Verarbeitungsarten erstellen und führen, für die gemäß Artikel 35 Absatz 4 eine \n\nDatenschutz-Folgenabschätzung durchzuführen ist;\n\nQuelle: DSGVO Art. 57\n\n12.04.2024\n\n88\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_88.png');
INSERT INTO pdf_pages VALUES(401,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',89,replace('DSGVO – AUFSICHTSBEHÖRDE\nAUFGABEN\n\nUnbeschadet anderer in dieser Verordnung dargelegter Aufgaben muss jede Aufsichtsbehörde in ihrem \nHoheitsgebiet\n\n Beratung in Bezug auf die in Artikel 36 Absatz 2 genannten Verarbeitungsvorgänge leisten;\n die Ausarbeitung von Verhaltensregeln gemäß Artikel 40 Absatz 1 fördern und zu diesen \n\nVerhaltensregeln, die ausreichende Garantien im Sinne des Artikels 40 Absatz 5 bieten müssen, \nStellungnahmen abgeben und sie billigen;\n\n die Einführung von Datenschutzzertifizierungsmechanismen und von Datenschutzsiegeln und -\n\nprüfzeichen nach Artikel 42 Absatz 1 anregen und Zertifizierungskriterien nach Artikel 42 Absatz 5 \nbilligen;\n\n gegebenenfalls die nach Artikel 42 Absatz 7 erteilten Zertifizierungen regelmäßig überprüfen;\n die Anforderungen an die Akkreditierung einer Stelle für die Überwachung der Einhaltung der \n\nVerhaltensregeln gemäß Artikel 41 und einer Zertifizierungsstelle gemäß Artikel 43 abfassen und \nveröffentlichen;\n\nQuelle: DSGVO Art. 57\n\n12.04.2024\n\n89\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_89.png');
INSERT INTO pdf_pages VALUES(402,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',90,replace('DSGVO – AUFSICHTSBEHÖRDE\nAUFGABEN\n\nUnbeschadet anderer in dieser Verordnung dargelegter Aufgaben muss jede Aufsichtsbehörde in ihrem \nHoheitsgebiet\n\n die Akkreditierung einer Stelle für die Überwachung der Einhaltung der Verhaltensregeln gemäß Artikel \n\n41 und einer Zertifizierungsstelle gemäß Artikel 43 vornehmen;\n\n Vertragsklauseln und Bestimmungen im Sinne des Artikels 46 Absatz 3 genehmigen;\n verbindliche interne Vorschriften gemäß Artikel 47 genehmigen;\n Beiträge zur Tätigkeit des Ausschusses leisten;\n interne Verzeichnisse über Verstöße gegen diese Verordnung und gemäß Artikel 58 Absatz 2 ergriffene \n\nMaßnahmen und\n\n jede sonstige Aufgabe im Zusammenhang mit dem Schutz personenbezogener Daten erfüllen.\n\nQuelle: DSGVO Art. 57\n\n12.04.2024\n\n90\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_90.png');
INSERT INTO pdf_pages VALUES(403,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',91,replace('DSGVO – AUFSICHTSBEHÖRDE\nBEFUGNISSE\n\nUntersuchungsbefugnisse\n\nAbhilfebefugnisse\n\n• Warnen (bei Verstößen)\n\n• Verwarnen (bei Verstößen)\n\n• Anweisen Betroffenenrechte umzusetzen\n\n• Anweisen Verarbeitungsvorgänge \n\ngegebenenfalls auf bestimmte Weise und \ninnerhalb eines bestimmten Zeitraums in \nEinklang mit dieser Verordnung zu bringen,\n\n• Anweisen Betroffenen zu informieren\n\n• eine vorübergehende oder endgültige \n\nGenehmigungsbefugnisse,\nberatende Befugnisse\n\n• Verantwortliche zu beraten,\n\n• zu allen Fragen, die im Zusammenhang mit \ndem Schutz personenbezogener Daten \nstehen, von sich aus oder auf Anfrage \nStellungnahmen an das nationale Parlament, \ndie Regierung des Mitgliedstaats oder im \nEinklang mit dem Recht des Mitgliedstaats \nan sonstige Einrichtungen und Stellen sowie \nan die Öffentlichkeit zu richten,\n\nBeschränkung der Verarbeitung, einschließlich \neines Verbots, zu verhängen,\n\n• eine Stellungnahme abzugeben und \n\nEntwürfe von Verhaltensregeln zu billigen,\n\n• die Berichtigung oder Löschung von \npersonenbezogenen Daten oder die \nEinschränkung der Verarbeitung […] \nanzuordnen,\n\n• eine Zertifizierung zu widerrufen\n\n• Standarddatenschutzklauseln festzulegen\n\n• Vertragsklauseln zu genehmigen,\n\n• Verwaltungsvereinbarungen zu genehmigen\n\n• verbindliche interne Vorschriften \n\n• eine Geldbuße gemäß Artikel 83 zu verhängen,\n\ngenehmigen.\n\n• die Aussetzung der Übermittlung von Daten an \n\neinen Empfänger in einem Drittland oder an eine \ninternationale Organisation anzuordnen.\n\n• Anordnung zur Herausgabe von \n\nInformationen\n\n• Untersuchungen in Form von \nDatenschutzüberprüfungen\n\n• eine Überprüfung Zertifizierungen\n\n• Hinweise zu Verstößen geben\n\n• Zugang zu allen personenbezogenen Daten \nund Informationen, die zur Erfüllung der \nDSGVO notwendig sind\n\n• Zugang zu den Räumlichkeiten, \n\neinschließlich aller \nDatenverarbeitungsanlagen und -geräte, des \nVerantwortlichen und des \nAuftragsverarbeiters zu erhalten\n\nQuelle: DSGVO Art. 58\n\n12.04.2024\n\n91\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_91.png');
INSERT INTO pdf_pages VALUES(404,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',92,replace('DSGVO\n\nÜbungen\n\n(Password: DaSchu_ITS_24) \n\n Wie lautet die für Sie zuständige Aufsichtsbehörde, wenn sich die Hauptniederlassung des relevanten \n\nVerantwortlichen in Hamburg befindet.\n\n Recherchieren Sie die Kontaktdaten und den Namen des Landesdatenschutzbeauftragten\n Wie könne Sie einen Datenschutzbeauftragen bei der Behörde anmelden?\n\n12.04.2024\n\n92\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_92.png');
INSERT INTO pdf_pages VALUES(405,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',93,replace('DSGVO – DER DATENSCHUTZBEAUFTRAGTE\nROLLE INNERHALB DER ORGANISATION\n\nDer Datenschutzbeauftragte…\n …fungiert als Berater des Verantwortlichen oder Auftragsverarbeiter im \n\nZusammenhang mit der Verarbeitung personenbezogener Daten,\n\n …ist gleichzeitig das diesbezügliche Kontrollorgan in der Organisation und\n … ist die Schnittstelle zu den Aufsichtsbehörden und den betroffenen \n\nPersonen, wenn diese Fragen zur Verarbeitung sie betreffender \npersonenbezogener Daten haben.\n\n12.04.2024\n\n93\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_93.png');
INSERT INTO pdf_pages VALUES(406,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',94,replace('DSGVO – DER DATENSCHUTZBEAUFTRAGTE\nMÖGLICHKEITEN\n\nTeilzeit\n\nExtern\n\n• Nebenamtlicher interner Datenschutzbeauftragter \n\n(Art. 37 Abs. 6)\n\n• Externer Datenschutzbeauftragter (Dienstleister) \n\n(Art. 37 Abs. 6)\n\n• Hauptamtlicher interner Datenschutzbeauftragter\n\nHauptamtlich Intern\n\n(Art. 37 Abs. 6)\n\nKonzern-DSB\n\nÖfftl. gemeinsamer-DSB \n\n• Gemeinsamer Datenschutzbeauftragter innerhalb \n\neiner Unternehmensgruppe (ermöglicht \nKonzerndatenschutzbeauftragten – Art. 37 Abs. 2)\n\n• Gemeinsamer Datenschutzbeauftragter für \nmehrere Behörden oder öffentliche Stellen\n(Art. 37 Abs. 3)\n\nQuelle: DSGVO Art. 37\n\n12.04.2024\n\n94\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_94.png');
INSERT INTO pdf_pages VALUES(407,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',95,replace('DSGVO – DER DATENSCHUTZBEAUFTRAGTE\nBENENNUNG\n\nBDSG\n§ 38 Abs. 2\n\nBDSG\n§ 38 Abs. 1\n\nBDSG\n§ 6 Abs. 4\n\nKündigungsschutz\n\nBDSG\n§ 6 Abs. 5\n\nDSGVO\nArt. 37 Abs. 1 \n\nVerweiskette\n\nVerschwiegenheit\n\nPflicht zur Benennung\n\nBDSG\n§ 6 Abs. 6\n\nBenennung des DSB\n\nQuelle: DSGVO Art. 37,  BDSG § 38 und § 6\n\n12.04.2024\n\n95\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\nZeugnisverweigerungs-\nrecht\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_95.png');
INSERT INTO pdf_pages VALUES(408,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',96,replace('DSGVO – DER DATENSCHUTZBEAUFTRAGTE\nVORAUSSETZUNGEN\n\n Der Datenschutzbeauftragte wird auf der Grundlage seiner beruflichen \n\nQualifikation und insbesondere des Fachwissens benannt, das er auf \ndem Gebiet des Datenschutzrechts und der Datenschutzpraxis besitzt, sowie \nauf der Grundlage seiner Fähigkeit zur Erfüllung der in Artikel 39 genannten \nAufgaben.\n\n Der Datenschutzbeauftragte kann andere Aufgaben und Pflichten \n\nwahrnehmen. Der Verantwortliche oder der Auftragsverarbeiter stellt sicher, \ndass derartige Aufgaben und Pflichten nicht zu einem Interessenkonflikt \nführen.\n\nQuelle: DSGVO Art. 37 & 38\n\n12.04.2024\n\n96\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_96.png');
INSERT INTO pdf_pages VALUES(409,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',97,replace('DSGVO\n\nÜbungen\n\n(Password: DaSchu_ITS_24) \n\n Wer ist der Datenschutzbeauftragte der IU?\n Wer ist der Datenschutzbeauftragte Ihres Partnerunternehmens?\n Nennen Sie drei Aufgaben des DSB.\n\n12.04.2024\n\n97\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_97.png');
INSERT INTO pdf_pages VALUES(410,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',98,replace('DSGVO – RECHENSCHAFTSPFLICHT \nDES VERANTWORTLICHEN\n\nDer Verantwortliche unterliegt einer Rechenschafts-\nund Nachweispflicht über…\n\nDer Verantwortliche muss im Rahmen der \nRechenschafts- und Nachweispflicht insb.:\n\n … die Einhaltung (der Grundsätze) der DSGVO \n\n(vgl. Art. 5 Abs. 2 und Art. 24 Abs. 1)\n\n … die Gewährleistung der Sicherheit der \n\nVerarbeitung durch ein Verfahren zur \nregelmäßigen Überprüfung, Bewertung und \nEvaluation der Wirksamkeit der entsprechenden \nMaßnahmen\n\n• Die Rechtmäßigkeit von Verarbeitungen \npersonenbezogener Daten sicherstellen.\n\n• Informationspflichten erfüllen\n\n• Betroffenenrechte umsetzen\n\n• Datenpannen melden und behandeln\n\n• Sensibilisieren und Schulen\n\n• Das Verzeichnis der Verarbeitungen erstellen und \n\nfortführen.  \n\nDie Einhaltung der Vorgaben impliziert eine umfangreiche Dokumentation\n Praktische Notwendigkeit eines Datenschutzmanagementsystems\n\n12.04.2024\n\n98\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_98.png');
INSERT INTO pdf_pages VALUES(411,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',99,replace('DSGVO – RECHENSCHAFTSPFLICHT \nDES VERANTWORTLICHEN - DSMS\n\nVerzeichnis der Verarbeitungstätigkeiten\n\nDokumentation\n\nDatenschutzhandbuch\n\nDarstellung der Zuständigkeiten\n\nRechtmäßigkeitsgrundlagen\n\nDatenschutzleitlinie\n\nUmgang mit Dokumenten\n\nRegelungswerk\n\nProzessbeschreibungen\n\nArbeits-\nanweisungen\n\nDSMS\n\nTOMs\n\nPrüfprozesse\n\nVerpflichtungen und Schulungsnachweise\n\n12.04.2024\n\n99\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\nEin Datenschutzmanagementsystem kann als \ndie Summe aller Dokumentation und \nUmsetzungen von Datenschutz-bezogenen \nRegelungen, Prozessen und Maßnahmen \ndefiniert werden.  \n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_99.png');
INSERT INTO pdf_pages VALUES(412,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',100,replace('DSGVO\n\nÜbungen\n\n(Password: DaSchu_ITS_24) \n\n Laden Sie die Vorlage eines VVV bei der DSK herunter: 201802_ah_muster_verantwortliche.pdf \n\n(datenschutzkonferenz-online.de)\n(Hinweise: Hinweise-zum-Verzeichnis-von-Verarbeitungstaetigkeiten.pdf (datenschutzzentrum.de))\n\n Füllen Sie die Vorlage für eine PBD-verarbeitende Tätigkeit bei Ihrem Praxispartner aus. Verändern Sie \nggf. den Verarbeitungsprozesse so, dass keine Vertraulichkeitsverpflichtungen gebrochen werden.\n\n Diskutieren Sie in der Kleingruppe:\n\n Was fällt schwer? Was ist einfach?\n Kennen Sie den Ablageort des VVV in Ihrem Unternehmen? Waren Sie an der Erstellung oder Pflege \n\nbeteiligt?\n\n Wie viele Prozesse müssten schätzungsweise bei Ihrem Praxispartner erfasst werden?\n\n Im Anschluss: Zufällige Kleingruppe stellt das Ergebnis vor.\n\n12.04.2024\n\n100\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_100.png');
INSERT INTO pdf_pages VALUES(413,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',101,replace('DSGVO – ARBEITSTEILIGE VERARBEITUNG PBD\n\nInformation\n\nGenehmigung\n\nVerantwortlicher\n\nAuftragsverarbeiter\n\nWeiterer Auftragsverarbeiter\n\nVertrag\n\nVertrag\n\nDatenschutzverpflichtung\n\n12.04.2024\n\n101\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_101.png');
INSERT INTO pdf_pages VALUES(414,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',102,replace('DSGVO\n\nÜbungen\n\n(Password: DaSchu_ITS_24) \n\n Stellen Sie sich vor, Sie arbeiten in einem kleinen Datenanalyse-Start-Up, das Unternehmensdaten \nnach Optimierungspotenzialen durchsucht. Sie greifen dabei insbesondere auf die Lohndaten und \nKrankheitstage je Mitarbeiter zu. Als Werkzeuge haben Sie M365 und Power BI Service im Einsatz.\n\n Im Rahmen einer Beauftragung treten Sie als Auftragsverarbeiter auf. Füllen Sie einen \n\nentsprechenden AVV aus: muster_adv_Version_13022018 (bayern.de) (Treffen Sie begründete Annahmen, wo \nnotwendig)\n\n Suchen Sie eine Vorlage für typische „TOMs“ und diskutieren Sie, was Ihnen als Start-Up \n\nwahrscheinlich leichter und was schwerer fällt.\n\n12.04.2024\n\n102\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_102.png');
INSERT INTO pdf_pages VALUES(415,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',103,replace('DSGVO – HAFTUNG UND SCHADENSERSATZ\n\n Jede Person, der wegen eines Verstoßes gegen diese Verordnung ein materieller oder immaterieller Schaden \n\nentstanden ist, hat Anspruch auf Schadenersatz gegen den Verantwortlichen oder gegen den \nAuftragsverarbeiter.\n\n Jeder an einer Verarbeitung beteiligte Verantwortliche haftet für den Schaden, der durch eine nicht dieser \n\nVerordnung entsprechende Verarbeitung verursacht wurde. Ein Auftragsverarbeiter haftet für den durch eine \nVerarbeitung verursachten Schaden nur dann, wenn er seinen speziell den Auftragsverarbeitern auferlegten \nPflichten aus dieser Verordnung nicht nachgekommen ist oder unter Nichtbeachtung der rechtmäßig erteilten \nAnweisungen des für die Datenverarbeitung Verantwortlichen oder gegen diese Anweisungen gehandelt hat.\n\n Der Verantwortliche oder der Auftragsverarbeiter wird von der Haftung gemäß Absatz 2 befreit, wenn er\n\nnachweist, dass er in keinerlei Hinsicht für den Umstand, durch den der Schaden eingetreten ist, \nverantwortlich ist. […]\n\n Hat ein Verantwortlicher oder Auftragsverarbeiter gemäß Absatz 4 vollständigen Schadenersatz für den erlittenen \n\nSchaden gezahlt, so ist dieser Verantwortliche oder Auftragsverarbeiter berechtigt, von den übrigen an \nderselben Verarbeitung beteiligten für die Datenverarbeitung Verantwortlichen oder Auftragsverarbeitern \nden Teil des Schadenersatzes zurückzufordern, der unter den in Absatz 2 festgelegten Bedingungen ihrem \nAnteil an der Verantwortung für den Schaden entspricht.\n\nQuelle: DSGVO Art. 82 1,2,3,5\n\n12.04.2024\n\n103\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_103.png');
INSERT INTO pdf_pages VALUES(416,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',104,replace('DSGVO – SCHADENSHÖHE\n\n Bei Verstößen gegen die folgenden Bestimmungen werden im Einklang mit Absatz 2 \n\nGeldbußen von bis zu 10 000 000 EUR oder im Fall eines Unternehmens von bis zu 2 % seines gesamten \nweltweit erzielten Jahresumsatzes des vorangegangenen Geschäftsjahrs verhängt, je nachdem, welcher der \nBeträge höher ist:\n\n die Pflichten der Verantwortlichen und der Auftragsverarbeiter gemäß den Artikeln 8, 11, 25 bis 39, 42 und 43;\n\n die Pflichten der Zertifizierungsstelle gemäß den Artikeln 42 und 43;\n\n die Pflichten der Überwachungsstelle gemäß Artikel 41 Absatz 4.\n\nQuelle: DSGVO Art. 83 Abs 4 \n\n12.04.2024\n\n104\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_104.png');
INSERT INTO pdf_pages VALUES(417,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',105,replace('DSGVO – SCHADENSHÖHE\n\n Bei Verstößen gegen die folgenden Bestimmungen werden im Einklang mit Absatz 2 \n\nGeldbußen von bis zu 20 000 000 EUR oder im Fall eines Unternehmens von bis zu 4 % seines gesamten \nweltweit erzielten Jahresumsatzes des vorangegangenen Geschäftsjahrs verhängt, je nachdem, welcher der \nBeträge höher ist:\n\n die Grundsätze für die Verarbeitung, einschließlich der Bedingungen für die Einwilligung, gemäß den Artikeln \n\n5, 6, 7 und 9;\n\n die Rechte der betroffenen Person gemäß den Artikeln 12 bis 22;\n\n die Übermittlung personenbezogener Daten an einen Empfänger in einem Drittland oder an eine internationale \n\nOrganisation gemäß den Artikeln 44 bis 49;\n\n alle Pflichten gemäß den Rechtsvorschriften der Mitgliedstaaten, die im Rahmen des Kapitels IX erlassen \n\nwurden;\n\n Nichtbefolgung einer Anweisung oder einer vorübergehenden oder endgültigen Beschränkung oder \nAussetzung der Datenübermittlung durch die Aufsichtsbehörde gemäß Artikel 58 Absatz 2 oder \nNichtgewährung des Zugangs unter Verstoß gegen Artikel 58 Absatz 1.\n\nQuelle: DSGVO Art. 83 Abs 5 \n\n12.04.2024\n\n105\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_105.png');
INSERT INTO pdf_pages VALUES(418,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',106,replace('DSGVO\n\nÜbungen\n\n(Password: DaSchu_ITS_24) \n\n Recherchieren Sie die höchsten drei Strafen gegen deutsche Unternehmen in den letzten drei Jahren.\n\n12.04.2024\n\n106\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_106.png');
INSERT INTO pdf_pages VALUES(419,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',107,replace('DSGVO – RISIKOBASIERTER ANSATZ\n\nDie DSGVO zielt an diversen Stellen auf einen risikobasierten Ansatz ab. Der Begriff des Risikos wird aber \nnicht explizit definiert. Die Erwägungsgründe 74-76 lassen aber eine abgeleitete Definition zu.\n\nE.g. Art. 32 Abs. 2:\n\n Bei der Beurteilung des angemessenen \n\nSchutzniveaus sind insbesondere die Risiken \nzu berücksichtigen, die mit der Verarbeitung \nverbunden sind, insbesondere durch – ob \nunbeabsichtigt oder unrechtmäßig –\nVernichtung, Verlust, Veränderung oder \nunbefugte Offenlegung von beziehungsweise \nunbefugten Zugang zu personenbezogenen \nDaten, die übermittelt, gespeichert oder auf \nandere Weise verarbeitet wurden.\n\nQuelle: DSGVO Art. 32 Abs. 2 und Konferenz der unabhängigen Datenschutzbehörden des Bundes und der Länder (Datenschutzkonferenz). Datenlizenz Deutschland – Namensnennung – Version 2.0 (www.govdata.de/dl-de/by-2-0).\n\n12.04.2024\n\n107\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_107.png');
INSERT INTO pdf_pages VALUES(420,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',108,replace('DSGVO – DATENSCHUTZ-FOLGENABSCHÄTZUNG\n\n Hat eine Form der Verarbeitung, insbesondere bei Verwendung neuer Technologien, aufgrund der Art, des \nUmfangs, der Umstände und der Zwecke der Verarbeitung voraussichtlich ein hohes Risiko für die Rechte \nund Freiheiten natürlicher Personen zur Folge, so führt der Verantwortliche vorab eine Abschätzung der \nFolgen der vorgesehenen Verarbeitungsvorgänge für den Schutz personenbezogener Daten durch. Für die \nUntersuchung mehrerer ähnlicher Verarbeitungsvorgänge mit ähnlich hohen Risiken kann eine einzige \nAbschätzung vorgenommen werden.\n\n Der Verantwortliche holt bei der Durchführung einer Datenschutz-Folgenabschätzung den Rat des \n\nDatenschutzbeauftragten, sofern ein solcher benannt wurde, ein.\n\n Eine Datenschutz-Folgenabschätzung gemäß Absatz 1 ist insbesondere in folgenden Fällen erforderlich:\n\n systematische und umfassende Bewertung persönlicher Aspekte natürlicher Personen, die sich auf \n\nautomatisierte Verarbeitung einschließlich Profiling gründet und die ihrerseits als Grundlage für \nEntscheidungen dient, die Rechtswirkung gegenüber natürlichen Personen entfalten oder diese in ähnlich \nerheblicher Weise beeinträchtigen;\n\n umfangreiche Verarbeitung besonderer Kategorien von personenbezogenen Daten gemäß Artikel 9 \nAbsatz 1 oder von personenbezogenen Daten über strafrechtliche Verurteilungen und Straftaten gemäß \nArtikel 10 oder\n\n systematische umfangreiche Überwachung öffentlich zugänglicher Bereiche.\n\nQuelle: DSGVO Art. 35 Abs. 1-3\n\n12.04.2024\n\n108\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_108.png');
INSERT INTO pdf_pages VALUES(421,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',109,replace('DSGVO – DATENSCHUTZ-FOLGENABSCHÄTZUNG\n\nQuelle: Konferenz der unabhängigen Datenschutzbehörden des Bundes und der Länder (Datenschutzkonferenz). Datenlizenz Deutschland – Kurzpapier Nr. 5 – Version 2.0 (www.govdata.de/dl-de/by-2-0).\n\n12.04.2024\n\n109\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_109.png');
INSERT INTO pdf_pages VALUES(422,'pdfs/DSBDSITS01_2_Grundlagen des Datenschutz.pdf',110,replace('DSGVO\n\nÜbungen\n\n(Password: DaSchu_ITS_24) \n\n Lesen Sie das Kurzpapier zur Datenschutz-Folgenabschätzung der DSK\n\nDSK_KPNr_5_Datenschutz-Folgenabschätzung_Lizenzvermerk (datenschutzkonferenz-online.de)\n\n Laden Sie das erläuternde Dokument zur Datenschutzfolgenabschätzung der Corona-Warn-App (CWA) \n\nherunter: 2022-10-10-CWA_DSFA-Bericht_Release_V2_25 (coronawarn.app)\n\n Sortieren Sie die Seitenzahlen des vorgenannten Dokuments zur CWA den einzelnen Schritten einer \n\nDatenschutzfolgeabschätzung zu. \n\n Wählen Sie das aus Ihrer Sicht gravierendste Risiko, welches im Dokument behandelt wird und \n\ndiskutieren Sie die abgeleiteten Maßnahmen. Sind diese Maßnahmen aus Ihrer Sicht sinnvoll und \nausreichend? Würden Ihnen weitere Maßnahmen einfallen?\n\n12.04.2024\n\n110\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_2_Grundlagen des Datenschutz_110.png');
