INSERT INTO pdf_pages VALUES(438,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',2,replace('LERNZIELE\n\n▪Aktivitäten zur Qualitätssicherung von Anforderungen\n\n▪Qualität von Architekturen \n\n▪Reifegrade für Softwareprozesse \n\n06.06.2024\n\n249\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_2.png');
INSERT INTO pdf_pages VALUES(439,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',3,replace('QUALITÄTSMANAGEMENT\n\n▪„Im Mittelpunkt des umfassenden Qualitätsmanagements stehen die:\n\n▪Kunden mit ihren Aufgaben, Prozessen, Erfordernissen und \nErwartungen und \n▪das Bemühen der Lieferanten, mit ihren Leistungen stets zu \nentsprechen.“ \n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 41, 43.\n\n06.06.2024\n\n250\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_3.png');
INSERT INTO pdf_pages VALUES(440,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',4,replace('QUALITÄTSMANAGEMENT\n\n▪„Qualitätsmanagement wird nach ISO 9000:2005 als ‚aufeinander \nabgestimmte Tätigkeiten zum leiten und lenken einer Organisation \nbezüglich Qualität‘ definiert.“ \n\n▪„Qualitätsmanagement dient dabei als Oberbegriff für eine Vielzahl \nvon Managementaufgaben und –aktivitäten zur Sicherstellung von \nQualität in Prozessen, Projekten und Produkten.“\n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 41, 43.\n\n06.06.2024\n\n251\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_4.png');
INSERT INTO pdf_pages VALUES(441,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',5,replace('DIN ISO 9001\n\n▪„Norm zum Thema Qualitätsmanagement“ \n\n▪„DIN ISO 9000-Reihe. Diese Normen sind auf eine Fülle von stark \nunterschiedlichen Unternehmen anwendbar, und können daher \nkeine spezifischen Techniken fordern.“ \n\n▪ISO 9000: Diese Norm definiert Grundlagen und Begriffe zu \nQualitätsmanagementsystemen.\n▪ISO 9001 legt die Mindestanforderungen an ein \nQualitätsmanagementsystem (QM-System)\n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 385\n\n06.06.2024\n\n252\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_5.png');
INSERT INTO pdf_pages VALUES(442,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',6,replace('DIN ISO 9001\n\n▪„ISO 9001 ist im Wesentlichen frei von konkreten technischen \nInhalten. Sie fordert die Durchführung bestimmter Tätigkeiten, z. B. \ndie Beurteilung von Unterlieferanten oder die Existenz von \nEingangsprüfungen. Sie legt nicht fest, wie diese Tätigkeiten zu \nrealisieren sind. Im Wesentlichen definiert die DIN EN ISO 9001 einen \norganisatorischen Rahmen für das Qualitätsmanagement.“ \n\n▪ISO 9004 → Leitfaden für die Implementierung von QM bzw. TQM\n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 385\n\n06.06.2024\n\n253\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_6.png');
INSERT INTO pdf_pages VALUES(443,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',7,replace('DIN 9126\n\n▪Bekannte ISO Norm zur Bewertung von Softwarequalität:\n▪ Funktionalität: Erfüllt die Software die geforderten Funktionen?\n▪ Zuverlässigkeit: Kann sie ein bestimmtes Leistungsniveau über Zeit \n\naufrechterhalten?\n\n▪ Benutzbarkeit: Wie aufwändig ist der Einsatz für den Benutzer?\n▪ Effizienz: Wie ist das Verhältnis von Leistung zu eingesetzten Betriebsmitteln?\n▪ Änderbarkeit/Wartbarkeit: Welchen Aufwand erfordern Änderungen an der \n\nSoftware?\n\n▪ Übertragbarkeit: Wie leicht lässt sich die Software in eine andere Umgebung \n\nübertragen?\n\n06.06.2024\n\n254\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_7.png');
INSERT INTO pdf_pages VALUES(444,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',8,replace('ISO 25010\n\n▪Die ISO 9126 ist in der neueren ISO/IEC 25000-Normenreihe aufgegangen.\n▪Erweiterungen und Neuerungen in ISO 25000/2500n-2504n\n▪ Einführung eines allgemeinen Referenzmodells\n▪ Einführung von Leitfäden für jede Teilnorm\n▪ Einbeziehung der Systemproduktqualität neben der Softwarequalität\n▪ Einführung eines Datenqualitätsmodells\n▪ Einführung von "Quality Measure Elements" in der Qualitätsmessungs-Teilnorm\n▪ Einführung einer Teilnorm für Qualitätsanforderungen\n▪ Überarbeitung und Einbindung der Evaluierungsprozesse\n▪ Praxisbeispiele zur Anwendung der Normen\n▪ Harmonisierung mit ISO/IEC 15939 (Software Measurement Process)\n\nhttps://webstore.iec.ch/preview/info_isoiec25000%7Bed2.0%7Den.pdf\n\n06.06.2024\n\n255\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_8.png');
INSERT INTO pdf_pages VALUES(445,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',9,replace('ISO 25010\n\n▪Die ISO 9126 ist in der neueren ISO/IEC 25000-Normenreihe aufgegangen.\n\n▪Neue Qualitätsmerkmale:\n▪Sicherheit und Kompatibilität als neue Hauptmerkmale hinzugefügt\n▪Funktionalität umbenannt in "funktionale Eignung"\n▪Effizienz umbenannt in "Leistungseffizienz"\n▪Gebrauchstauglichkeit erweitert um "Bedienbarkeit" und "Zugänglichkeit"\n\nhttps://webstore.iec.ch/preview/info_isoiec25000%7Bed2.0%7Den.pdf\n\n06.06.2024\n\n256\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_9.png');
INSERT INTO pdf_pages VALUES(446,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',10,replace('ANSATZ DES QUALITÄTSMANAGEMENTS\n\n▪„[Heute] geht man zunehmend dazu über, die Qualität des Software \nProduktes indirekt über die Qualität des Entwicklungsprozesses zu \nverbessern. Prüfungen und Tests sind dann Teilaufgaben dieses \nProzesses und als solche nur Teil des gesamten \nQualitätsmanagements. Dieser Ansatz basiert auf der Hypothese, \ndass man durch einen qualitativ hochwertigen, reifen Prozess auch \nqualitativ hochwertige Produkte herstellen kann.“ \n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 27\n\n06.06.2024\n\n257\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_10.png');
INSERT INTO pdf_pages VALUES(447,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',11,replace('ROLLEN IM QUALITÄTSMANAGEMENT\n\nIn der Praxis kann eine Aufgabenverteilung wie folgt organisiert sein:\n\n▪Softwareentwickler/innen: Zuständig für den Aufbau der Softwarelösung; Sicherstellung \nder Qualität der Komponente, für die sie verantwortlich sind \n▪Software-Tester: Bewertet die Qualität der Software und verringert Risiko\n▪Projektleiter/in / Manager/in:  Verwaltet Softwareprojekte & leitet Softwareabteilungen\n\nReflexion für euer Projekt (Blitzlicht): Wer übernimmt bei euch das \nQualitätsmanagement?\n\n06.06.2024\n\n258\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_11.png');
INSERT INTO pdf_pages VALUES(448,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',12,replace('KOSTEN VON QUALITÄT\n\nBeachten: Es besteht eine Verbindung zwischen Qualitätsgrad (Wie \nhoch ist meine Qualität) und den Kosten, die für die Erstellung oder \nErbringung notwendig sind. \n\n▪Zur Erbringung von Qualität werden Prüf- und Vorbeugekosten in \nKauf genommen. \n\n▪Für Abweichungen in der Qualität werden Fehlerkosten in Kauf \ngenommen.\n\n06.06.2024\n\n259\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_12.png');
INSERT INTO pdf_pages VALUES(449,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',13,replace('BEDEUTUNG VON QUALITÄTSSICHERUNG\n\n▪„Im Schadensfall ergibt sich z. B. aus dem Produkthaftungsgesetz die \nVerpflichtung Ersatz eines Schadens, der durch ein fehlerhaftes \nProdukt entstanden ist.“ \n\n▪„Ein Haftungsausschluss setzt voraus, dass der Fehler zum Zeitpunkt \ndes in Verkehr bringen noch nicht vorlag, oder dass er nach dem \nStand von Wissenschaft und Technik nicht erkennbar war, Die \nBeweislast für diesen Sachverhalt liegt im wesentlichen beim \nHersteller.“ \n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 382\n\n06.06.2024\n\n260\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_13.png');
INSERT INTO pdf_pages VALUES(450,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',14,replace('STANDARDS\n\n▪Softwarefehler können Schadensersatzforderungen nach sich ziehen\n\n▪„Besonders Firmen, die sicherheitskritische Systeme entwickeln, \nmüssen im Schadensfall erhebliche juristische Konsequenzen \nfürchten. “ \n\n▪Der Softwarehersteller trägt die Beweislast, nachzuweisen, dass sein \nProdukt fehlerfrei geliefert wurde oder ein Fehler nicht hätte erkannt \nwerden können. \n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 382\n\n06.06.2024\n\n261\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_14.png');
INSERT INTO pdf_pages VALUES(451,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',15,replace('STANDARDS\n\n▪„Durch Einhaltung der jeweils relevanten Normen kann ein Hersteller \nsicherstellen, dass der Stand der Technik erreicht ist, und er damit \nseine Sorgfaltspflicht erfüllt hat.“\n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 382\n\n06.06.2024\n\n262\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_15.png');
INSERT INTO pdf_pages VALUES(452,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',16,replace('GRUNDLAGEN ZUM PRÜFEN VON ANFORDERUNGEN\n\nKernaktivität: \n▪Requirements Engineering (Anforderungsmanagement), auch \nentwicklungsbegleitend\n▪fachliche und technische Korrektheit\n▪Abstimmung mit allen relevanten Stakeholdern\n▪Achtung: Für Anforderungen gilt → vollständiges Testen ist nicht \nmöglich\n\n06.06.2024\n\n263\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_16.png');
INSERT INTO pdf_pages VALUES(453,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',17,replace('GROUPDISCUSSION\n\n▪Was hat Produktqualität mit Prozessqualität zu tun (Insbesondere bei Software)?\n\n▪Was sind eure Erkenntnisse aus den Unternehmen?\n▪Was sind eure Erkenntnisse aus euren Projekten?\n\n20 Minuten Mixed-Groups → Danach Ergebnisübersicht der einzelnen Gruppen \n(Findings) \n\n06.06.2024\n\n264\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_17.png');
INSERT INTO pdf_pages VALUES(454,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',18,replace('REMINDER: QUALITÄTSSICHERUNG VON ANFORDERUNGEN\n\nPrüfkriterien festlegen\n\nPrüfprinzipien und Prüftechniken auswählen\n\nPrüfungen durchführen und Ergebnisse dokumentieren\n\nAbstimmen der Anforderungen / Konfliktmanagement\n\n06.06.2024\n\n265\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_18.png');
INSERT INTO pdf_pages VALUES(455,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',19,replace('REMINDER: QUALITÄTSSICHERUNG VON ANFORDERUNGEN\n\nPrüfkriterien für Anforderungen\n\nPrüfprinzipien\n\nPrüfen des Inhalts\n\nBeteiligung der richtigen Stakeholder\n\nPrüfen der Dokumentation\n\nTrennung von Fehlersuche und Fehlerkorrektur\n\nPrüfkriterien zur Abgestimmtheit\n\nPrüfung aus unterschiedlicher Sicht\n\nGeeigneter Wechsel von \nEntwicklungsartefakten\n\nKonstruktion von Entwicklungsartefakten\n\nWiederholte Prüfung\n\n06.06.2024\n\n266\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_19.png');
INSERT INTO pdf_pages VALUES(456,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',20,replace('QUALITÄTSSICHERUNG VON ARCHITEKTUREN\n\nWas versteht man noch einmal unter Softwarearchitektur? \n\n„Eine Softwarearchitektur ist einer der Architekturtypen in der Informatik und \nbeschreibt die grundlegenden Komponenten und deren Zusammenspiel innerhalb \neines Softwaresystems.“\n\nWikipedia-Autoren. (2003, 6. Dezember). Softwarearchitektur. https://de.wikipedia.org/wiki/Softwarearchitektur\n\n06.06.2024\n\n267\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_20.png');
INSERT INTO pdf_pages VALUES(457,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',21,replace('QUALITÄTSSICHERUNG VON ARCHITEKTUREN\n\nPrüfung, der Eignung der Architektur zur Erfüllung der Anforderungen \n\nEx ante oder ex Post?\n\n▪Ex Ante (vor der Implementierung): Szenariobasiertes Vorgehen: Bewertung \nanhand von definierten Anwendungsszenarien => Architecture Trade off Analysis \nMethode (ATAM)\n\n▪Ex Post (nach der Implementierung): Prüfen der Implementierung gegen \nArchitekturbeschreibung (Architecture Compliance Checking)\n\n06.06.2024\n\n268\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_21.png');
INSERT INTO pdf_pages VALUES(458,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',22,replace('QUALITÄTSSICHERUNG VON ARCHITEKTUREN: ATAM\n\n▪Szenariobasiertes Vorgehen, d.h. Bewertung anhand von definierten \nAnwendungsszenarien (Ex ante)\n▪Erkennt Trends\n▪Früh anwendbar\n\n▪“When evaluating an architecture using the ATAM, the goal is to understand the \nconsequences of architectural decisions with respect to the quality attribute \nrequirements of the system.” \n(inkl. des Trade-Offs der Ziele Architekturentscheidungen) \n\n06.06.2024\n\n269\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_22.png');
INSERT INTO pdf_pages VALUES(459,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',23,replace('QUALITÄTSSICHERUNG VON ARCHITEKTUREN: ATAM\n\nArchitecture Tradeoff \nAnalysis Methode (ATAM)\n\nVorbereitung und \nPräsentation (Phase 1)\n\nUntersuchung und \nAnalyse (Phase 2)\n\nTest (Phase 3)\n\nBerichterstattung (Phase \n4)\n\nSchritt 1: ATAM \nPräsentation\nSchritt 2: Business Driver\nSchritt 3: Basisarchitektur\n\nSchritt 4: Alternative \nArchitekturvarianten\nSchritt 5: Utility Tree\nSchritt 6: Grobanalyse\n\nSchritt 7: Finden und \nPriorisieren von \nSzenarien\nSchritt 8: Detailanalyse\n\nSchritt 9: Präsentation \nder Ergebnisse\n\nKazman, R., Klein, M., Clementes, P. (2000). ATAM: Method for Architecture Evaluation, CMU/SEI.\n\n06.06.2024\n\n270\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_23.png');
INSERT INTO pdf_pages VALUES(460,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',24,replace('QUALITÄTSSICHERUNG: PROZESSANALYSE\n\n▪Durch Stakeholderbefragung mit Hilfe von Prozessmerkmalen \n\n▪Reifegradmodelle z.B. Capability Maturity Model Integration \n\n06.06.2024\n\n271\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_24.png');
INSERT INTO pdf_pages VALUES(461,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',25,replace('QUALITÄTSATTRIBUTE FÜR SOFTWAREPROZESSE\n\n▪Verständlichkeit \n▪Standardisierung \n▪Sichtbarkeit \n▪Messbarkeit \n▪Unterstützbarkeit \n▪Akzeptanz \n▪Zuverlässigkeit \n▪Stabilität \n▪Wartungsfreundlichkeit \n▪Schnelligkeit \n\n06.06.2024\n\n272\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_25.png');
INSERT INTO pdf_pages VALUES(462,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',26,replace('QUALITÄTSSICHERUNG: CAPABILITY MATURITY MODEL \nINTEGRATION\n\n▪Rahmenwerk für Prozessverbesserungen\n▪Stufenmodell\n▪22 Prozessgebiete aus dem  Software \nEngineering\n▪Ziele, die spezifisch für jedes Prozessgebiet  \nformuliert werden; sowie\n▪Vorgehensweisen, mit denen die Ziele \nerreicht werden können.\n\nGrad 1: Initial\n\nGrad 2: Geführt\n\nGrad 3: Definiert\n\nGrad 4: Quantitativ geführt\n\nGrad 5: Prozessoptimierung\n\n06.06.2024\n\n273\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_26.png');
INSERT INTO pdf_pages VALUES(463,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',27,replace('QUALITÄTSSICHERUNG: CAPABILITY MATURITY MODEL \nINTEGRATION\n\nStufe 1 –Initial: \n\n▪Es gibt keine Prozessdefinition, die \nAktivitäten finden ad hoc statt bzw. \nwerden chaotisch durchgeführt. Der \nProzess ist in der Regel nicht wiederholbar.\n\nGrad 1: Initial\n\nGrad 2: Geführt\n\nGrad 3: Definiert\n\nGrad 4: Quantitativ geführt\n\nGrad 5: Prozessoptimierung\n\n06.06.2024\n\n274\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_27.png');
INSERT INTO pdf_pages VALUES(464,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',28,replace('QUALITÄTSSICHERUNG: CAPABILITY MATURITY MODEL \nINTEGRATION\n\nStufe 2 –Geführt: \n\n▪Der Prozess ist wiederholbar. Umfang und \nErgebnis der zu erledigenden Aktivitäten \nist den Teammitgliedern bekannt. Es gibt \naber keinen detaillierten Prozess, der \nAktivitäten, Rollen, Ergebnisse und deren \nAbhängigkeiten explizit definiert.\n\n06.06.2024\n\n275\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\nGrad 1: Initial\n\nGrad 2: Geführt\n\nGrad 3: Definiert\n\nGrad 4: Quantitativ geführt\n\nGrad 5: Prozessoptimierung\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_28.png');
INSERT INTO pdf_pages VALUES(465,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',29,replace('QUALITÄTSSICHERUNG: CAPABILITY MATURITY MODEL \nINTEGRATION\n\nStufe 3 –Definiert: \n\n▪Es gibt einen definierten Prozess, in dem \nAktivitäten, Rollen und Ergebnisse \nverbindlich dokumentiert sind. Der \nProzess enthält konkrete Methoden und \nVorgehensweisen zu den einzelnen \nSoftware Engineering Aktivitäten.\n\n06.06.2024\n\n276\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\nGrad 1: Initial\n\nGrad 2: Geführt\n\nGrad 3: Definiert\n\nGrad 4: Quantitativ geführt\n\nGrad 5: Prozessoptimierung\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_29.png');
INSERT INTO pdf_pages VALUES(466,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',30,replace('QUALITÄTSSICHERUNG: CAPABILITY MATURITY MODEL \nINTEGRATION\n\nStufe 4 –Quantitativ geführt: \n\n▪Die Prozessqualität kann über Messung \nquantitativer Prozesseigenschaften \nfestgestellt und gesteuert werden. Über \nerhobene Kennzahlen zu Aktivitäten der \nProzesse sowie zu den erzeugten \nErgebnissen können Havarien identifiziert \nund entsprechend gegengesteuert \nwerden.\n\n06.06.2024\n\n277\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\nGrad 1: Initial\n\nGrad 2: Geführt\n\nGrad 3: Definiert\n\nGrad 4: Quantitativ geführt\n\nGrad 5: Prozessoptimierung\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_30.png');
INSERT INTO pdf_pages VALUES(467,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',31,replace('QUALITÄTSSICHERUNG: CAPABILITY MATURITY MODEL \nINTEGRATION\n\nStufe 5 –Prozessoptimierung: \n\n▪Prozess- und Produktmessungen werden \nkontinuierlich erhoben und zur \nProzessverbesserung eingesetzt. Je nach \nMesswert kann der Prozess auf den \naktuellen Bedarf hin angepasst werden.\n\nGrad 1: Initial\n\nGrad 2: Geführt\n\nGrad 3: Definiert\n\nGrad 4: Quantitativ geführt\n\nGrad 5: Prozessoptimierung\n\n06.06.2024\n\n278\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_31.png');
INSERT INTO pdf_pages VALUES(468,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',32,replace('PROZESSVERBESSERUNGEN\n\nVerbesserungen \nerkennen\n\nVerbesserungen \npriorisieren\n\nProzessänderun-\ngen einführen\n\nStakeholder \nschulen\n\nProzessänderun-\ngen anpassen\n\nProzessmodell\n\nProzess-\nänderungsplan\n\nSchulungsplan\n\nReaktion auf die \nVerbesserungen\n\nÜberarbeitetes \nProzessmodell\n\n06.06.2024\n\n279\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_32.png');
INSERT INTO pdf_pages VALUES(469,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',33,replace('SHORTDISCUSSION\n\nGruppendiskurs/Reflektion: \n▪Warum ist es nicht möglich Anforderungen komplett zu testen?\n▪Welches sind vernünftige Größen von Software- oder Architekturelementen zum \ntesten?\n\n06.06.2024\n\n280\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_33.png');
INSERT INTO pdf_pages VALUES(470,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',34,replace('REMINDER: ARTEN DER QUALITÄTSSICHERUNG IN DER \nSOFTWAREENTWICKLUNG\n\nModultest:\n\n„Das Ziel des Modultests ist die Überprüfung \neinzelner Komponenten (Module) gegen \nihre Spezifikation.“ \n\nModultest\n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 370-371\n\n06.06.2024\n\n281\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\nIntegrationstest\n\nSystemtest\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_34.png');
INSERT INTO pdf_pages VALUES(471,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',35,replace('REMINDER: ARTEN DER QUALITÄTSSICHERUNG IN DER \nSOFTWAREENTWICKLUNG\n\nIntegrationstest:\n\n„Das Ziel des Integrationstests ist die \nÜberprüfung des Interagierens \nverschiedener Module über Schnittstellen. \nIn dieser Phase sind der innere Aufbau von \nModulen und die von der derzeit \nbetrachteten Schnittstelle weit entfernten \nSchnittstellen nicht relevant.“ \n\nModultest\n\nIntegrationstest\n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 370-371\n\n06.06.2024\n\n282\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\nSystemtest\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_35.png');
INSERT INTO pdf_pages VALUES(472,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',36,replace('REMINDER: ARTEN DER QUALITÄTSSICHERUNG IN DER \nSOFTWAREENTWICKLUNG\n\nSystemtest:\n\n„Die Ziele des Systemtests sind die Prüfung \nder Funktion, Leistung und Qualität des \nfertig integrierten Systems gegen die \nAnforderungsdokumente. Im Systemtest \nwerden die inneren Strukturen von \nSystemen nicht betrachtet.“ \n\nModultest\n\nIntegrationstest\n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 370-371\n\n06.06.2024\n\n283\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\nSystemtest\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_36.png');
INSERT INTO pdf_pages VALUES(473,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7.pdf',37,replace('BONUS: CODE-REVIEW-ÜBUNG\n\npublic static void main(String[] args) {\nScanner scan = new Scanner(System.in);\nList<Product> products;\nint input = 0; int i = 0;\ndo {\n\ni ++;\nSystem.out.println("Was möchten Sie tun? 1 = Produkt anlegen, \n2 = kommentieren, 3 = beenden");\ninput = scan.nextInt();\nif(input == 1) {\n\nSystem.out.println("Name: ");\nString n = scan.next();\nproducts.add(new Product(i, n, ""));\n\n}\nif(input == 2) {\n\nSystem.out.println("Welches Produkt? (ID)");\nlong id = scan.nextLong();\nProduct p = getProduct(products, id);\nSystem.out.println("Kommentar: ");\nString c = scan.next();\np.comments.add(new Comment(c));\n\n}\n\n}while(input != 3);\n\n}\n\n06.06.2024\n\n284\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\nComment.java\npublic class Comment {\n\npublic Comment(String c) {\ncomment = c;\n}\npublic long ProduktID;\npublic long commentID;\npublic String comment;\npublic String autor;\n\n}\n\nProduct.java\npublic class Product {\n  private long id;\n  private String name;\n  private String description;\n  public List<Comment> comments;\n\npublic Product(long pId, String pName, String description) {\n\n    setId(pId);\n    name = pName;\n    description = description;\n  }\n\npublic void setId(long id) {\n\n    this.id = id;\n  }\n}\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24 Lek 7_37.png');
