INSERT INTO pdf_pages VALUES(594,'pdfs/slidesgo-portfolio-presentation-20240606080919GGVn.pdf',2,replace('VISUALISIERUNG DER\nPROJEKTE\n\nDie folgende Graﬁk zeigt die\nPortfoliodarstellung der sechs Projekte\nbasierend auf ihrer\nRealisierungswahrscheinlichkeit (X-Achse)\nund dem Return on Investment (RoI) (Y-\nAchse). Die Größe der Kreise repräsentiert den\nAufwand.\n\n','\n',char(10)),'imgs/slidesgo-portfolio-presentation-20240606080919GGVn_2.png');
INSERT INTO pdf_pages VALUES(595,'pdfs/slidesgo-portfolio-presentation-20240606080919GGVn.pdf',3,replace('PRIORISIERUNG DER\nPROJEKTE\n\nDie Priorisierung der Projekte erfolgt durch die\nBerechnung des "gewichteten" RoI, wobei dieser wie folgt\nberechnet wird: \nGewichteter RoI = RoI×Realisierungswahrscheinlichkeit\nGewichteter\nRoI=RoI×RealisierungswahrscheinlichkeitGewichteter RoI\nund Reihenfolge der Projekte:\nProjekt E: 37.8 (Aufwand: 1200 T€)\nProjekt A: 17.5 (Aufwand: 2000 T€)\n\Projekt D: 11.9 (Aufwand: 300 T€)\nProjekt B: 1.8 (Aufwand: 500 T€)\nProjekt F: 0.9 (Aufwand: 200 T€)\nProjekt C: 0.0 (Aufwand: 100 T€)\n\n','\n',char(10)),'imgs/slidesgo-portfolio-presentation-20240606080919GGVn_3.png');
INSERT INTO pdf_pages VALUES(596,'pdfs/slidesgo-portfolio-presentation-20240606080919GGVn.pdf',4,replace('PROBLEME UND LÖSUNGEN\n\nProblem mit Projekt C\n\nProblem: Projekt C hat einen RoI von 0%, was\nbedeutet, dass es keinen direkten Nutzen\nbringt.\n\nLösung\n\nProjekt  C  sollte  nur  durchgeführt  werden,\nwenn  es  strategisch  notwendig \n(z.B.\nSicherheitsanforderungen erfüllen) oder wenn\nes  indirekte Vorteile  gibt,  die  nicht  in  den  RoI\neingerechnet sind.\n\nist \n\n','\n',char(10)),'imgs/slidesgo-portfolio-presentation-20240606080919GGVn_4.png');
INSERT INTO pdf_pages VALUES(597,'pdfs/slidesgo-portfolio-presentation-20240606080919GGVn.pdf',5,replace('EMPFEHLUNGEN FÜR PROJEKTE\n\nNicht durchzuführen: Projekte mit dem geringsten gewichteten RoI, also\nProjekt C und eventuell Projekt F, je nach Verfügbarkeit und strategischen\nNotwendigkeiten.\n\n','\n',char(10)),'imgs/slidesgo-portfolio-presentation-20240606080919GGVn_5.png');
INSERT INTO pdf_pages VALUES(598,'pdfs/slidesgo-portfolio-presentation-20240606080919GGVn.pdf',6,replace('DURCHFÜHREN\n\nProjekte E, A und D, da diese den höchsten\ngewichteten RoI haben und daher den\ngrößten erwarteten Nutzen bieten.\n\n','\n',char(10)),'imgs/slidesgo-portfolio-presentation-20240606080919GGVn_6.png');
INSERT INTO pdf_pages VALUES(599,'pdfs/slidesgo-portfolio-presentation-20240606080919GGVn.pdf',7,replace('FAZIT\n\nBasierend auf der gewichteten RoI-Analyse und unter\nBerücksichtigung des IT-Budgets von 4000 T€, sollten die Projekte E,\nA und D priorisiert werden. Projekt C sollte nur in Erwägung gezogen\nwerden, wenn es andere wichtige, nicht-monetäre Vorteile bietet.\n\n','\n',char(10)),'imgs/slidesgo-portfolio-presentation-20240606080919GGVn_7.png');
