INSERT INTO pdf_pages VALUES(608,'pdfs/DSBDSITS01_1_Begriffsbestimmungen.pdf',6,replace('EINFÜHRUNG\n\n Informationstechnik (IT) ist heute allgegenwärtig: \n\n im Haus, im Auto, am Arbeitsplatz\n Insbesondere im Privaten\n\n Für Verbände, Behörden und Unternehmen ist das Thema „Digitalisierung“ (in einem globalen Umfeld) ein Muss \n\n Industrie 4.0 / Wirtschaft 4.0 / Interoperabilität \n\n Zunahme des Grades der Digitalisierung\n\n angewiesen auf Informations- und Kommunikationssysteme, Automatisierung und Vernetzung\n\n Gleichzeitig…\n\n soziale Netzwerke geben Mitgliederdaten unerlaubt an Werbetreibende weiter\n Hackern gelingt der Zugriff auf Millionen von Kundendaten\n Krankenhäuser entsorgen Patientendaten ungeschreddert im Altpapiercontainern\n Edward Snowden: „Geheimdienste haben ungehindert Zugriff auf unsere Smartphones, Computer und E-Mails, fordern \n\neigene Hintertüren für verschlüsselte Geräte“\n\n04.04.2024\n\n6\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_1_Begriffsbestimmungen_6.png');
INSERT INTO pdf_pages VALUES(609,'pdfs/DSBDSITS01_1_Begriffsbestimmungen.pdf',7,replace('EINFÜHRUNG\n\n Immer wieder gelangen Informationen \n\nüber einen unerwünschten oder \nunerlaubten Zugriff auf persönliche \noder geschäftliche Daten an die \nÖffentlichkeit\n\n Nach allem, was bekannt ist, ist dies \nnur die metaphorische  „Spitze des \nEisberges“\n\n Kriminelle\n Terroristen\n Nationale Kräfte / Militär\n Geheimdienste\n Fahrlässigkeit /Zufall\n …\n\n04.04.2024\n\n7\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_1_Begriffsbestimmungen_7.png');
