INSERT INTO pdf_pages VALUES(612,'pdfs/DSBDSITS01_1_Begriffsbestimmungen.pdf',10,replace('INFORMATIONSTECHNIK (IT)\nDefinition\n\n04.04.2024\n\n10\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n\n\n\n\n\n\nDer Begriff Informationstechnik (IT) bezeichnet einen \nkonkreten Einsatz von elektronischen Geräten für die \nErhebung und Verwendung von Daten durch Menschen \noder Maschinen.\n\nDaten sind in der Informationstechnik formalisierte \nDarstellungen von Sachverhalten, Konzepten, \nVorstellungen und Anweisungen, die für die \nÜbertragung, Speicherung und die Verarbeitung durch \nMenschen oder Maschinen geeignet sind.\n\nSignale sind Darstellungen von Daten durch \ncharakteristische, räumliche und/oder zeitliche \nVeränderungen der Werte physikalischer Größen.\n\n','\n',char(10)),'imgs/DSBDSITS01_1_Begriffsbestimmungen_10.png');
INSERT INTO pdf_pages VALUES(613,'pdfs/DSBDSITS01_1_Begriffsbestimmungen.pdf',11,replace('INFORMATIONSTECHNIK (IT)\nZusammenspiel\n\nInformationen: Gegenstände unseres \nDenkens (z.B. Fakten, Konzepte, \nVorstellungen, Anweisungen, …)\n\nDaten: Formalisierte Darstellung von \nInformationen/Signalen (z.B. Glyphe, \nSymbol, Zeichen, Code, …)\n\nSignale: Physische Darstellungen von \nDaten (z.B. Spannung, Strom, Licht, \nSchall, …)\n\n04.04.2024\n\n11\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_1_Begriffsbestimmungen_11.png');
INSERT INTO pdf_pages VALUES(614,'pdfs/DSBDSITS01_1_Begriffsbestimmungen.pdf',12,replace('IT-INFRASTRUKTUR\n\nÜbung: Daten vs. Informationen\n\nErarbeiten Sie in der Gruppe mindestens 3 Beispiele anhand deren Sie \nerläutern können, wie sich Informationen und Daten zueinander verhalten.\n\n Welche Informationen gibt es in Ihrem Beispiel? \n Welche Daten gibt es in Ihrem Beispiel?\n Wie verhalten sich die Informationen und Daten? \n\n04.04.2024\n\n12\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n(Password: DaSchu_ITS_24) \n\n','\n',char(10)),'imgs/DSBDSITS01_1_Begriffsbestimmungen_12.png');
INSERT INTO pdf_pages VALUES(615,'pdfs/DSBDSITS01_1_Begriffsbestimmungen.pdf',13,replace('IT-VERBÜNDE UND INFRASTRUKTUR\nBegriff\n\nDas Bundesamt für Sicherheit in der Informationstechnik (BSI) bezeichnet eine konkrete Ausprägung der \nInformationstechnik als einen „IT-Verbund“ bzw. „Informationsverbund“.\n\nalle technischen Komponenten \n„IT-Infrastruktur“\nHardware\nSoftware\nBauliche Einrichtungen\n\n\n\n\n\nalle organisatorischen \nKomponenten\n\n\n\n\n\nInstitution\nStruktur\nPersonal\n\n04.04.2024\n\n13\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_1_Begriffsbestimmungen_13.png');
