INSERT INTO pdf_pages VALUES(617,'pdfs/DSBDSITS01_1_Begriffsbestimmungen.pdf',15,replace('IT-INFRASTRUKTUR\nBeispiele für Organisatorische Komponenten\n\nEU (Verordnung)\n\nBund, Länder, … \n\nInstitution\n\n DSGVO\n\nBeispiele\n\nISO, DIN …\n\nGeschäftsführung\n\nBetriebsrat\n\nDatenschutzbeauftrag.\n\nStruktur\n\nMitarbeitende\n\nPersonal\n\n04.04.2024\n\n15\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n Explizite Rollen in der Organisation und \n\nderen Einfluss auf den Verbund\n\n Anzahl der Mitarbeitenden\n Qualifikation der Mitarbeitenden\n Planung, Wartung und Betrieb\n\n','\n',char(10)),'imgs/DSBDSITS01_1_Begriffsbestimmungen_15.png');
INSERT INTO pdf_pages VALUES(618,'pdfs/DSBDSITS01_1_Begriffsbestimmungen.pdf',16,replace('IT-INFRASTRUKTUR \n\nÜbung: Technischen Komponenten\n\nWelche technischen und organisatorischen Komponenten \ngibt es bei Ihrem Praxispartner? Und wie viele? \nBeschränken Sie sich ggf. auf Ihre Abteilung oder Ihr Team.\n\n Listen Sie diese auf\n Kategorisieren Sie diese nach den vorher benannten Kategorien\n Diskutieren Sie in Ihrer Gruppe die Ergebnisse aller Gruppenteilnehmer\n\n04.04.2024\n\n16\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n(Password: DaSchu_ITS_24) \n\n','\n',char(10)),'imgs/DSBDSITS01_1_Begriffsbestimmungen_16.png');
