INSERT INTO pdf_pages VALUES(622,'pdfs/DSBDSITS01_1_Begriffsbestimmungen.pdf',20,replace('SCHUTZVERBESSERUNG DURCH GEMEINSCHAFTEN \n\nIndividualsphäre\n\n• Persönliches Leben der frei \n\nbeobachtbar stattfindet und \nfür jeden zugänglich ist\n\nPrivatsphäre\n\nIntimsphäre\n\n• Gesamter häuslicher Bereich \nund nahestehende Personen\n\n• Bsp.: Interaktion mit \nLebensgefährten\n\n• Absolut durch Gemeinschaft \n\ngeschützt werden\n• Bsp.:: Sexualität, \n\nKrankheiten, Gefühle, \nGedanken\n\n04.04.2024\n\n20\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_1_Begriffsbestimmungen_20.png');
