INSERT INTO pdf_pages VALUES(650,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',12,replace('MARKETINGZIELE & -STRATEGIEN\n\nSITUATIONSANALYSE\n\nSWOT-Analyse\nPestel-Struktur\n5 Forces-Modell\nUSP\n\nMARKETINGZIELE\n\nZielhierarchien\nArten von Marketingzielen\n\nMARKETINGSTRATEGIE\n\nMarktfeldstrategien: Produkt-Markt-Matrix\nMarktstimulierung (nach Porter)\n\nQuelle: Vgl. Bruhn, M. (2022): Marketing - Grundlagen für Studium und Praxis, 15. Auflage\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n9\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_12.png');
INSERT INTO pdf_pages VALUES(651,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',13,replace('MARKETINGZIELE & -STRATEGIEN\n\nSITUATIONSANALYSE\n\nSWOT-Analyse\nPestel-Struktur\n5 Forces-Modell\nUSP\n\nMARKETINGZIELE\n\nZielhierarchien\nArten von Marketingzielen\n\nMARKETINGSTRATEGIE\n\nMarktfeldstrategien: Produkt-Markt-Matrix\nMarktstimulierung (nach Porter)\n\nQuelle: Vgl. Bruhn, M. (2022): Marketing - Grundlagen für Studium und Praxis, 15. Auflage\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n9\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_13.png');
INSERT INTO pdf_pages VALUES(652,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',14,replace('SITUATIONSANALYSE ALS BASIS\nAUFBAU EINER MARKETINGKONZEPTION\n\nQuelle: Vgl. Bruhn, M. (2022): Marketing - Grundlagen für Studium und Praxis, 15. Auflage\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n1 0\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_14.png');
INSERT INTO pdf_pages VALUES(653,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',15,replace('SITUATIONSANALYSE ALS BASIS\n\nDie Situationsanalyse ist der Ausgangspunkt jedes systematischen Vorgehens im Marketing.\n\nSie erfasst eine spezielle Situation, in der sich ein Unternehmen befindet sowie die sich\ndaraus ergebende marktorientierte Problemstellung.\n\nZur Entwicklung und Fundierung strategischer Entscheidungen werden Analyseinstrumente\neingesetzt, die Hinweise auf die erforderlichen Schwerpunkte der Marktbearbeitung\ngeben.\n\nAls Basis für die Erarbeitung von Marketingzielen und -strategien dient eine umfassende\ninterne sowie externe Analyse eines Unternehmens.\n\nQuelle: Vgl. Bruhn, M. (2022): Marketing - Grundlagen für Studium und Praxis, 15. Auflage\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n1 1\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_15.png');
INSERT INTO pdf_pages VALUES(654,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',16,replace('BESTANDTEILE EINER ANALYSE\nErfolgreiche Unternehmen schauen vor der Festlegung von Zielen sorgfältig sowohl nach innen als auch\nnach außen.\n\nUnternehmensanalyse (intern)\n\nProdukte (Produktentwicklung, Portfolioanalyse)\nWorkflow (Potentiale, Ressourcen, Organisation) \n\nUmfeldanalyse (extern)\n\nMarktanalyse (Marktanteil, Markdurchdringung, Marktpotential)\nKonkurrenzanalyse, Branchenstruktur, Trendanalyse \n\nUmweltanalyse\n\nWirtschaft, soziodemografisches- und technologisches Umfeld\nPolitik, Gesellschaft, ökologisches Umfeld \n\nQuelle: Vgl. Bruhn, M. (2022): Marketing - Grundlagen für Studium und Praxis, 15. Auflage\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n1 2\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_16.png');
INSERT INTO pdf_pages VALUES(655,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',17,replace('SITUATIONSANALYSE ALS BASIS\n\nAnalyse der\nIst-Situation\n\nAnalyse der\nkünftigen Entwicklungen\n\nUnternehmen\n\nWettbewerber\n\nUnternehmensumfeld\n\nUmweltanalyse\n\nUnternehmens-\nanalyse\n\nKonkurrenten-\nanalyse\n\nMarkt-\nanalyse\n\nUmwelt-\nanalyse\n\nStärken-Schwächen-\nAnalyse\n\nChancen-Risiken-\nAnalyse\n\nSWOT-ANALYSE\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n1 3\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_17.png');
INSERT INTO pdf_pages VALUES(656,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',18,replace('SWOT-ANALYSE\n\nEin generisches und nahezu klassisches Verfahren zur Analyse der Marketingsituation\nstellt die Methode der SWOT-Analyse dar.\n\nZiel des Instruments ist eine prägnante Analyse der Entwicklung und eine Prognose der\nrelevanten Einflussfaktoren des Marketings.\n\nAls Ergebnis werden die wichtigsten externen Chancen und Risiken sowie\nkorrespondierenden Stärken und Schwächen eines Unternehmens identifiziert.\n\nQuelle: Vgl. Bruhn, M. (2022): Marketing - Grundlagen für Studium und Praxis, 15. Auflage\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n1 4\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_18.png');
INSERT INTO pdf_pages VALUES(657,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',19,replace('SWOT-ANALYSE\n\nSTRENGTHS - STÄRKEN\n\nsind Eigenschaften oder\nFähigkeiten eines Unternehmens,\ndie im Wettbewerb einen Vorteil\ndarstellen oder die das\nUnternehmen besser beherrscht als\ndie Konkurrent:innen.\n\nOPPORTUNITIES - CHANCEN\n\nsind Gelegenheiten oder\nEntwicklungen in der Umwelt oder im\nMarkt, die für das Unternehmen von\nVorteil sein können oder aus denen\nPotenziale entstehen können.\n\nS\n\nO\n\nW\n\nT\n\nQuelle: Vgl. Bruhn, M. (2022): Marketing - Grundlagen für Studium und Praxis, 15. Auflage\n\nWEAKNESSES - SCHWÄCHEN\n\nsind Eigenschaften oder Merkmale\neines Unternehmens, die im\nWettbewerb einen Nachteil darstellen\nund sich negativ auf den Erfolg\nauswirken können.\n\nTHREATS - SCHWÄCHEN\n\nsind Faktoren oder Entwicklungen\nim Umfeld oder auf dem Markt, die\npotenzielle Nachteile oder Gefahren\nfür das Unternehmen darstellen und\nes schwächen oder zu Verlusten\nführen können.\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n1 5\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_19.png');
INSERT INTO pdf_pages VALUES(658,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',20,replace('SWOT-ANALYSE - INTERN\n\nSTRENGTHS - STÄRKEN\n\nWEAKNESSES - SCHWÄCHEN\n\nWas machen wir gut?\nWas gefällt unseren Kunden oder Partnern an uns?\nIn welchen Bereichen übertreffen wir unsere\nWettbewerber?\nWas ist einzigartig an unserem Unternehmen und\nunseren Produkten oder Dienstleistungen?\nWelche Vermögenswerte besitzen wir? (eigene\nTechnologien, Kapital)\n\nWas können wir verbessern?\nWomit sind unsere Kunden oder Partner\nunzufrieden?\nWo fallen wir hinter unsere Wettbewerber zurück?\nWo fehlen uns Wissen oder Ressourcen?\n\nWir haben einen ausgezeichneten Kundensupport\nWir bieten Funktionen, die kein anderes Unternehmen\nabdeckt\nWir haben mehr Website-Traffic als 75 % unserer\nMitbewerber\nUnser Social-Media-Marketing generiert sehr viele\nInteraktionen\n\nUnsere Prozesse sind ineffizient.\nUnsere Kunden finden unser Produkt schwierig zu\nbenutzen.\nUnser organischer Traffic ist geringer als der\nunserer Konkurrenten.\nWir haben nicht die nötigen Ressourcen, um zu\nexpandieren. \n\nLeitfragen\n\nBeispiele\n\nQuelle: Vgl. Bruhn, M. (2022): Marketing - Grundlagen für Studium und Praxis, 15. Auflage\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n1 6\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_20.png');
INSERT INTO pdf_pages VALUES(659,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',21,replace('SWOT-ANALYSE - EXTERN\n\nOPPORTUNITIES - CHANCEN\n\nTHREATS - RISIKEN\n\nLeitfragen\n\nVon welchen aufkommenden Trends können wir\nprofitieren?\nWelche unserer Stärken könnten für potenzielle Partner\nwertvoll sein?\nWelche angrenzenden Märkte könnten wir erschließen?\nGibt es geografische Standorte mit weniger Wettbewerb?\nKönnen wir unsere Geschichte oder Erfolge mit der Welt\nteilen?\n\nWas macht unsere Konkurrenz?\nWie könnten unsere Schwächen uns verwundbar machen?\nAuf welche Markttrends sind wir nicht vorbereitet?\nWelche wirtschaftlichen oder politischen Probleme könnten\nsich auf unser Geschäft auswirken?\n\nBeispiele\n\nKunden fragen ein ähnliches Produkt wie unseres nach.\nKönnen wir die Nachfrage bedienen?\nNiemand verkauft ein Produkt wie unseres in Italien.\nKönnen wir expandieren?\nWir haben gerade einen wichtigen Meilenstein erreicht.\nKönnen wir positive Medienberichterstattung anstoßen?\n\nQuelle: Vgl. Bruhn, M. (2022): Marketing - Grundlagen für Studium und Praxis, 15. Auflage\n\nUnser Hauptkonkurrent bringt ein Produkt mit ähnlichen\nEigenschaften auf den Markt\nDie Regierung diskutiert über einen Gesetzentwurf, der sich auf\nunser Geschäft auswirken könnte\nDie Wirtschaft hat Schwierigkeiten, sich an die neuen\nBedingungen unter Covid 19 anzupassen\nWir haben nicht das Marketingbudget, um mit unseren\nKonkurrenten mitzuhalten\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n1 7\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_21.png');
INSERT INTO pdf_pages VALUES(660,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',22,replace('S\n\nW\n\nO\n\nT\n\nSTÄRKEN\n\nSCHWÄCHEN\n\nCHANCEN\n\nRISIKEN\n\nWorin ist dein Unternehmen\nbesser als andere?\nWelche Vorteile hat dein\nUnternehmen?\nWorin sehen andere die Stärke\ndeines Unternehmens?\nWelche einzigartigen oder\ngünstigen Ressourcen hat dein\nUnternehmen?\nWelche Produkte oder\nDienstleistungen sind besonders\nwettbewerbsfähig?\nWas weißt du über deine\nZielgruppe und wie kannst du\ndiese möglichst\nzufriedenstellen?\nWelche Faktoren führen dein\nUnternehmen zum Erfolg?\nWas ist der USP der\nDienstleistung oder der Produkte\ndeines Unternehmens?\n\nWas kann in deinem\n\nWelche guten Chancen bieten\n\nWelche Hindernisse stehen dem\n\nUnternehmen verbessert werden?\n\nsich Ihrem Unternehmen?\n\nErfolg Ihres Unternehmens im\n\nWas sollte dein Unternehmen\n\nWelche interessanten Trends\n\nWeg?\n\nvermeiden?\n\nkönnen Sie für Ihr Unternehmen\n\nWas macht die Konkurrenz\n\nWas waren Rückschläge in der\nVergangenheit?\nWorin sehen andere die\n\nSchwächen deines\nUnternehmens?\nWelche Arten von Aufträgen\n\ngehen oft an die Konkurrenz, statt\nan dein Unternehmen und warum\nist das so?\n\nWas kann die Konkurrenz\nvielleicht besser als dein\nUnternehmen?\n\nWas müsst du an deiner\nDienstleistung oder Ihrem\nProdukt besonders genau\n\nerklären, damit es\nAußenstehende verstehen?\nWelche Faktoren tragen zum\n\nMisserfolg bei?\n\nnutzen?\nWas geschieht auf den Märkten\nund im technologischen\n\nBereich?\nWelche Märkte können mit den\nProdukten oder\n\nDienstleistungen deines\nUnternehmens noch erschloßen\nwerden?\n\nWelche Gesetzesänderungen\nbetreffen unser Unternehmen\nund inwiefern?\n\nWelchen Einfluss haben\ngesellschaftliche Entwicklungen\nauf unser Unternehmen?\n\nWelche lokalen Ereignisse sind\nfür unser Unternehmen\ninteressant?\n\nanders und vielleicht besser?\nGibt es Konkurrent:innen, die\nder Stellung Ihres Unternehmens\n\nauf dem Markt gefährlich\nwerden können?\nWelche Änderungen bei\n\nQualitätsstandards, Produkten\noder Dienstleistungen gibt es?\nGibt es oder entsteht durch den\n\ntechnischen Wandel eine\nBedrohung für Ihr Unternehmen?\nWelche Trends beeinflussen Ihre\n\nBranche?\nWie hoch sind Schulden oder\noffene Rechnungen in Ihrem\n\nUnternehmen?\nStellen die Schwächen deines\nUnternehmens eine Gefahr dar?\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n1 8\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_22.png');
INSERT INTO pdf_pages VALUES(661,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',23,replace('SWOT-ANALYSE\n\nE\nS\nY\nL\nA\nN\nA\nE\nN\nR\nE\nT\nN\n\nI\n\nStärken \n\nChancen\n\nWorin sind wir besser als\nandere?\nWelche Faktoren führen\nzu Erfolg?\nHaben wir einzigartige\nRessourcen?\nWas ist unser USP?\n\nWelche Chancen\nbestehen?\nBieten Trends oder\nlokale/globale Ereignisse\nneue Chancen?\nWelche Märkte sind noch\ninteressant für uns?\n\nSchwächen\n\nRisiken\n\nAn welchen Stellen\nkönnen wir uns\nverbessern?\nWas waren Rückschläge\nin der Vergangenheit?\nWelche Faktoren können\nzu Misserfolg führen?\n\nAn welchen Stellen\nkönnen wir uns\nverbessern?\nWas waren Rückschläge\nin der Vergangenheit?\nWas macht die\nKonkurrenz anders und\nvielleicht besser?\n\nE\nX\nT\nE\nR\nN\nE\nA\nN\nA\nL\nY\nS\nE\n\nQuelle: Vgl. Bruhn, M. (2022): Marketing - Grundlagen für Studium und Praxis, 15. Auflage\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n1 9\n\n \n \n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_23.png');
