INSERT INTO pdf_pages VALUES(663,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',25,replace('SITUATIONSANALYSE ALS BASIS\n\nNachdem eine interne und externe Analyse durchgeführt wurde, werden die gesammelten\nInformationen zur Ableitung von Strategien genutzt.\n\nDafür wird die SWOT-Analyse um vier zusätzliche Felder erweitert.\n\nAls Erweiterung der SWOT-Analyse wirft ein Licht auf die Beziehung zwischen internen und\nexternen Faktoren.\n\nDie Beziehungen zwischen den Quadranten liefern Ansätze für Strategien.\n\nQuelle: Vgl. Bruhn, M. (2022): Marketing - Grundlagen für Studium und Praxis, 15. Auflage\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n2 1\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_25.png');
