INSERT INTO pdf_pages VALUES(665,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',27,replace('SITUATIONSANALYSE ALS BASIS\n\nStärken/Chancen — SO-Strategie:\n\nVerknüpfung von Stärken und Chancen\nIdeen, wie Stärken eingesetzt werden können, um Chancen zu ergreifen\n\nStärken/Risiken — WO-Strategie:\n\nVerknüpfung von Schwächen und Chancen\nIdeen, wie Stärken genutzt werden können, um Risiken zu überwinden\n\nSchwächen/Chancen — ST-Strategie:\n\nSchwächen minimieren, um Risiken abzuwenden\nIdeen, wie Schwächen minimiert werden können, indem Chancen genutzt werden\n\nSchwächen/Risiken— WT-Strategie:\n\nSchwächen abbauen, um Risiken zu reduzieren\nIdeen, wie Schwächen umgangen und gleichzeitig Risiken minimiert werden können\n\nQuelle: Vgl. Bruhn, M. (2022): Marketing - Grundlagen für Studium und Praxis, 15. Auflage\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n2 3\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_27.png');
INSERT INTO pdf_pages VALUES(666,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',28,replace('SWOT-ANALYSE\n\nStärken \n\nSchwächen\n\nWorin sind wir besser als\nandere?\nWelche Faktoren führen\nzu Erfolg?\nHaben wir einzigartige\nRessourcen?\nWas ist unser USP?\n\nAn welchen Stellen\nkönnen wir uns\nverbessern?\nWas waren Rückschläge\nin der Vergangenheit?\nWelche Faktoren können\nzu Misserfolg führen?\n\nChancen\n\nWelche Chancen\nbestehen?\nBieten Trends oder\nlokale/globale Ereignisse\nneue Chancen?\nWelche Märkte sind noch\ninteressant für uns?\n\nRisiken\n\nAn welchen Stellen\nkönnen wir uns\nverbessern?\nWas waren Rückschläge\nin der Vergangenheit?\nWas macht die\nKonkurrenz anders und\nvielleicht besser?\n\nS-O-Strategie\nAus welchen\nStärken\nergeben sich\nChancen?\n\nW-O-Strategie\nSchwächen\neliminieren, um\nneue Chancen\nzu nutzen\n\nS-T-Strategie\nWelche Stärken\nminimieren\nRisiken?\n\nW-T-Strategie\nVerteidigungs-\nstrategien, damit\nSchwächen nicht\nzu Risiken\nwerden\n\nQuelle: Vgl. Bruhn, M. (2022): Marketing - Grundlagen für Studium und Praxis, 15. Auflage\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n2 4\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_28.png');
