INSERT INTO pdf_pages VALUES(669,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',31,replace('EXTERNE ANALYSE\nAUF DREI EBENEN \n\nU N T E R N E H M E N\n\nAllgemeines Umfeld\n\nUmfeldfaktoren, die viele Organisationen mehr\noder weniger beeinflussen, z. B. Klimawandel,\nInternet, alternde Gesellschaft\n\nBranchenumfeld\n\nFaktoren in Bezug auf Unternehmen in der\ngleichen Branche, d.h. Anbieter des gleichen\nProdukts oder Services, z. B. Automobilindustrie\n\nWettbewerbsumfeld\n\nFaktoren in Bezug auf konkrete Wettbewerber\ndes betreffenden Unternehmens\n\nQuelle: Vgl. Bruhn, M. (2022): Marketing - Grundlagen für Studium und Praxis, 15. Auflage\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n2 7\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_31.png');
INSERT INTO pdf_pages VALUES(670,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',32,replace('EXTERNE ANALYSE\nNACH DER “PESTEL”-STRUKTUR - ANALYSE DES ALLGEMEINEN UNTERNEHMENSUMFELDES\n\nP\n\nE\n\nS\n\nPOLITICAL\n\nECONOMIC\n\nSOCIAL\n\nRegierungspolitik\npolitische Stabilität\nKorruption\nArbeitsrecht\n\nWechselkurse\nWirtschaftswachstum\nZinssätze\nInflationsraten\n\nBevölkerungswachstum\nLebensstil\nAltersverteilung\nSicherheitsbedürfnis\n\nT\n\nE\n\nL\n\nTECHNOLOGICAL\n\nENVIRONMENT\n\nLEGAL\n\nInnovationsgraD\nAutomatisierung\nF&E-Tätigkeit\ntechnologischer Wandel\n\nKlimawandel\nNachhaltigkeit\nVerschmutzung\nMüll & Recycling\n\nKartellrecht\nPatent- und\nUrheberrecht\nVerbraucherschutzrecht\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n2 8\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_32.png');
INSERT INTO pdf_pages VALUES(671,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',33,replace('EXTERNE ANALYSE\nANALYSE DES UNTERENEHMENSUMFELDS MIT “PORTERS´ FIVE FORCES”\n\nNeue Anbieter\n\nLieferanten\n\nWettbewerbs-\nintensität\n\nAbnehmer\n\nSubstitute\n\nDas auf Porter zurückgehende\n”Fünf-Kräfte-Modell” betrachtet die\nderzeitige Wettbewerbsintensität in\neinem Markt, sowie vier Faktoren,\ndie die Wettbewerbsintensität\nbeeinflussen.\nDas Modell stellt im Wesentlichen\neinen Strukturierungsansatz für die\nstrategische Marktanalyse dar.\n\nQuelle: Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n2 9\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_33.png');
INSERT INTO pdf_pages VALUES(672,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',34,replace('EXTERNE ANALYSE\nANALYSE DES UNTERENEHMENSUMFELDS MIT “PORTERS´ FIVE FORCES”\n\nVerhandlungsmacht\nvon Lieferanten\n\nBedrohung durch neue\nAnbieter\n\nWettbewerb zwischen\nderzeitigen Anbietern\nund derzeitigen\nProdukten/Leistungen\n\nBedrohung durch\nSubstitutprodukte/-\nleistungen\n\nVerhandlungsmacht\nder Abnehmer\n\nQuelle: Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n3 0\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_34.png');
INSERT INTO pdf_pages VALUES(673,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',35,replace('WANN SIND DIE KRÄFTE BESONDERS STARK?\n\nWettbewerbs-\nintensität\n\nNeue Anbieter\n\nLieferanten\n\nSubstitute\n\nAbnehmer\n\nWettbewerber in etwa gleich groß und geringes Marktwachstum \nHohe Fixkosten und hohe Austrittsbarrieren\nKaum Möglichkeiten zur Produktdifferenzierung\n\nkeine spezifische Erfahrung oder große Investitionen erforderlich, um in den Markt einzutreten\nkein Vorteil für besonders große Unternehmen („Betriebsgrößenersparnisse“)\nunbegrenzter Zugang zu den Vertriebskanälen möglich\nkeine rechtlichen Einschränkungen (Zölle, Patente) notwendig\n\nEs gibt nur wenige Lieferanten, die zumeist für ein großes Volumen verantwortlich sind.\nEin Lieferantenwechsel wäre teuer für das Unternehmen.\nEs gibt nur wenige oder gar keine Substitute für die Produkte.\n\nDas Preis-Leistungs-Verhältnis der Substitute ist für die Kunden attraktiv (und wenn Produktpreis\nA steigt, wechseln Kunden gern auf das Substitut „Kreuzpreiselastizität“)\nGeringe Umstellungskosten für die Kunden beim Wechsel zum Substitut\n\nEs gibt nur wenige und große Käufer/Kunden.\nEs gibt volle Transparenz über Produkte und Preise in der Branche.\nDie Käufer haben niedrige Kosten für den Wechsel von Händler A zu Händler B.\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n3 1\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_35.png');
INSERT INTO pdf_pages VALUES(674,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',36,replace('EXTERNE ANALYSE\nANALYSE DER WETTBEWERBER\n\nWas den\nWettbewerber\nantreibt\n\nWas der\nWettbewerber tut\nund kann\n\nWas der\nWettbewerber über\ndie Branche denkt\n\nWas die Fähigkeiten\ndes Wettbewerbers\nsind\n\nZukünftige Ziele\n\nWie sehen unsere Ziele aus im Vergleich zu denen unserer Wettbewerber?\nWo wird in Zukunft der Schwerpunkt liegen?\n\nAktuelle Strategie\n\nAuf welcher Basis konkurrieren wir derzeit?\n\nAnnahmen\n\nGehen wir davon aus, dass die Zukunft volatil sein wird?\nWelche Annahmen treffen unsere Wettbewerber über die Branche und sich\nselbst?\n\nFähigkeiten\n\nWas sind unsere Stärken und Schwächen?\nWie bewerten wir sie im Vergleich zu unseren Wettbewerbern?\n\nQuelle: Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\nWas werden\nunsere\nWettbewerber in\nZukunft tun?\nWo haben wir\neinen Vorteil\ngegenüber\nunseren\nWettbewerbern?\nWie wird sich\ndadurch unsere\nBeziehung zu\nunseren\nWettbewerbern\nverändern?\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n3 2\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_36.png');
INSERT INTO pdf_pages VALUES(675,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',37,replace('INTERNE ANALYSE\nUNIQUE SELLING PROPOSITION\n\nDie Abkürzung USP steht für „Unique Selling Proposition“ oder „Unique Selling Point“, was in\nbeiden Fällen mit „einzigartiges Verkaufsversprechen“ übersetzt werden kann.\n\nDer Marketingbegriff meint das Alleinstellungsmerkmal eines Produkts oder einer\nDienstleistung und beantwortet für Kunden die Frage, was ein Angebot von der Konkurrenz\nabhebt und welchen Vorteil sie durch dein Angebot haben.\n\nIm Idealfall fließt ein USP auch in das Erscheinungsbild eines Unternehmens ein und findet\nsich beispielsweise im Logo, dem Design einer Website und auf anderen Werbemitteln\nwieder.\n\nQuelle: Vgl. Bruhn, M. (2022): Marketing - Grundlagen für Studium und Praxis, 15. Auflage\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n3 3\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_37.png');
INSERT INTO pdf_pages VALUES(676,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',38,replace('UNIQUE SELLING PROPOSITION\nWELCHE EIGENSCHAFTEN EINE UNIQUE SELLING PROPOSITION ERFÜLLEN SOLLTE:\n\nEinzigartigkeit\n\nDas Produkt- oder Kundenversprechen sollte einzigartig sein, um Wettbewerbsvorteile zu\nkommunizieren.\nDie Einzigartigkeit deiner Idee, auch bei bewährten Geschäftsideen, sollte herausgestellt werden.\n\nZielgruppenrelevanz\n\nDer USP sollte zur Zielgruppe passen und deren Probleme lösen.\nKlar vermitteln, warum das Produkt oder die Dienstleistung für die Zielgruppe relevant ist.\n\nWirtschaftlichkeit\n\nDas Alleinstellungsmerkmal muss rentabel sein, damit es langfristig erfolgreich ist.\nNiedrige Preise sollten nicht dazu führen, dass du draufzahlst.\n\nQuelle: Vgl. Bruhn, M. (2022): Marketing - Grundlagen für Studium und Praxis, 15. Auflage\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n3 4\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_38.png');
INSERT INTO pdf_pages VALUES(677,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',39,replace('UNIQUE SELLING PROPOSITION\nWELCHE EIGENSCHAFTEN DIE UNIQUE SELLING PROPOSITION ERFÜLLEN SOLLTE:\n\nGlaubwürdigkeit\n\nDer USP muss der Wahrheit entsprechen und nachweisbar sein.\nVermeide unrealistische Versprechungen und behalte die Glaubwürdigkeit bei.\n\nEinfachheit\n\nDer USP sollte leicht verständlich sein, ohne zusätzliche Erklärungen.\nFormuliere den USP in einem kurzen, verständlichen Satz für klare Kommunikation.\n\nQuelle: Vgl. Bruhn, M. (2022): Marketing - Grundlagen für Studium und Praxis, 15. Auflage\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n3 5\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_39.png');
