INSERT INTO pdf_pages VALUES(681,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',43,replace('UNIQUE SELLING PROPOSITION\nBEISPIELE FÜR EINEN GELUNGENEN USP\n\nDas Fernbusunternehmen Flix aus München wirbt vor allem\nmit besonders günstigen Fernreisen. So nutzt Flix seine\nPreise als Alleinstellungsmerkmal.\nWährend die Marke FlixBus 2013 noch lediglich Busreisen\nanbot, hat das Unternehmen nun auch den FlixTrain auf den\nMarkt gebracht. So kann Flix über die günstigen Preise sogar\nder Deutschen Bahn für bestimmte Strecken Konkurrenz\nmachen.\nIhren USP präsentiert die Marke schnörkellos und direkt:\n„FLIX it“. Der Slogan (ähnlich wie „fix it“ = repariere/löse es)\nfungiert als Lösung für typische Reiseprobleme. Kein\nSitzplatz? FLIX it! Zugticket zu teuer? FLIX it!\n\nQuelle: https://www.facebook.com/watch/?v=536426047388312\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n3 9\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_43.png');
INSERT INTO pdf_pages VALUES(682,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',44,replace('UNIQUE SELLING PROPOSITION\nBEISPIELE FÜR EINEN GELUNGENEN USP\n\nDas Unternehmen WMF\nbietet innovative und\nhochwertige\nKüchenprodukte aus dem\nhöherpreisigen Segment an.\nWMFs Nutzenversprechen\nhebt daher auf Qualität und\nLuxus ab. Auf den Punkt\ngebracht wurde der USP in\nder Rebranding-Kamapgne\nvon WMF im Jahr 2021: „Feel\nPerfection“.\n\nQuelle: https://www.youtube.com/watch?v=WV4eWNQj_3U\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n4 0\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_44.png');
INSERT INTO pdf_pages VALUES(683,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',45,replace('UNIQUE SELLING PROPOSITION\nBEISPIELE FÜR EINEN GELUNGENEN USP\n\nDie Marke Armedangels verspricht nachhaltige Kleidungsstücke\nund hat sich als USP „höchsten Respekt für Natur und Mensch“\nauf die Fahnen geschrieben.\nDas Unternehmen verfolgt dabei einen ganzheitlichen Ansatz, der\nin sämtlichen Ebenen der Produktionskette stringent verfolgt\nwird.\nVon der Herstellung der Kleidung unter fairen\nArbeitsbedingungen über die Inhaltsstoffe aus nachwachsenden\nRohstoffen bis hin zu den wenig an Trends orientierten\nKollektionen, setzt Armedangels bei seinen Produkten auf\nLanglebigkeit und zeitlosen Chic ohne schlechtes Gewissen.\nDieses Nutzenversprechen formuliert die Marke prägnant mit\nihrem Slogan: „Made to make a difference“.\n\nQuelle: https://www.armedangels.com/de-en/about-us/our-mission\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n4 1\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_45.png');
INSERT INTO pdf_pages VALUES(684,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',46,replace('UNIQUE SELLING PROPOSITION\nPRODUKTMERKMALE EIGNEN SICH ALS USP\n\nDer günstigste Preis:\n\nFlixBus wirbt mit dem einfachen Versprechen, günstige Fernreisen zu ermöglichen, schon im Slogan: „Flixbus\n– Günstig mit dem Fernbus reisen.“ Der Optiker Fielmann gibt ebenfalls ein Preisversprechen, indem das\nUnternehmen zahlreiche Dienstleistungen kostenlos anbietet und bewirbt.\n\nSchnelle Lieferung:\n\nAmazon verspricht mit dem Premium-Dienst „Amazon Prime“ einen schnellen und kostenlosen Versand –\noftmals mit Lieferung direkt am nächsten Tag.\n\nEinfachheit:\n\nDie Online-Bank Ing wirbt erfolgreich damit, ein so komplexes Thema wie Banking und Finanzen in wenigen\nProdukten online für jeden zugänglich und einfach zu machen.\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n4 2\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_46.png');
INSERT INTO pdf_pages VALUES(685,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',47,replace('UNIQUE SELLING PROPOSITION\nPRODUKTMERKMALE EIGNEN SICH ALS USP\n\nFlexibilität:\n\nDer Fitnessanbieter Urban Sports Club ermöglicht es seinen Mitgliedern nicht nur zuhause in Deutschland,\nsondern auch im Urlaub in vielen Ländern europaweit zu trainieren.\n\nNachhaltigkeit:\n\nDie Modemarke Armedangels verspricht „höchsten Respekt für Natur und Mensch“ und produziert unter\nfairen Bedingungen nachhaltige Kleidungsstücke, die in langlebigen Kollektionen ihren Platz finden. All diese\nPunkte finden sich im Slogan „Fair & Eco Fashion“ wieder.\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n4 3\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_47.png');
