INSERT INTO pdf_pages VALUES(689,'pdfs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1.pdf',51,replace('ARBEITSAUFGABE\n\nAnalysiert euer Start-up auf Stärken und Schwächen sowie\nChancen und Risiken (SWOT-Analyse).\n\nBeschreibt vor allem die Wettbewerbssituation: \n\nWo wird euer Start-up von Wettbewerbern bedrängt, wo könnt\nihr euch positionieren und wie Wettbewerbsvorteile erzielen?\nFormuliert euren Wettbewerbsvorteil/USP!\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n4 7\n\n','\n',char(10)),'imgs/Modul Marketing_Zwischenpräsentation und Marketingstrategien und -ziele_Teil 1_51.png');
