INSERT INTO pdf_pages VALUES(696,'pdfs/20240614 IU Vorlesung NIS-2 und KI.pdf',6,replace('14.06.2024\n\nDigitalisierung als Chance oder Risiko?\nAktuelles zu NIS-2, KI und mehr\n\nSektoren KRITIS-Dachgesetz\n\nKRITIS-DachG\n\n„KRITIS-Sektoren“\n\nNIS2UmsuCG\n\nEnergie\n\nTransport & Verkehr\n\nWeltraum\n\nGesundheit\n\nErnährung\n\nTrinkwasser/Abwasser\n\nSiedlungsabfälle\n\nIT und TK\n\nFinanz/Versicherungen\n\nDefinierte Anlagen aus KritisV\n\nz. B. Bahnhof, Krankenhaus, Kraftwerk\n\nVersorgt > 500.000 Menschen\n\nBetreiber kritischer Anlagen \n(KRITIS)\n\nBundesverwaltung (tw.)\n\nBundeskanzleramt und Bundesministerien\n\nStand 05/2024\n\n11\n\n11\n\nDigitalisierung als Chance oder Risiko?\nAktuelles zu NIS-2, KI und mehr\n\nKRITIS-DachG – Physische Sicherheit\n\n− Registrierung (Frist: 3 Monate)\n\n− Resilienz-Maßnahmen (Frist: 10 Monate)\n\n− Risikoanalyse (Frist: alle 4 Jahre)\n\n− Meldepflicht (Frist: 10 Monate – nach Vorfall 24 h)\n\n− Nachweise/Audits\n\n− Sanktionen\n\n12\n\n12\n\nStand 05/2024\n\n6\n\n','\n',char(10)),'imgs/20240614 IU Vorlesung NIS-2 und KI_6.png');
INSERT INTO pdf_pages VALUES(697,'pdfs/20240614 IU Vorlesung NIS-2 und KI.pdf',7,replace('14.06.2024\n\nDigitalisierung als Chance oder Risiko?\nAktuelles zu NIS-2, KI und mehr\n\nSchwellwerte NIS2UmsuCG\n\nOrganisationen\n\nSektoren\n\nMitarbeitende\n\nUmsatz\n\nBilanz\n\nBesonders wichtige \nEinrichtungen\n\nNIS-2 Anlage 1\n\nWichtige \nEinrichtungen\n\nNIS-2 Anlage 1\nNIS-2 Anlage 2\n\na) ≥ 250\nb) \n\na) ≥ 50\nb) \n\n≥ 50 Mio. EUR und\n\n≥ 43 Mio. EUR\n\n≥ 10 Mio. EUR und \n\n≥ 10 Mio. EUR \n\nKritische\nAnlagen\n\nKRITIS-Sektoren\n\nSchwellwerte werden pro Anlage definiert\n\n13\n\n13\n\nStand 05/2024\n\nDigitalisierung als Chance oder Risiko?\nAktuelles zu NIS-2, KI und mehr\n\nPflichten im NIS2UmsuCG\n\n− Registrierung (Frist: 3 Monate)\n\n− Risikomanagement (Risikoanalysen, ISMS, Incident Management, BCM, etc.)\n\n− Meldepflichten (nach Vorfall 24 h, SOC/SIEM)\n\n− Nachweise/Audits\n\n− Informationspflichten \n\n− Governance (persönliche Haftung Geschäftsleitung)\n\n− Sanktionen (bis 10 Mio. EUR/2 % vom weltweiten Umsatz)\n\n14\n\n14\n\nStand 05/2024\n\n7\n\n','\n',char(10)),'imgs/20240614 IU Vorlesung NIS-2 und KI_7.png');
INSERT INTO pdf_pages VALUES(698,'pdfs/20240614 IU Vorlesung NIS-2 und KI.pdf',8,replace('Digitalisierung als Chance oder Risiko?\nAktuelles zu NIS-2, KI und mehr\n\nGanz konkret gefragt…:\n\n− Haben wir ein aktuelles Backup?\n\n− Wo liegt das Backup? Ist das Backup offline?\n\n− Haben wir die Wiederherstellung getestet?\n\n− Also haben wir getestet, ob System <xx> wirklich wiederherstellbar ist?\n\n− Wie lange braucht das Wiedereinspielen?\n\n− … das gleiche für Firewall, Patch-Management, Benutzerverwaltung, uvm.\n\n15\n\n15\n\nDigitalisierung als Chance oder Risiko?\nAktuelles zu NIS-2, KI und mehr\n\nNotfallprozess & Notfallhandbuch\n\n16\n\n16\n\n14.06.2024\n\n8\n\n','\n',char(10)),'imgs/20240614 IU Vorlesung NIS-2 und KI_8.png');
INSERT INTO pdf_pages VALUES(699,'pdfs/20240614 IU Vorlesung NIS-2 und KI.pdf',9,replace('14.06.2024\n\nWas gilt denn nun?\n\n17\n\nDigitalisierung als Chance oder Risiko?\nAktuelles zu NIS-2, KI und mehr\n\nWer ist betroffen von NIS-2?\n\n1. Unmittelbar betroffen in definierten Sektoren sind\n\n− „Wichtige“ Einrichtungen\n\n− „Besonders wichtige“ Einrichtungen \n\n− Betreiber kritischer Anlagen \n\n2. Mittelbar betroffen sind \n\n− Dienstleister, Zulieferer\n\n− Nebenprozesse, die schnell mal übersehen werden\n\n18\n\n18\n\nVorbehaltlich Änderungen, Stand 05/2024\n\n9\n\n','\n',char(10)),'imgs/20240614 IU Vorlesung NIS-2 und KI_9.png');
