INSERT INTO pdf_pages VALUES(711,'pdfs/20240614 IU Vorlesung NIS-2 und KI.pdf',21,replace('14.06.2024\n\nWie funktioniert \nMachine Learning/KI?\n\nChatBots & Datenschutz\n\nAbgesehen von den\n„ Datenschutz-Basics“:\n\n1. Vertrauliches oder \n\npersonenbezogene Daten \nim Prompt?\n\n2. Nutzung des Outputs?\nLernendes LLM?\n\n3. Wie sind die Datenströme?\n\n41\n\n41\n\nWie funktioniert \nMachine Learning/KI?\n\nPrompt-Vorlage: Arztbrief mit ChatGPT\n\n42\n\n42\n\nQuelle: https://www.sophiehundertmark.com/chatgpt-und-generative-ai-fuer-arztberichte/\n\n21\n\n','\n',char(10)),'imgs/20240614 IU Vorlesung NIS-2 und KI_21.png');
INSERT INTO pdf_pages VALUES(712,'pdfs/20240614 IU Vorlesung NIS-2 und KI.pdf',22,replace('14.06.2024\n\nWas kann ich für dich tun?\n\nIch möchte wissen, wie viele \nUrlaubstage mir dieses Jahr zustehen\n\nOk, kleinen Moment…\nDu hast dieses Jahr noch \n12 ungeplante Urlaubstage\n\nDanke! Und wie sieht es bei \nmeinen Kollegen aus?\n\nWie funktioniert \nMachine Learning/KI?\n\nHerausforderung\nZugriffsrechte\n\nUnternehmensinterne ChatBots:\n\n− Qualität der Trainingsdaten\n\n(Bias, Diskriminierung, Datenschutz, \nSchutzrechte Dritter)\n\n− Überprüfung von Prompts – \n\nwie Zugriffsrechte handhaben?\n\n− Bewertung und Überprüfung \nder Ausgabe in Hinblick auf \nBerechtigung, Korrektheit, etc.\n\n43\n\n43\n\nWie funktioniert \nMachine Learning/KI?\n\nVASA-1: Lifelike Audio-Driven Talking Faces\n\n44\n\n44\n\nQuelle: https://www.microsoft.com/en-us/research/project/vasa-1/\n\n22\n\n','\n',char(10)),'imgs/20240614 IU Vorlesung NIS-2 und KI_22.png');
INSERT INTO pdf_pages VALUES(713,'pdfs/20240614 IU Vorlesung NIS-2 und KI.pdf',23,replace('14.06.2024\n\nHalluzination\n\nist das überzeugend formulierte \nErgebnis generativer KI, das nicht \ndurch Trainingsdaten gerechtfertigt \nzu sein scheint und objektiv falsch \nsein kann.\n\nPressemitteilung: https://noyb.eu/\n\nWie funktioniert \nMachine Learning/KI?\n\nProblem KI-Halluzination\n\n45\n\n45\n\nWie funktioniert \nMachine Learning/KI?\n\nAuswirkung von KI-Halluzination\n\nRisiken \nim Umgang mit \nHalluzinationen bei \nEinsatz von KI im \nGesundheits- und \nSozialwesen\n\nIgnorieren des Risikopotentials\n\nUnterschätzen der Konsequenzen\n\nFehlende Transparenz\n\nUnzureichende Qualitätssicherung/Testung\n\nVerharmlosung\n\n46\n\n46\n\n23\n\n','\n',char(10)),'imgs/20240614 IU Vorlesung NIS-2 und KI_23.png');
