INSERT INTO pdf_pages VALUES(720,'pdfs/20240614 IU Vorlesung NIS-2 und KI.pdf',30,replace('14.06.2024\n\nHürde Datenschutz?\nKI & Copilot im Einsatz\n\nAufgaben Datenschutz bei Einsatz von KI\n\nVoraussetzungen\n\nAnbieter & Lösung\n\nBetrieb & Einsatz\n\no Zweck?\n\no Eignung\n\no Nutzung Lokal, Hosting, Cloud\n\no Sitz/Rechtsrahmen, Datenströme\n\no Rechtsgrundlage\n\no Richtlinien\n\no Sensibilisierung und Schulung\n\no Nutzungsbedingungen\n\no Auftragsverarbeitung \n\nbzw. Joint Controllership\n\no Erlaubter Nutzungsumfang \n\n(privat/beruflich/vertraulich)\n\no Dokumentation \n\n(Verarbeitung und KI-Verfahren)\n\no Betroffenenrechte\n\n(z. B. Auskunft, Widerspruch)\n\no Datenschutz-Folgenabschätzung\n\no Risiko-Bewertung AI-Act\n\no Prozesse, Umgang mit Vorfällen\n\no Qualität des KI-Modells \n\no Richtigkeit Output\n\n(Herkunft der Trainingsdaten)\n\no Eigene Zwecke des Anbieters \n(Training, Verbesserung?)\n\n60\n\n60\n\nHürde Datenschutz?\nKI & Copilot im Einsatz\n\nPriorisierung von KI-Projekten\n\nRisiko\n− Datenquellen/Datenqualität und Nutzung\n− (IT-)Sicherheitsfragen\n− Transparenz und Regularien\n− Strategische Fragen\n− Lieferkette/Auftragnehmer\n\nWirksamkeit\n− Lösung von Problemen/Arbeitserleichterung\n− Qualitätsverbesserung\n− Strategische Bedeutung\n− Skalierbarkeit\n− Kosteneinsparung, Umsatz/Ergebnis\n\nMachbarkeit\n− Datenqualität und Architektur\n− Reifegrad von Technologie/System\n− Personelle Ausstattung\n− Veränderungsbereitschaft der Organisation\n\nRisiko\n\nHohe Priorität\nMittlere Priorität\nNiedrige Priorität\n\nWirksamkeit\n\nMachbarkeit\n\nin Anlehnung an McKinsey\n\n61\n\n61\n\n30\n\n','\n',char(10)),'imgs/20240614 IU Vorlesung NIS-2 und KI_30.png');
INSERT INTO pdf_pages VALUES(721,'pdfs/20240614 IU Vorlesung NIS-2 und KI.pdf',31,replace('14.06.2024\n\nHürde Datenschutz?\nKI & Copilot im Einsatz\n\nProjektskizze KI im Sozialwesen\n\n„Responsible AI“\n\nBR/MAV \neinbinden\n\nProjekt-Setup \n(ggf. Beirat berufen)\n\nSensibilisierung \nFührungskräfte\n\nHaltung \ndefinieren\n\nKI-Strategie \nveröffentlichen\n\nVorbereitung \nAI Act\n\nPotentialanalyse \nKI/ML\n\nUmgang mit\n§ 44b UrhG?\n\nPortfolioanalyse\nRoadmap KI/ML\n\nDatenschutz-\nFolgenabschätzung?\n\nGovernance \n& Monitoring\n\nAI Impact \nAssessment?\n\nBestandsaufnahme \nKI/ML\n\nRichtlinie \nUmgang mit KI\n\nSchulung \nMitarbeitende\n\n62\n\n62\n\nAusblick\n\nRisikostufen AI Act\n\nInakzeptabel\n\nHohes \nRisiko\n\nBegrenztes \nRisiko\n\nMinimales\nRisiko\n\nEinsatz von KI verboten\nBeeinflussung, Ausnutzung von Schwäche oder Schutzbedürftigkeit,\nverschiedene Formen von Überwachung\nErlaubt unter Vorgaben\nin den Bereichen kritische Infrastruktur, Bildung, Strafverfolgung, \nMedizinprodukte, etc. mit Konformitätsnachweisen\n\nErlaubt mit Transparenzpflichten\nwie klassische Chatbots oder Technologien, die für die Erstellung von \nDeepfakes geeignet sind; generell KI am Arbeitsplatz\n\nGrundsätzlich erlaubt – nicht weiter reguliert\nwie Spamfilter, Empfehlungsmarketing, etc. müssen nur allgemein \nübliche Auflagen (z. B. die DSGVO) erfüllen\n\nAllgemeine Zwecke\n(General Purpose AI)\n\nBeurteilung anhand von Leistung und Reichweite\nals Level 1 mit Transparenzpflichten für GPAI-Systeme oder \nLevel 2 mit zusätzlichen Pflichten ab 10^25 FLOP\n\n63\n\n63\n\nStand Gesetzgebungsverfahren 04/2024\n\n31\n\n','\n',char(10)),'imgs/20240614 IU Vorlesung NIS-2 und KI_31.png');
