INSERT INTO pdf_pages VALUES(863,'pdfs/DSBDSITS01_0_Begrüßung.pdf',9,replace('WEGWEISER\n\n Folien basieren auf Foliensatz des Dualen Studiums der Vorsemester, welche auf dem Studienskript des Fernstudiums \n\n(s. IU Learn) und dem DSGVO-Text beruhen\n IU Learn bietet damit eine gute Möglichkeit zum Vor- oder Nacharbeiten im eigenen Lerntempo\n Eine Beschäftigung mit dem Originaltext der DSGVO ist sinnvoll\n Feedback bzgl. Geschwindigkeit und didaktischer Qualität, aber dennoch jederzeit (e.g. via Miro) herzlich willkommen\n\n Inhalte sind nach didaktischen Kriterien in Lektionen / Kapitel eingeteilt und Terminen zugeordnet.\n\nGrob je Woche ein (neuer) inhaltlicher Schwerpunkt\n\n Verwendung des generischen Maskulinums zur besseren Lesbarkeit der Folien \n\n Ungeachtet dessen möchte ich hervorheben, dass immer Männer und Frauen, Inter- und Trans*Personen gemeint sind \nsowie auch jene, die sich keinem Geschlecht zuordnen wollen oder können. Bei Wunsch passe ich die Folien an (Miro).\n\n Folien dienen als Strukturierendes Element, sodass\n\n Nicht immer alles der Folien erwähnt oder vorgetragen wird (aber dennoch Relevanz hat)\n Auf der Tonspur Sachverhalte erklärt werden, die dennoch Relevanz haben\n\n04.04.2024\n\n9\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_0_Begrüßung_9.png');
