INSERT INTO pdf_pages VALUES(866,'pdfs/DSBDSITS01_0_Begrüßung.pdf',12,replace('QUALIFIKATIONSZIELE\n\nNach erfolgreichem Abschluss sind die Studierenden in der Lage, \n\n Grundlegende Begriffe und Konzepte der IT-Sicherheit zu kennen und zu wissen, \n\nin welchen Gebieten es welche typischen Verfahren und Techniken gibt\n\n Grundlegende der gesetzlichen Regelungen zum Datenschutz zu kennen\n\n Über vertiefende Kenntnisse zum IT-Sicherheitsmanagement zu verfügen und \n\ngeeignete Maßnahmen zur Umsetzung kennen\n\n Einen Überblick über Aktivitäten und Strategien zur IT-Sicherheit in der Software-\n\nund Systementwicklung zu haben\n\n04.04.2024\n\n12\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_0_Begrüßung_12.png');
