INSERT INTO pdf_pages VALUES(872,'pdfs/DSBDSITS01_0_Begrüßung.pdf',18,replace('DATENSCHUTZ & IT-SICHERHEIT\n\nÜbung: Warum diese Vorlesung?\n\nDiskutiert in den Gruppen warum aus eurer Sicht diese Vorlesung mit den Inhalten jetzt und \nauch in der Zukunft (ggf. noch viel mehr oder auch deutlich weniger) relevant  ist oder sein \nwird.  Gehen Sie dabei folgendermaßen vor:\n1. Überlegen Sie zunächst alleine, wann Sie das letzte Mal mit Datenschutz in Berührung \ngekommen sind. Kennen Sie den Datenschutzbeauftragten Ihres Praxispartners? \n(5 Minuten)\n\n2. Diskustieren Sie in 2er-Gruppen, in welchen Situationen Datenschutz und IT-Sicherheit \n\nRelevanz haben. Clustern Sie die Ergebnisse. \n\n04.04.2024\n\n18\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n(Password: DaSchu_ITS_Berlin) \n\n','\n',char(10)),'imgs/DSBDSITS01_0_Begrüßung_18.png');
