INSERT INTO pdf_pages VALUES(952,'pdfs/20200804_IMT102-01_Repetitorium.pdf',77,replace('Frage\n\nEinführung zu Graphen\n\nFrage 43 \n\nGegeben ist der folgende Graph. Entscheiden Sie, ob die nachstehenden Aussagen richtig oder \nfalsch sind:\n\na)  Der Graph ist ein endlicher Graph.\n\nb)  Der Graph ist ein einfacher Graph.\n\nc)  Der Graph ist ein zusammenhängender Graph.\n\nFrage 44 \n\nGegeben  ist  der  folgende  Graph.  Entscheiden  Sie,  ob  die  nachstehenden  Aussagen  richtig \noder falsch sind:\n\na)  Alle Knoten sind benachbart.\n\nb)  Der Graph verfügt über eine Schlinge.\n\nc)  Der Graph besteht aus zwei Teilgraphen.\n\n77\n\nwww.iubh-fernstudium.de','\n',char(10)),'imgs/20200804_IMT102-01_Repetitorium_77.png');
INSERT INTO pdf_pages VALUES(953,'pdfs/20200804_IMT102-01_Repetitorium.pdf',78,replace('Lösung\n\n78\n\nLösung 43 \n\nLösungshinweis\n\nMachen Sie sich mit den Definitionen der Graphentheorie vertraut.\n\nKurze Lösung\na)  richtig  \n\nb)  falsch\n\nc)  falsch\n\nAusführliche Lösung\na)  Richtig, da es eine endliche Anzahl an Knoten und Kanten gibt.\n\nb)  Falsch, da er eine Schlinge besitzt.\n\nc)  Falsch, da isolierte Knoten existieren.\n\nLösung 44 \n\nLösungshinweis\n\nMachen Sie sich mit den Definitionen der Graphentheorie vertraut.\n\nKurze Lösung\na)  falsch  \n\nb)  richtig\n\nc)  richtig\n\nAusführliche Lösung\na)  Falsch, da es Knoten gibt, die nicht durch eine Kante miteinander verbunden sind. \n\nb)  Richtig, da der Graph über zwei Schlingen verfügt. \n\nc)  Richtig, da zwei Graphen nicht miteinander verbunden sind.\n\nwww.iubh-fernstudium.de','\n',char(10)),'imgs/20200804_IMT102-01_Repetitorium_78.png');
INSERT INTO pdf_pages VALUES(954,'pdfs/20200804_IMT102-01_Repetitorium.pdf',79,replace('Frage\n\nEinführung zu Graphen\n\nFrage 45 \n\nGegeben ist der folgende Graph. Entscheiden Sie, ob die nachstehenden Aussagen richtig oder \nfalsch sind:\n\na)  Der Graph ist ein einfacher Graph.\n\nb)  Es handelt sich um keinen Graphen.\n\nc)  Der Graph ist zusammenhängend.\n\n79\n\nwww.iubh-fernstudium.de','\n',char(10)),'imgs/20200804_IMT102-01_Repetitorium_79.png');
