INSERT INTO pdf_pages VALUES(956,'pdfs/20200804_IMT102-01_Repetitorium.pdf',81,replace('Einführung zu Graphen\n\nFrage\n\n4.2  Weitere Eigenschaften von Graphen\n\nFrage 46 \n\nZeichnen Sie einen Graphen mit vier Knoten, wobei ein Knoten den Grad 1 hat und drei \nKnoten den Grad 3 haben.\n\nFrage 47 \n\nBestimmen Sie den Grad jedes Knotens.\n\nW2\n\nW1\n\nW3\n\n81\n\nwww.iubh-fernstudium.de','\n',char(10)),'imgs/20200804_IMT102-01_Repetitorium_81.png');
INSERT INTO pdf_pages VALUES(957,'pdfs/20200804_IMT102-01_Repetitorium.pdf',82,replace('Lösung\n\n82\n\nLösung 46 \n\nLösungshinweis\n\nVerwenden Sie Mehrfachkanten und Schlingen, falls notwendig.\n\nLösung\nMehrere Lösungen sind möglich.\n\n(Schlinge wird doppelt gezählt).\n\nLösung 47 \n\nLösungshinweis\n\nDie Schlinge wird doppelt gezählt.\n\nKurze Lösung\n\n•  Grad von w1: 1 \n\n•  Grad von w2: 2 \n\n•  Grad von w3: 5\n\nAusführliche Lösung\n\n•  Grad von w1: 1 \n\n•  Grad von w2: 2 \n\n•  Grad von w3: 5 (Schlinge wird doppelt gezählt).\n\nwww.iubh-fernstudium.de','\n',char(10)),'imgs/20200804_IMT102-01_Repetitorium_82.png');
INSERT INTO pdf_pages VALUES(958,'pdfs/20200804_IMT102-01_Repetitorium.pdf',83,replace('Einführung zu Graphen\n\nFrage 48 \n\nWas versteht man darunter, wenn zwei Graphen isomorph sind? Reicht es hierzu aus, wenn sie \ndie gleiche Anzahl an Knoten und Kanten aufweisen?\n\nFrage 49 \n\nSind beide Graphen isomorph? Falls ja, begründen Sie.\n\n83\n\nwww.iubh-fernstudium.deFrage','\n',char(10)),'imgs/20200804_IMT102-01_Repetitorium_83.png');
INSERT INTO pdf_pages VALUES(959,'pdfs/20200804_IMT102-01_Repetitorium.pdf',84,replace('Lösung 48 \n\nLösungshinweis\n\nMachen Sie sich mit der Definition der Isomorphie vertraut.\n\nKurze Lösung\nZwei Graphen sind isomorph, wenn der eine Graph durch Umzeichnung des anderen Graphen \nentsteht; nein\n\nAusführliche Lösung\nZwei Graphen sind isomorph, wenn der eine durch Umzeichnung des anderen entsteht. Die \nKanten dürfen verbogen, gedehnt oder zusammengezogen, aber nicht durchgeschnitten oder \nverknotet  werden.  Für  Isomorphie  ist  es  nicht  ausreichend,  dass  zwei  Graphen  gleich  viele \nKnoten, Kanten und gleiche Grade aufweisen.\n\nLösung 49 \n\nLösungshinweis\n\nMachen Sie sich mit der Definition der Isomorphie vertraut.\n\nKurze Lösung\nJa. Bezeichnen Sie die Kanten und Knoten. \n\nAusführliche Lösung\nDie Graphen sind isomorph. Um Isomorphie erkennbar zu machen, können die Knoten und \nKanten bezeichnet werden. \n\nLösung\n\n84\n\nwww.iubh-fernstudium.de','\n',char(10)),'imgs/20200804_IMT102-01_Repetitorium_84.png');
