INSERT INTO pdf_pages VALUES(1003,'pdfs/20200804_IMT102-01_Repetitorium.pdf',128,replace('Lösung\n\nLösung 78 \n\nLösungshinweis\n\nZeichnen Sie \nWeg bzw. ein Eulerscher Kreis vorliegt?\n\n5\n\n. Welche Voraussetzungen müssen gegeben sein, damit ein Eulerscher \n\nW\n\nLösung\n ist weder ein Eulerscher Weg noch ein Eulerscher Kreis. Er hat vier Knoten \nDer Radgraph \n. Voraussetzung für einen Eulerschen Weg sind max. zwei Knoten \nmit dem ungeraden Grad \nmit ungeradem Grad. Voraussetzung für einen Eulerschen Kreis sind nur Knoten mit geradem \nGrad.\n\nW\n\n5\n\n3\n\nLösung 79 \n\nLösungshinweis\n\nZeichnen Sie den vollständigen Graphen. Welche Voraussetzungen müssen gegeben sein, \ndamit ein Eulerscher Kreis vorliegt?\n\nLösung\nJa, da er nur Knoten mit geradem Knotengrad besitzt. Alle vollständigen Graphen mit \nrade sind eulersch.\n\n unge-\n\nn\n\n128\n\nwww.iubh-fernstudium.de','\n',char(10)),'imgs/20200804_IMT102-01_Repetitorium_128.png');
