INSERT INTO pdf_pages VALUES(1017,'pdfs/20200804_IMT102-01_Repetitorium.pdf',142,replace('Lösung 90 \n\nLösungshinweis\n\nEin- und Ausgangsgrad müssen übereinstimmen.\n\nKurze Lösung\nZusätzliche Pfeile sind \n\n und \n\n; Eulerscher Digraph: \n\n(D, B)\n\n(B, C)\n\nABCDBEDBCA\n\nAusführliche Lösung\nBei den Knoten \nein Pfeil ankommen, bei \nD\n\n nach \n\n und \n\nC\n\n stimmen Ein- und Ausgangsgrad nicht überein. Bei \n\n müsste noch \n ein Pfeil abgehen. Also wird der kürzeste gerichtete Kantenzug von \n\n gesucht. Dieser führt über \n\n mit der Länge \n\n. Die zusätzlichen Pfeile sind also \n\nC\n\n. Ein Eulerscher Kreis im modifizierten Eulerschen Digraphen ist \n\nD\n\nB\n\n9\n\n.\n(D, B)\nABCDBEDBCA\n\nund \nD\n\nC\n(B, C)\n\n142\n\nLösungwww.iubh-fernstudium.de \n','\n',char(10)),'imgs/20200804_IMT102-01_Repetitorium_142.png');
