INSERT INTO pdf_pages VALUES(1043,'pdfs/20200804_IMT102-01_Repetitorium.pdf',168,replace('Lösung 108 \n\nLösungshinweis\n\nHamiltonscher Kreis mit minimaler Gewichtung\n\nKurze Lösung\nZ. B. \n\n mit dem Gesamtgewicht \n\nADBCA\nAusführliche Lösung\nEs ist ein Hamiltonscher Kreis zu suchen mit dem geringsten Gesamtgewicht. Eine mögliche \nLösung lautet \n\n mit dem Gesamtgewicht \n\n11\n\n.\n\nADBCA\n\nLösung 109 \n\n11\n\nLösungshinweis\n\nHamiltonscher Kreis mit minimaler Gewichtung\n\nKurze Lösung\n\n mit dem Gesamtgewicht \n\nZ. B. AEDCBA\nAusführliche Lösung\nEs ist ein Hamiltonscher Kreis zu suchen mit dem geringsten Gesamtgewicht. Eine mögliche \nLösung lautet \n\n mit dem Gesamtgewicht \n\n19\n\n.\n\nAEDCBA\n\n19\n\nLösung\n\n168\n\nwww.iubh-fernstudium.de','\n',char(10)),'imgs/20200804_IMT102-01_Repetitorium_168.png');
