INSERT INTO pdf_pages VALUES(1051,'pdfs/20200804_IMT102-01_Repetitorium.pdf',176,replace('Lösung 113 \n\nLösungshinweis\n\nEin Graph mit n Knoten hat \n\n Kanten.\n\nLösung\nDer Graph enthält sieben Knoten und sechs Kanten, daher muss er ein Baum sein.\n\nn – 1\n\nLösung 114 \n\nLösungshinweis\n\nEin Graph mit n Knoten hat \n\n Kanten.\n\nn – 1\n\nLösung\nDer Graph enthält neun Knoten und neun Kanten, daher kann er kein Baum sein. Darüber \nhinaus ist ersichtlich, dass er einen geschlossenen Kantenzug enthält.\n\nLösung\n\n176\n\nwww.iubh-fernstudium.de','\n',char(10)),'imgs/20200804_IMT102-01_Repetitorium_176.png');
