INSERT INTO pdf_pages VALUES(1055,'pdfs/20200804_IMT102-01_Repetitorium.pdf',180,replace('Lösung 116 \n\nLösungshinweis\n\nBei einem ausgefüllten Wurzelbaum haben alle inneren Knoten die höchstmögliche \nAnzahl an Kindern, wodurch der Grad entweder gleich der Ordnung des Baumes \n(innerer Knoten) oder aber null (Blatt) ist. Ein vollständiger Wurzelbaum hat auf jedem \nNiveau die höchstmögliche Anzahl an Knoten, wenn alle inneren Knoten zuvor die \nmaximale Anzahl an Kindern hatten. Jeder vollständige Wurzelbaum ist auch ausgefüllt.\n\nKurze Lösung\nEr ist nicht ausgefüllt und damit auch nicht vollständig.\n\nAusführliche Lösung\nDer angegebene Wurzelbaum ist nicht ausgefüllt, da der Knoten auf Tiefe \nhat. Damit ist der Wurzelbaum auch nicht vollständig.\n\n nicht genug Kinder \n\n1\n\nLösung 117 \n\nLösungshinweis\n\nBei einem ausgefüllten Wurzelbaum haben alle inneren Knoten die höchstmögliche \nAnzahl an Kindern, wodurch der Grad entweder gleich der Ordnung des Baumes \n(innerer Knoten) oder aber null (Blatt) ist. Ein vollständiger Wurzelbaum hat auf jedem \nNiveau die höchstmögliche Anzahl an Knoten, wenn alle inneren Knoten zuvor die \nmaximale Anzahl an Kindern hatten. Jeder vollständige Wurzelbaum ist auch ausgefüllt.\n\nKurze Lösung\nEr ist vollständig und ausgefüllt. \n\nAusführliche Lösung\nEr ist vollständig, da es auf jedem Niveau zwei Kinder gibt und er damit ausgefüllt ist.\n\nLösung\n\n180\n\nwww.iubh-fernstudium.de','\n',char(10)),'imgs/20200804_IMT102-01_Repetitorium_180.png');
