INSERT INTO pdf_pages VALUES(1081,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',5,replace('VON DER ANALYSE ZUR ZIELDEFINITION\n\nFunktion von Zielen \n\nOrientierungs- und Lernfunktion \nKontrollfunktion\nMotivationsfunktion\n\nAnforderungen an Zielstellungen \n\nZielinhalt (Was soll erreicht werden?)\nZielausmaß (Wie viel soll erreicht werden?) \nZielhorizont (Bis wann soll es erreicht werden?) \nGeltungsbereich (Wo soll es erreicht werden?) \n\nQuelle: Vgl. Bruhn, M. (2022): Marketing - Grundlagen für Studium und Praxis, 15. Auflage\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n5 0\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_5.png');
INSERT INTO pdf_pages VALUES(1082,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',6,replace('VON DER ANALYSE ZUR ZIELDEFINITION\n\nAusgangspunkt für strategische Entscheidungen sind die strategische Ziele eines\nUnternehmens.\n\nUnternehmensziele\n\n „Wunschorte“, also Orientierungs- bzw. Richtgrößen für unternehmerisches Handeln\nAntwort auf die Frage: Wo wollen wir hin?\nAngestrebte Zustände bzw. Ereignisse, die durch unternehmerische Maßnahmen erreicht\nwerden sollen\nIn Abgrenzung von Strategie, d.h. geplante Verhaltensweise, um die (langfristig) gesetzten\nZiele zu realisieren\n\nQuelle: Vgl. Bruhn, M. (2022): Marketing - Grundlagen für Studium und Praxis, 15. Auflage\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n5 1\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_6.png');
