INSERT INTO pdf_pages VALUES(1087,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',11,replace('STRATEGISCHE ZIELE\n\nunternehmensgerichtete Ziele\n\nkundengerichtete Ziele\n\nwettbewerbsgerichtete Ziele\n\nvorwiegend ökonomische-\nund Marktstellungsziele\nspiegeln den ökonomischen\nErfolg des Dienstleistungs-\nunternehmens wieder\nDazu gehören zum Beispiel\n\nAbsatz\nMarktanteil\nDeckungsbeitrag \nUmsatz\nGewinn \n\nalle Ziele, die aktuelle und\npotentielle externe\nZielgruppen ansprechen\nlassen sich in psychologische,\nverhaltensbezogene und\nökonomische Ziele unterteilen \nDazu gehören zum Beispiel\nBekanntheit und Image \nQualitätswahrnehmung\nKundenzufriedenheit \nBeziehungsqualität\n\nQuelle: Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\ndienen der Abgrenzung\ngegenüber Hauptkonkurrenten\nDazu gehören zum Beispiel: \n\nTechnologie- und\nInnovationsführerschaft\nPreisniveau\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n5 6\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_11.png');
