INSERT INTO pdf_pages VALUES(1096,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',20,replace('VON DER ZIELDEFINITION ZUR STRATEGIE\n\nDynamische und komplexe Umfeld- und Marktkonstellationen erfordern ein planvoll gesteuertes\nMarketinghandeln von den Unternehmen. \n\nUnternehmen benötigen aus diesem Grund umfassende Handlungsanweisungen um ein markt- bzw.\nkundenbezogenes Handeln zu realisieren.\n\nMarketing-Konzeptionen bilden die Grundlage eines planvollen, ganzheitlichen unternehmerischen\nHandelns. \n\nDiese „Fahrplan-Funktion“ können Konzeptionen vor allem dann erfüllen, wenn sie auf abgestimmten\nEntscheidungen auf allen drei Konzeptionsebenen (Ziel-, Strategie- und Mixebene) beruhen.\n\nZur Umsetzung der Unternehmens- und Marketingziele wird eine Marketingstrategie formuliert.\n\nQuelle: Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n6 6\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_20.png');
INSERT INTO pdf_pages VALUES(1097,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',21,replace('"Marketingstrategien sind bedingte, mehrere\nPlanungsperioden umfassende, verbindliche\nVerhaltenspläne von Unternehmen für\nausgewählte Planungsobjekte. Sie beinhalten\nEntscheidungen zur Marktwahl und\n-bearbeitung und legen den Weg fest, wie\nstrategische Marketingziele eines\nUnternehmens zu erreichen sind."\n\nQuelle: Bruhn, M. (2022): Marketing - Grundlagen für Studium und Praxis, 15. Auflage\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_21.png');
INSERT INTO pdf_pages VALUES(1098,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',22,replace('TYPEN VON MARKETINGSTRATEGIEN\n\nMarktbezogene Strategien (integrativer Ansatz von Becker) \n\nMarktfeldstrategien: Produkt-Markt-Matrix (Ansoff)\nMarktstimulierung (nach Porter)\nMarktparzellierungsstrategie\nMarktarealstrategie \n\nAbnehmergerichtete Strategien\nKonkurrenzgerichtete Strategien \nAbsatzmittlergerichtete Strategien \nGesellschaftsorientierte Strategien \n\nQuelle: Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n6 8\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_22.png');
INSERT INTO pdf_pages VALUES(1099,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',23,replace('TYPEN VON MARKETINGSTRATEGIEN\n\nMarktbezogene Strategien (integrativer Ansatz von Becker) \n\nMarktfeldstrategien: Produkt-Markt-Matrix (Ansoff)\nMarktstimulierung (nach Porter)\nMarktparzellierungsstrategie\nMarktarealstrategie \n\nAbnehmergerichtete Strategien\nKonkurrenzgerichtete Strategien \nAbsatzmittlergerichtete Strategien \nGesellschaftsorientierte Strategien \n\nQuelle: Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n6 9\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_23.png');
INSERT INTO pdf_pages VALUES(1100,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',24,replace('MARKTBEZOGENE STRATEGIEN\n\nStrategieebene\n\nArt der strategischen Festlegung \n\nMarktfeldstrategien\n\nFixierung der Produkt/Markt- Kombinationen (Ansoff-Matrix) \n\nMarktstimulierungsstrategien \n\nBestimmung der Art und Weise der Marktbeeinflussung\n(Präferenz oder Preis-Mengen-Strategie) \n\nMarktparzellierungsstrategien\n\nMarktsegmentierung und Festlegung von Art bzw. Grad der\nDifferenzierung der Marktbearbeitung \n\nMarktarealstrategien\n\nBestimmung des Markt- bzw. Absatzraumes\n\nQuelle: Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n7 0\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_24.png');
INSERT INTO pdf_pages VALUES(1101,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',25,replace('MARKTFELDSTRATEGIEN\n\nDie Markfeldstrategie fixiert, welche bestehenden bzw. neuen Produkte ein Unternehmen auf\nwelchen bestehenden bzw. neuen Märkten anbieten wird, um ein Wachstum zu erreichen. \n\nIn der Praxis kommt hier sehr häufig die Produkt-Markt-Matrix (oder auch Ansoff-Matrix) zur\nAnwendung. \n\nSie ist ein Hilfsmittel zur Planung und dient als Denkraster zur Entwicklung einer\nWachstumsstrategie. \n\nDie Produkt-Markt-Matrix betrachtet die Potenziale und Risiken von vier möglichen Produkt-Markt-\nKombinationen. \n\nQuelle: Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n7 1\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_25.png');
INSERT INTO pdf_pages VALUES(1102,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',26,replace('PRODUKT-MARKT-MATRIX (NACH ANSOFF)\n\nMÄRKTE\n\nVORHANDEN\n\nNEU\n\nMarktdurchdringung\n\nMarktentwicklung\n\nProduktentwicklung\n\nDiversifikation\n\nN\nE\nD\nN\nA\nH\nR\nO\nV\n\nU\nE\nN\n\nE\nT\nK\nU\nD\nO\nR\nP\n\nQuelle: Vgl. Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n7 2\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_26.png');
INSERT INTO pdf_pages VALUES(1103,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',27,replace('MARKTDURCHDRINGUNG\n\nDie Strategie der Marktdurchdringung wird bei einem bereits\nbestehenden Produkt auf einem bereits bestehenden Markt\nangewendet.\nDas große Ziel ist hierbei die Umsatzsteigerung des Produktes\ngenau auf dem Markt, auf dem es bereits aktiv ist.\nHier gibt es verschiedene Möglichkeiten:\n\nPreissenkungen\nMarketingkampagnen\nOptimierung der Prozesse oder das Verkaufen von\ngrößeren Einheiten kann dazu führen, dass der Marktanteil\ndes bereits bestehenden Produktes erhöht wird.\n\nDer große Vorteil ist hierbei, dass das Risiko sehr gering ist.\n\nQuelle: Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\n2 0   %\n\nK U R Z F R I S T I G E   R E D U Z I E R U N G   D E S   P R E I S E S   U M\n2 0 % .   D I E S E   P R E I S S E N K U N G   V E R M I T T E L N   S I E\nD U R C H   E I N E   N E U E   M A R K E T I N G K A M P A G N E   M I T\nD E M   Z I E L ,   M E H R   K U N D E N   A U F   D A S   P R O D U K T\nA U F M E R K S A M   Z U   M A C H E N .   D A D U R C H   K Ö N N E N   S I E\nD E N   A B S A T Z   E R H Ö H E N   U N D   L A N G F R I S T I G   T R E U E\nK U N D E N   G E W I N N E N .\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n7 3\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_27.png');
INSERT INTO pdf_pages VALUES(1104,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',28,replace('MARKTENTWICKLUNG\n\nBei der Marktentwicklung, auch Markterschließung, geht es um die\nErschließung neuer Märkte mit einem bereits bestehenden Produkt.\nMan kann beispielsweise sein Produkt in neue Länder und Regionen\neinführen oder auch eine neue Zielgruppe definieren.\nWichtig bei der Marktentwicklungsstrategie ist, dass im Vorhinein\nanalysiert wird, ob das Produkt auch für die Neukunden oder die neuen\nZielländer geeignet ist. Eventuell müssen hier geringe Modifikationen\nvorgenommen werden, das Grundprodukt sollte jedoch weiterhin\nbestehen bleiben.\nDie Kosten für die Produktentwicklung sind daher bei der\nMarktentwicklung etwas höher als bei der Marktdurchdringung.\nViel wichtiger ist, dass das Produkt in den neuen Märkten beworben\nwerden muss, deshalb sind die Marketingkosten hier dementsprechend\nhoch. Dies erhöht damit auch das Risiko dieser Strategie.\n\nQuelle: Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n7 4\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_28.png');
INSERT INTO pdf_pages VALUES(1105,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',29,replace('PRODUKTENTWICKLUNG\n\nBei der Produktentwicklung bzw. Produktmodifikation geht es\ndarum, ein neues Produkt für die bestehenden Kunden zu\nentwickeln. \nDer größte Vorteil der Produktentwicklungsstrategie ist, dass\nUnternehmen bereits in den Märkten aktiv sind. Vertriebskanäle,\nMarketingkanäle, Logistik, etc. sind bereits bekannt. Dies\nerleichtert wesentlich die operativen Rahmenbedingungen für\nden Verkauf der neuen Produkte.\nWesentlich höher fallen hier jedoch die Entwicklungskosten aus.\nImmerhin muss an dem neuen Produkt geforscht werden, es\nmuss entwickelt werden und auch Marktumfragen müssen\noftmals durchgeführt werden. Natürlich lohnen sich diese Kosten,\nwenn das Produkt erfolgreich vom Markt angenommen wird.\nDennoch ist damit ein wesentliches Risiko verbunden.\n\nQuelle: Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\n  E I N E   O F F E N S I C H T L I C H E   M E T H O D E   I S T   H I E R B E I ,\nE I N E   N E U E   G E S C H M A C K S R I C H T U N G   A N   C H I P S   Z U\nE N T W I C K E L N .   H I E R   G I B T   E S   S E H R   V I E L E\nM Ö G L I C H K E I T E N   U N D   M A N   K A N N   S I C H   E N T W E D E R\nA N   D E R   K O N K U R R E N Z   O R I E N T I E R E N   O D E R   N E U E\nG E S C H M Ä C K E R   A U S T E S T E N . .\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n7 5\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_29.png');
INSERT INTO pdf_pages VALUES(1106,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',30,replace('DIVERSIFIKATION\n\nBei der Diversifikation geht es darum, ein neues Produkt auf einem\nneuen Markt einzuführen.\nHierbei handelt es sich um die risikoreichste Strategie. Immerhin muss\nan dem neuen Produkt geforscht werden, der neue Markt muss\nanalysiert und auch erschlossen werden.\nMit den Risiken kommen aber auch sehr viele Chancen. Denn ist die\nDiversifikationsstrategie erfolgreich, erhöht dies wesentlich den Umsatz\ndes Unternehmens.\nZudem wurde nun ein zweiter Einkommensstrom in einem neuen\nAbsatzmarkt entwickelt, der völlig unabhängig von der Entwicklung des\nersten Einkommensstroms ist. Gerade wenn Unternehmen in einem\nvolatilen Markt tätig sind, kann die Diversifikationsstrategie sinnvoll sein,\ndenn somit kann ein konstanter Umsatz gesichert werden.\n\nQuelle: Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n7 6\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_30.png');
INSERT INTO pdf_pages VALUES(1107,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',31,replace('DIVERSIFIKATION\n\nHORIZONTALE DIVERSIFIKATION\n\nVERTIKALE DIVERSIFIKATION\n\nLATERALE DIVERSIFIKATION\n\nBei der horizontalen\nDiversifikation geht es darum,\ndas eigene\nProduktsortiment zu\nerweitern. Dabei wird meist\nein Produkt der gleichen\nMarktstufe aufgenommen.\nVorteil: neue Kundengruppen\nerschließen, vorhandes\nWissen kann genutzt werden\nRisiko: gering\n\nBei dieser Strategie wird ein\nUnternehmen auf den im\nWertschöpfungsprozess vor-\nund nachgelagerten Stufen\ntätig, die er vorher extern hat\ndurchführen lassen.\nVorteil: Sicherung von\nRohstoffen oder Absatzwegen\nRisiko: völlig neue\nKundengruppen und\nGeschäftszweige\n\nHier geht es darum, komplett\nneue Produkte für eine\nkomplett neue Zielgruppe zu\nentwickeln.\nDas Produktsortiment wird\nhierbei um eine vollkommen\nneue Branche erweitert.\nVorteil: Wachstumssicherung,\nUnabhängigkeit\nRisiko: sehr hoch\n\nQuelle: Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n7 7\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_31.png');
INSERT INTO pdf_pages VALUES(1108,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',32,replace('WACHSTUMSSTRATEGIEN\nREIHENFOLGE DER WACHSTUMSSTRATEGIEN NACH ANSOFF\n\nDiese Strategie kann vorteilhaft für Unternehmen sein, deren Stärke sich eher auf einen\nspezifischen Kundenkreis bezieht, als auf spezifische Produkte.\n\nQuelle: Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n7 8\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_32.png');
INSERT INTO pdf_pages VALUES(1109,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',33,replace('WACHSTUMSSTRATEGIEN\nREIHENFOLGE DER WACHSTUMSSTRATEGIEN NACH ANSOFF\n\nDiese Strategie ist empfehlenswer für Unternehmen, die ihre Kompetenzen eher auf ein\nspezifisches Produkt ausgerichtet haben , als auf einen spezifischen Markt.\n\nQuelle: Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n7 9\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_33.png');
INSERT INTO pdf_pages VALUES(1110,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',34,replace('WACHSTUMSSTRATEGIEN\n\nQuelle: Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n8 0\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_34.png');
INSERT INTO pdf_pages VALUES(1111,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',35,replace('BEISPIEL PORSCHE\n\nMÄRKTE\n\nVORHANDEN\n\nNEU\n\nPorsche für\nKleinwagenmarkt\n\nMarktdurchdringung\n\nMarktentwicklung\n\nN\nE\nD\nN\nA\nH\nR\nO\nV\n\nU\nE\nN\n\nE\nT\nK\nU\nD\nO\nR\nP\n\nProduktentwicklung\n\nDiversifikation\n\nQuelle: Vgl. Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n8 1\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_35.png');
INSERT INTO pdf_pages VALUES(1112,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',36,replace('MARKTBEZOGENE STRATEGIEN\n\nStrategieebene\n\nArt der strategischen Festlegung \n\nMarktfeldstrategien\n\nFixierung der Produkt/Markt- Kombinationen (Ansoff-Matrix) \n\nMarktstimulierungsstrategien \n\nBestimmung der Art und Weise der Marktbeeinflussung\n(Präferenz oder Preis-Mengen-Strategie) \n\nMarktparzellierungsstrategien\n\nMarktsegmentierung und Festlegung von Art bzw. Grad der\nDifferenzierung der Marktbearbeitung \n\nMarktarealstrategien\n\nBestimmung des Markt- bzw. Absatzraumes\n\nQuelle: Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n8 2\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_36.png');
INSERT INTO pdf_pages VALUES(1113,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',37,replace('MARKTSTIMULIERUNGSSTRATEGIEN\n\nBei Marktstimulierung spricht man von von der Art und Weise der Maktbeeinflussung bzw.                        \n- stimulierung im Sinne der zuvor definierten Marketingziele. Hier geht es um die Frage, wie die\nNachfrage stimuliert werden kann und soll.\n\nzwei Grundausrichtungen zur Stimulierung des Marktes werden unterschieden:\n\nPreis-Mengen-Strategie (Strategie der Kostenführerschaft)\nPräferenzstrategie (Differnzierungstrategie)\n\nEtablierte Märkte sind in der Regel mehrschichtig (drei Schichten), aufgrund der Mehrschichtigkeit\nkönnen beide Strategien zum Einsatz kommen. \n\nBei der Bearbeitung dieser mehrschichtigen Märkte, können diese Strategien über die verschiedenen\nSchichten ineinander übergehen. \n\nQuelle: Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n8 3\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_37.png');
INSERT INTO pdf_pages VALUES(1114,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',38,replace('MARKTSTIMULIERUNGSSTRATEGIEN\n\nSTRATEGISCHER VORTEIL\n\nSingularität aus Käufersicht\n\nKostenvorsprung\n\nDifferenzierung durch\nhohe Qualität\n\nUmfassende\nKostenführerschaft\n\n“Präferenzstrategie”\n\n“Preis-Mengen-\nStrategie”\n\nKonzentration auf Schwerpunkte \n(einzelne Leistungen günstig anbieten)\n\n“Vorteils-Strategie”\n\nBranchenweit\n\nSTRATEGISCHES\nZIELOBJEKT\n\nBeschränkung\nauf ein Segement\n\nQuelle: Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n8 4\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_38.png');
INSERT INTO pdf_pages VALUES(1115,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',39,replace('MARKTSTIMULIERUNGSSTRATEGIEN\n\nPreis-Mengen-Strategie\n\nPräferenzstrategie\n\nauch Strategie der Kostenführerschaft\nzielt auf die Erreichung der günstigsten\nKostenposition in der Branche ab\nEine solche Kostenposition ermögliht dem\nAnbieter einen größeren Spielraum bei der\nPreisgestaltung\nTypische Merkmale:\n\naggressive Niedrigpreispolitik\nweitestgehenden Standardisierung des\nLeistungsangebotes\nNutzung effizienter Vertriebswege\nBetonung attraktiver Preise\n\nauch Differenzierungsstrategie\nzielt auf eine leistungsbezogene Überlegenheit\ndes Unternehmens ab\nbei der Differenzierung auf Basis überlegener\nProdukte wird angestrebt, Produkte eines\nUnternehmens so zu gestalten, dass sie vom\nKunden im Konkurrenzvergleich als einzigartig\nwahrgenommen werden\nTypische Mermale:\n\nIntensive Markenpflege\ngehobenes Preisniveau\naufwändiges Produktdesing\n\nQuelle: Vgl. Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n8 5\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_39.png');
INSERT INTO pdf_pages VALUES(1116,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',40,replace('MARKTSTIMULIERUNGSSTRATEGIEN\nFÜNF WETTBERWERBSSTRATEGIEN NACH PORTER\n\nGESAMTMARKT\n\nZIELGRUPPE\n\nTEILMARKT\n\nKostenführerschaft\n\nDifferenzierung\n\nIntegrierte\nKostenführerschaft\nund Differenzierung\n\nFokussierte\nKostenführerschaft\n\nFokussierte\nDifferenzierung\n\nGÜNSTIG\n\nEINZIGARTIG\n\nWETTBEWERBSVORTEIL\n\nQuelle: Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n8 6\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_40.png');
INSERT INTO pdf_pages VALUES(1117,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',41,replace('MARKTSTIMULIERUNGSSTRATEGIEN\n\nKostenführerschaft\n\nDifferenzierung\n\nIntegrierte Kostenführerschaft und Differenzierung\n\nQuelle: Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n8 7\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_41.png');
INSERT INTO pdf_pages VALUES(1118,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',42,replace('MARKTSTIMULIERUNGSSTRATEGIEN\n\nStimulierung\nüber günstige\nPreise\n\nKostenführerschaft\n\nDifferenzierung\n\nStimulierung über\nProdukt/Leistung\n\nIntegrierte Kostenführerschaft und Differenzierung\n\nUnd hier?\nOft “stuck in the\nmiddle”\n\nQuelle: Homburg, C; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n8 8\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_42.png');
INSERT INTO pdf_pages VALUES(1119,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',43,replace('MARKTSTIMULIERUNGSSTRATEGIEN\n“STUCK-IN-THE-MIDDLE” PROBLEMATIK\n\nProdukte im Mittellagenbereich sind\nproblematisch, da sie sich weder durch eine\nhervorragende Leistung, noch durch einen\ngünstigen Preis auszeichnen.\n\nQuelle: Homburg, C.; (2020) Marketingmanagement - Strategie, Instrumente, Umsetzung, Unternehmensführung\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n8 9\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_43.png');
