INSERT INTO pdf_pages VALUES(1121,'pdfs/Modul Marketing_Marketingstrategien und -ziele_Teil 2.pdf',45,replace('EINE GUTE STRATEGIE...\n\n...basiert auf einer klaren Analyse und Definition des Status Quo.\n...berücksichtigt Rahmenbedingungen (z. B. Markt und Wettbewerb) und ihre Veränderungen über\ndie Zeit.\n...beschreibt, was das Unternehmen seinen Kunden in Zukunft anbieten wird und wie es sich vom\nWettbewerb abheben wird.\n...formuliert ein klares Ziel für ein bestimmtes Zeitfenster und diese Ziele sind ehrgeizig und\nerreichbar.\n...definiert (eine begrenzte Anzahl von) Prioritäten, um diese Ziele zu erreichen und gibt Orientierung\nfür Entscheidungen.\n...beschreibt, was das Unternehmen tut und WAS ES NICHT TUT.\n...kann man in wenigen Worten erklären.\n\nI U   I N T E R N A T I O N A L E   H O C H S C H U L E\n\n9 1\n\n','\n',char(10)),'imgs/Modul Marketing_Marketingstrategien und -ziele_Teil 2_45.png');
