INSERT INTO pdf_pages VALUES(1128,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',3,replace('VORSTELLUNG\n\n03.04.2024\n\n3\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\nWer bin ich?\n\nKnut Linke\n\n−Studium WI & BWL in der EU & den USA\n−Seit 2000 beruflich in der IT und mit Bezug Internet\n−Professor für Informatik an der IU in Hannover\n\n−Fun Fact: Taucht lieber als Fallschirm springen\n−Vorkenntnisse: Produktmanagement, div. Tätigkeiten \nim Bereich Onlinemarketing, Digitalisierungsberater\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_3.png');
