INSERT INTO pdf_pages VALUES(1159,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',34,replace('AGENDA\n\nEinführung in die Softwarequalitätssicherung\n\nOrganisation und Planung von Softwarequalität\n\nKonstruktives Qualitätsmanagement \n\nStatische Qualitätssicherung: Begutachtung und Messen\n\nDynamische Qualitätssicherung: Testen\n\nSystematisches Testen von Software\n\nSystematische Qualitätssicherung von Anforderungen, Architekturen \nund Prozessen\n\n03.04.2024\n\n34\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n1\n\n2\n\n3\n\n4\n\n5\n\n6\n\n7\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_34.png');
