INSERT INTO pdf_pages VALUES(1161,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',36,replace('GRUPPENIMPULS\n\nKleingruppendiskurs:\n\nWelche Methoden zur Qualitätssicherung in der \nSoftwareentwicklung setzt ihr ein?\n\nBerücksichtigt auch das Ziel des QS\n\n▪Ergebnisse werden von ausgewählten (freiwilligen) Gruppen im Plenum vorgestellt \n(PowerPoint o. ä.)\n▪20 Minuten Zeit\n\n03.04.2024\n\n36\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_36.png');
