INSERT INTO pdf_pages VALUES(1223,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',98,replace('LERNZIELE\n\n▪Aktivitäten und Maßnahmen konstruktive Qualitätssicherung \n\n▪5-Why-Methode \n\n▪Timeboxing und Checklisten \n\n03.04.2024\n\n101\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_98.png');
INSERT INTO pdf_pages VALUES(1224,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',99,replace('ÜBERBLICK ÜBER KONSTRUKTIVE QUALITÄTSSICHERUNG\n\n1. Die Grundidee des Konstruktiven Qualitätsmanagements ist, dass \ndie Qualität des Produktes von der Qualität des Erstellungsprozess \nabhängig ist. \n\n2. Das Ziel? Fehlervermeidung bei Aktivitäten zur Konstruktion, zum \n\nBetrieb und der Weiterentwicklung von Softwaresystemen\n\n3. Teil der Qualitätsplanung ist es die konstruktiven Maßnahmen zu \n\nplanen \n\n03.04.2024\n\n102\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_99.png');
INSERT INTO pdf_pages VALUES(1225,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',100,replace('GESAMMELTE MAßNAHMEN KONSTRUKTIVE \nQUALITÄTSSICHERUNG\n\nMaßnahmen\n\nZwischenmenschliche \nMaßnahmen\n\nTechnische \nMaßnahmen\n\nOrganisatorische \nMaßnahmen\n\nQualifizierung\n\nFrameworks\n\nTemplates \n\nArbeitsumfeld\n\nSprachen\n\nChecklisten und \nRichtlinien\n\nGemeinsame \nAktivitäten\n\nWerkzeuge\n\nStandards\n\nSchiffmann, J. (2022). Qualitätssicherung im Softwareprozess – Konstruktivistisches Qualitätsmanagement. IU.\n\n03.04.2024\n\n103\n\nFußzeile\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_100.png');
INSERT INTO pdf_pages VALUES(1226,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',101,replace('AKTIVITÄTEN FÜR EINE KONSTRUKTIVE QUALITÄTSSICHERUNG\n\nMaßnahmen \neinführen\n\nAktivitäten \nanpassen\n\nEinführung \nkontrollieren\n\nSchiffmann, J. (2022). Qualitätssicherung im Softwareprozess – Konstruktivistisches Qualitätsmanagement. IU.\n\n03.04.2024\n\n104\n\nFußzeile\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_101.png');
INSERT INTO pdf_pages VALUES(1227,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',102,replace('AKTIVITÄTEN FÜR EINE KONSTRUKTIVE QUALITÄTSSICHERUNG\n\n1. Einsatz eines Vorgehensmodells, das in jeder Phase explizit eine \nRisikoabschätzung für den Entwicklungsprozess vorsieht; \n\n2. Verwendung eines Werkzeugs zur Konfigurationsverwaltung von \nQuell- und Objektcode; \n\nSchiffmann, J. (2022). Qualitätssicherung im Softwareprozess – Konstruktivistisches Qualitätsmanagement. IU.\n\n03.04.2024\n\n105\n\nFußzeile\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_102.png');
INSERT INTO pdf_pages VALUES(1228,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',103,replace('AKTIVITÄTEN FÜR EINE KONSTRUKTIVE QUALITÄTSSICHERUNG\n\n3. Dokumentenmuster für einen Qualitätsmanagementplan mit \nvorgegebenem Gliederungsschema und einer Anleitung für das \nAusfüllen, das sicherstellt, dass alle wichtigen Punkte behandelt \nwerden; \n\n4. Aufstellen von Programmierrichtlinien, die unter anderem die \nsprechende Benennung von Variablen fordern und Unit-Tests für \njede fachliche Funktion im Programmcode verpflichtend macht.\n\nSchiffmann, J. (2022). Qualitätssicherung im Softwareprozess – Konstruktivistisches Qualitätsmanagement. IU.\n\n03.04.2024\n\n106\n\nFußzeile\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_103.png');
INSERT INTO pdf_pages VALUES(1229,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',104,replace('DIE 5-WHY METHODE\n\nDie Kernidee dieser Analysetechnik kann im folgenden Satz auf den \nPunkt gebracht werden:\n\nBei einem Problem 5x fragen, warum das Problem existiert\n\n→ Zu einem Fehler soll fünfmal hintereinander die Frage „Warum“ \ngestellt werden, dann hat man sehr wahrscheinlich die ursprüngliche \nUrsache gefunden.\n\n03.04.2024\n\n107\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_104.png');
INSERT INTO pdf_pages VALUES(1230,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',105,replace('ROOT CAUSE ANALYSE\n\nIm Rahmen einer Root Cause-Analyse \n(deutsch sinngemäß: Analyse der \nursprünglichen Ursache) wird versucht, die \neigentliche Fehlerursache von \nFehlersymptomen zu identifizieren.\n\n03.04.2024\n\n108\n\nFußzeile\n\nSammlung aller verfügbaren Informationen\n\nAnwenden der 5-Why-Methode\n\nIdentifikation der Stellen im Programmcode\n\nIdentifikation (konstruktive und analytischer) QS-Maßnahmen, um \nFehler zukünftig zu vermeiden\n\nEinführung der QS-Maßnahmen\n\nEvaluation\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_105.png');
INSERT INTO pdf_pages VALUES(1231,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',106,replace('ROOT CAUSE ANALYSE\n\nSammlung aller verfügbaren Informationen\n\nAnwenden der 5-Why-Methode\n\nIdentifikation der Stellen im Programmcode\n\nIdentifikation (konstruktive und analytischer) QS-Maßnahmen, um Fehler zukünftig zu vermeiden\n\nEinführung der QS-Maßnahmen\n\nEvaluation\n\n03.04.2024\n\n109\n\nFußzeile\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_106.png');
INSERT INTO pdf_pages VALUES(1232,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',107,replace('DIE 5-WHY METHODE\n\nDie Kernidee dieser Analysetechnik kann im folgenden Satz auf den \nPunkt gebracht werden:\n\nBei einem Problem 5x fragen, warum das Problem existiert\n\n→ Zu einem Fehler soll fünfmal hintereinander die Frage „Warum“ \ngestellt werden, dann hat man sehr wahrscheinlich die ursprüngliche \nUrsache gefunden.\n\n03.04.2024\n\n110\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_107.png');
INSERT INTO pdf_pages VALUES(1233,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',108,replace('BEISPIELE: 5-WHY METHODE\n\n1. Warum-Frage: Warum wurde der Fehler gemeldet? \n\nAntwort: Weil die Berechnung Gesamtsumme häufig etwas von der Summe \nder Einzelpositionen abweicht.\n\n2. Warum-Frage: Warum weicht die Gesamtsumme ab? \n\nAntwort: Weil bei der Berechnung von Rabatten für Premiumkunden \nRundungsfehler auftreten.\n\n3. Warum-Frage: Warum treten Rundungsfehler auf?\n\nAntwort: Weil bei der internen Berechnung von Zwischenergebnissen diese \nnicht in der erforderlichen Genauigkeit gespeichert werden.\n\nSchiffmann, J. (2022). Qualitätssicherung im Softwareprozess – Konstruktivistisches Qualitätsmanagement. IU.\n\n03.04.2024\n\n111\n\nFußzeile\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_108.png');
INSERT INTO pdf_pages VALUES(1234,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',109,replace('CHECKLISTEN – WELCHE KRITERIEN MACHEN EINE GUTE \nCHECKLISTE AUS?\n\n1. Ein Punkt auf der Checkliste entspricht genau einem Ziel, zusammengesetzte \nZiele sollten vermieden werden und auf mehrere Punkte aufgeteilt werden.\n\n2. Eine klare und einfache Formulierung der Checkpunkte erhöht die Lesbarkeit und \n\ndie Verständlichkeit.\n\n3. Eine Checkliste sollte nicht größer als ein DIN A4-Blatt sein, sodass sie mit jedem \nDrucker einfach ausgedruckt werden kann und alle Checkpunkte auf einen Blick \nerfasst werden können.\n\n4. Die zur Erfüllung eines Checkpunktes erforderlichen Kriterien müssen \ntransparent sein, bei Bedarf werden zu einzelnen Checkpunkten \nUntercheckpunkte oder eigene Checklisten erstellt.\n\nSchiffmann, J. (2022). Qualitätssicherung im Softwareprozess – Konstruktivistisches Qualitätsmanagement. IU.\n\n03.04.2024\n\n112\n\nFußzeile\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_109.png');
INSERT INTO pdf_pages VALUES(1235,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',110,replace('ÜBUNG KONSTRUKTIVES QUALITÄTSMANAGEMENT\n\nSie treten in ein Unternehmen ein, das Individualsoftware entwickelt: \n▪ Es gibt ein Entwicklerteam, welches auch die Tests durchführt.\n▪Je nach Arbeitslast werden die Entwickler unterschiedlichen Teams zugeordnet. \n▪Bei Fehlermeldungen durch den Kunden wird ein beliebiger Entwickler den Fehler \nbeheben. Jeder Entwickler arbeitet an einem selbst gewählten Arbeitspaket.\n▪Die Entwickler arbeiten mit unterschiedlichen Entwicklungsumgebungen, der \nQuellcode liegt auf einem Server. \n\nWelche konstruktiven Maßnahmen würden Sie einführen? Gruppendiskurs\n\n03.04.2024\n\n113\n\nFußzeile\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_110.png');
INSERT INTO pdf_pages VALUES(1236,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',111,replace('04\n\nSTATISCHE QUALITÄTSSICHERUNG: \nBEGUTACHTUNG UND MESSEN\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_111.png');
INSERT INTO pdf_pages VALUES(1237,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',112,replace('LERNZIELE\n\n▪Einsatzgebiete statischer Verfahren \n\n▪Rollen und Aktivitäten in den verschiedenen Review-Techniken \n\n▪Einsatzgebiete von Metriken & typische Softwaremetriken\n\n▪Statische Codeanalyse\n\n03.04.2024\n\n115\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_112.png');
INSERT INTO pdf_pages VALUES(1238,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',113,replace('STATISCHE CODEANALYSE\n\n▪„Grundsätzlich können alle statischen Analysen auch von Hand \ndurchgeführt werden. \n\n▪Eine manuelle Durchführung besitzt aber deutliche Nachteile: \n▪Neben dem hohen Zeitbedarf und \n▪dem erforderlichen Aufwand \n▪ist damit zu rechnen, dass die Analyseergebnisse eine schlechtere \nQualität besitzen werden als automatisch erzeugte Ergebnisse. “\n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 270\n\n03.04.2024\n\n116\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_113.png');
INSERT INTO pdf_pages VALUES(1239,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',114,replace('STATISCHE CODEANALYSE - GRUNDLAGEN\n\nWas ist die statische Codeanalyse?\n▪Qualitative Bewertung von Programmcode\n▪Inhaltliche Auswertung des Quellcodes\n▪Typischer Anwendungsfall: automatische Stilüberprüfung & Analyse \nnach typischen Fehlermustern\n▪Typische Werkzeuge: CheckStyle, PMD, FindBugs\n\n03.04.2024\n\n117\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_114.png');
INSERT INTO pdf_pages VALUES(1240,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',115,replace('STATISCHE CODEANALYSE\n\n▪Manuelle Prüfungen sind besonders in frühen Phasen der Software-Entwicklung, \nz. B. für die Überprüfung von Entwurfsdokumenten wichtig. \n\n▪Sie können auch ergänzend zu dynamischen Tests auf Quellcode angewandt \nwerden.\n\n▪Eine positive Eigenschaft von manuellen Prüfungen ist, dass sie in der Lage \nsind, semantische Aspekte zu beachten. \n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 305-306\n\n03.04.2024\n\n118\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_115.png');
INSERT INTO pdf_pages VALUES(1241,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',116,replace('STATISCHE CODEANALYSE\n\n▪Die Qualität vieler Merkmale z. B. Verständlichkeit, Änderbarkeit, \nAussagefähigkeit von Bezeichnern und Kommentaren – kann nur manuell \ngeprüft werden.\n\n▪Es bietet sich an, eine Inspektion bzw. ein Review am Ende jeder frühen \nEntwicklungsphase als Zwischenabnahme zu verwenden. So wird sichergestellt, \ndass ein qualitätsgerechtes Ergebnis in die nachfolgende Software-\nEntwicklungsphase übergeben wird. \n\n▪Das Bestehen der Prüfung kann als Meilenstein genutzt werden.\n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 305-306\n\n03.04.2024\n\n119\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_116.png');
INSERT INTO pdf_pages VALUES(1242,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',117,replace('EINSATZ UND ÜBERBLICK ÜBER STATISCHE VERFAHREN\n\nBei statischen Maßnahmen wird das zu testende Artefakt nicht \nausgeführt!\n\nEinsatzgebiete: \n▪fachlichen Anforderungen\n▪technischer Spezifikation / Testfälle \n▪Quality Gates\n▪Bewertung der geplanten Architektur\n▪Komponententests\n\n03.04.2024\n\n120\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\nAnalytische \nMaßnahmen\n\nStatische \nVerfahren\n\nDynamische \nVerfahren\n\nReview\n\nStellungnahme\n\nWalkthrough\n\nInspektion\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_117.png');
INSERT INTO pdf_pages VALUES(1243,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',118,replace('DIE STATISCHE CODEANALYSE FINDET FOLGENDE FEHLER\n\n▪Referenzieren einer Variablen mit nicht definiertem Wert\n▪Typenkonflikte\n▪Inkonsistente Schnittstellen zwischen Modulen und Komponenten\n▪Variablen, die nie verwendet werden\n▪Unerreichbarer (toter) Code (»dead code«)\n▪Verletzung von Programmierkonventionen\n▪Sicherheitsschwachstellen\n▪Syntax-Verletzungen von Code und Softwaremodellen\n\n03.04.2024\n\n121\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_118.png');
INSERT INTO pdf_pages VALUES(1244,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',119,replace('BEGUTACHTEN MIT REVIEW-TECHNIKEN\n\nniedrig\n\nKein formaler Prozess, keine/wenige \nVorgaben an Gutachter\n\nStellungnahme\n\nHauptziel: Kostengünstig & Nutzen erzielen\n\nWalkthrough\n\nInspektion\n\nSchiffmann, J. (2022). Qualitätssicherung im Softwareprozess – Statische Qualitätssicherung. IU.\n\n03.04.2024\n\n122\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\nEinbindung eines Review-Teams, \ndokumentierte Ergebnisse und Abläufe\n\nHauptziele:  Fehlerzustände finden.\nLernen,  Verständnis erwerben, \nhoch\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_119.png');
INSERT INTO pdf_pages VALUES(1245,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',120,replace('EINORDNUNG VON INSPEKTIONEN\n\n▪Inspektionen werden in allen Phasen einer Software-\nEntwicklung eingesetzt. \n\n▪Es gibt Inspektionen von Anforderungsdokumenten, von \nEntwürfen, von Quelltexten und auch von Testfällen.\n\n▪Bei den Prüfkriterien handelt es sich typischerweise um \neinfach prüfbare, syntaktische Regeln. \n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 309\n\n03.04.2024\n\n123\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_120.png');
INSERT INTO pdf_pages VALUES(1246,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',121,replace('ÜBUNG ZU INSPEKTIONEN (DIE STELLUNGSNAHME)\n\n1. Entwerfen Sie im Team eine möglichst präzise Anforderungsbeschreibung (z. B. in \nBezug auf das Referat oder aus ihrer Fallstudie – wählen Sie eine Funktion aus).\n\nAlternativ: Sie erhalten von Ihrem Kunden den Auftrag, eine Software zu \nentwickeln, die die Umrechnung eines Betrags von einer Währung in eine andere \nWährung ermöglicht. Beschreiben Sie diese Funktion präzise.\n\n2. Tauschen Sie die Anforderungsbeschreibung (übersenden) mit einem anderen Team\n3. Nehmen Sie eine Inspektion (ist die Anforderung nachvollziehbar) vor\n\nJe 30 Min., Diskurs über die Learnings\n\n03.04.2024\n\n124\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_121.png');
INSERT INTO pdf_pages VALUES(1247,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',122,replace('ÜBUNG ZU INSPEKTIONEN (FFM/HAN) – TEIL 2\n\nEs tauschen bitte: \n▪Team A mit Team C\n▪Team B mit Team A\n▪Team C mit Team B\n\nJedes Team erstellt bitte eine Zusammenfassung: \n▪Was war verständlich?\n▪Wo ist noch etwas unverständlich?\n▪„Was ging gar nicht“?\n\nDiskurs: Was hat euch komplett gefehlt?\n\n03.04.2024\n\n125\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_122.png');
INSERT INTO pdf_pages VALUES(1248,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',123,replace('SPEZIFIKATIONEN & FEEDBACK\n\n03.04.2024\n\n126\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_123.png');
INSERT INTO pdf_pages VALUES(1249,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',124,replace('REVIEWS\n\n▪Reviews und Inspektionen verlangen die Durchführung einer Review-, bzw. \nInspektionssitzung. Man spricht auch von manuellen Prüfungen in \nSitzungstechnik.\n\n▪Reviewtechniken gibt es unter einer Vielzahl von Bezeichnungen mit zum Teil \nunklaren Abgrenzungen und oft nur geringfügigen Unterschieden. Typische \nBezeichnungen sind Peer Review und Structured Walkthrough. \n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 317\n\n03.04.2024\n\n127\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_124.png');
INSERT INTO pdf_pages VALUES(1250,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',125,replace('ABLAUF VON INSPEKTIONEN UND WALKTHROUGHS\n\nPlanung: \n▪Festlegung von wichtigen \nRahmenbedingungen\n▪Anfangs-, Endbedingungen \n▪Prüfbedingungen \n▪Zusammenstellung des Reviewteams\n▪Einbindung des Autors \n▪Organisatorische Planung \n\n03.04.2024\n\n128\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\nPlanung\n\nVorbesprechung / Kick-off\n\nIndividuelle Vorbereitung\n\nReviewsitzung\n\nNachbearbeitung\n\nBewertung (Follow-up)\n\n▪ Bei einem Walkthrough verpflichtend\n▪ Bei einer Inspektion zusätzlich verpflichtend\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_125.png');
INSERT INTO pdf_pages VALUES(1251,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',126,replace('ABLAUF VON INSPEKTIONEN UND WALKTHROUGHS\n\nVorbesprechung: \n▪Nur falls notwendig \n▪Wann ist es nicht notwendig?\n▪Wann ist alles bekannt? \n▪Alle Beteiligten erhalten die notwendigen \nInformationen!\n\nPlanung\n\nVorbesprechung / Kick-off\n\nIndividuelle Vorbereitung\n\nReviewsitzung\n\nNachbearbeitung\n\n03.04.2024\n\n129\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\nBewertung (Follow-up)\n\n▪ Bei einem Walkthrough verpflichtend\n▪ Bei einer Inspektion zusätzlich verpflichtend\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_126.png');
INSERT INTO pdf_pages VALUES(1252,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',127,replace('ABLAUF VON INSPEKTIONEN UND WALKTHROUGHS\n\n▪Neben dem Dokument, das einem Review \nunterzogen werden soll, müssen den \nbeteiligten Personen weitere Unterlagen zur \nVerfügung stehen: \n▪Dokumente, die herangezogen werden \nmüssen, um zu entscheiden, ob eine \nAbweichung, ein Fehler oder eine korrekte \nBeschreibung des Sachverhalts vorliegt, also \ndie Dokumente (z.B. Pflichtenheft, Richtlinien \noder Standards), gegen die geprüft wird \n\nPlanung\n\nVorbesprechung / Kick-off\n\nIndividuelle Vorbereitung\n\nReviewsitzung\n\nNachbearbeitung\n\nBewertung (Follow-up)\n\n03.04.2024\n\n130\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n▪ Bei einem Walkthrough verpflichtend\n▪ Bei einer Inspektion zusätzlich verpflichtend\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_127.png');
INSERT INTO pdf_pages VALUES(1253,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',128,replace('ABLAUF VON INSPEKTIONEN UND WALKTHROUGHS\n\n▪Neben dem Dokument, das einem Review \nunterzogen werden soll, müssen den \nbeteiligten Personen weitere Unterlagen zur \nVerfügung stehen: \n▪Darüber hinaus sind Prüfkriterien (z.B. in \nForm von Checklisten) sehr sinnvoll, die ein \nstrukturiertes Vorgehen unterstützen \n\nPlanung\n\nVorbesprechung / Kick-off\n\nIndividuelle Vorbereitung\n\nReviewsitzung\n\nNachbearbeitung\n\n03.04.2024\n\n131\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\nBewertung (Follow-up)\n\n▪ Bei einem Walkthrough verpflichtend\n▪ Bei einer Inspektion zusätzlich verpflichtend\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_128.png');
INSERT INTO pdf_pages VALUES(1254,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',129,replace('ABLAUF VON INSPEKTIONEN UND WALKTHROUGHS\n\nReviewsitzung:\n▪In der Reviewsitzung wird der Prüfling \ndurchgegangen und die Punkte aus der \nVorbereitung genannt \n▪Reviewteam gibt Empfehlung über die \nAnnahme des Prüflings ab: \n▪Am Schluss geben alle Sitzungsteilnehmer \ndas Protokoll frei\n▪Der Moderator hat das Recht, eine Sitzung \nabzusagen oder zu vertragen\n\n03.04.2024\n\n132\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\nPlanung\n\nVorbesprechung / Kick-off\n\nIndividuelle Vorbereitung\n\nReviewsitzung\n\nNachbearbeitung\n\nBewertung (Follow-up)\n\n▪ Bei einem Walkthrough verpflichtend\n▪ Bei einer Inspektion zusätzlich verpflichtend\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_129.png');
INSERT INTO pdf_pages VALUES(1255,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',130,replace('ABLAUF VON INSPEKTIONEN UND WALKTHROUGHS\n\nÜberarbeitung (im Rahmen der Reviewsitzung):\n▪Der Autor überarbeitet den Prüfling anhand \ndes Protokolls \n\nPlanung\n\nVorbesprechung / Kick-off\n\nIndividuelle Vorbereitung\n\nReviewsitzung\n\nNachbearbeitung\n\n03.04.2024\n\n133\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\nBewertung (Follow-up)\n\n▪ Bei einem Walkthrough verpflichtend\n▪ Bei einer Inspektion zusätzlich verpflichtend\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_130.png');
INSERT INTO pdf_pages VALUES(1256,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',131,replace('ABLAUF VON INSPEKTIONEN UND WALKTHROUGHS\n\nNachbereitung:\n▪Kontrolle der ordnungsgemäßen \nDurchführung der Überarbeitung \n▪Prüfung, dass die aufgedeckten \nFehlerzustände durch die Korrekturen auch \nbeseitigt wurden\n▪Prüfung von Endkriterien (bei mehr formalen \nReviewarten)\n\nPlanung\n\nVorbesprechung / Kick-off\n\nIndividuelle Vorbereitung\n\nReviewsitzung\n\nNachbearbeitung\n\n03.04.2024\n\n134\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\nBewertung (Follow-up)\n\n▪ Bei einem Walkthrough verpflichtend\n▪ Bei einer Inspektion zusätzlich verpflichtend\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_131.png');
INSERT INTO pdf_pages VALUES(1257,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',132,replace('ABLAUF VON INSPEKTIONEN UND WALKTHROUGHS\n\nNachbereitung:\n▪War das Resultat des ersten Reviews nicht \nakzeptabel und muss ein weiteres Review \ndurchgeführt werden, so ist die hier \nbeschriebene Vorgehensweise für das zweite \nReview entsprechend durchzuführen, jedoch \nmeist verkürzt\n\nPlanung\n\nVorbesprechung / Kick-off\n\nIndividuelle Vorbereitung\n\nReviewsitzung\n\nNachbearbeitung\n\n03.04.2024\n\n135\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\nBewertung (Follow-up)\n\n▪ Bei einem Walkthrough verpflichtend\n▪ Bei einer Inspektion zusätzlich verpflichtend\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_132.png');
INSERT INTO pdf_pages VALUES(1258,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',133,replace('ZUSAMMENFASSUNG VON ROLLEN UND AKTIVITÄTEN\n\nZiele\n\nErgebnis\n\nVerantwortlich\n\nPlanung\n\n▪ Organisation aller benötigten \n\nRessourcen (wie Personen, Termine, \nRäume)\n\n▪ Bestimmung der Prüfziele\n▪ Bestimmung der zu „Bestanden“-\n\nKriterien, \n\n▪ Benennung der Gutachter\n\n▪ Moderator\n\n▪ Mit allen Beteiligten \nallen abgestimmte \nTerminplanung\n▪ Dokumentierte \nPrüfziele und \nBestanden-\nKriterien\n\nVorbesprech-\nung (bei \nBedarf)\n\n▪ Alle Beteiligten erhalten einen Überblick \n\n▪ Jeder Gutachter \n\n▪ Moderator\n\nüber den Prüfling und die Prüfziele.\n\n▪ ggf. Aufteilung der Prüfaspekte / Sichten \n\nder Prüfung auf Gutachter\n\n▪ Verteilung aller benötigten Unterlagen\n▪ Klären von organisatorischen und \n\nfachlichen Fragen\n\nkennt den Prüfling, \ndas Ziel der Prüfung \nund die \nPrüfkriterien, für \ndie er \nverantwortlich ist.\n\nSchiffmann, J. (2022). Qualitätssicherung im Softwareprozess – Statische Qualitätssicherung. IU.\n\n03.04.2024\n\n136\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_133.png');
INSERT INTO pdf_pages VALUES(1259,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',134,replace('ZUSAMMENFASSUNG VON ROLLEN UND AKTIVITÄTEN\n\nIndividuelle \nVorbereitung \nder Gutachter\n\nZiele\n\nErgebnis\n\nVerantwortlich\n\n▪ Prüfling wird durch \n\n▪ Dokumentierte Fragen des \n\n▪ Gutachter\n\nGutachter individuell \ndurchgearbeitet und \nanhand der festgelegten \nKriterien geprüft\n\nGutachters, sowie dokumentierte \ninhaltliche Mängel und formale \nMängel (wie Schreibfehler)\n\nReviewsitzung\n\n▪ Gemeinsames \n\n▪ Protokoll aller identifizierten \n\n▪ Moderator\n\nDurchgehen durch den \nPrüfling durch alle \nBeteiligten\n▪ Diskussion und \n\nfachlicher Mängel\n\n▪ Sammlung aller formalen Fehler \n\n(werden nicht in Sitzung \nbesprochen)\n\nBewertung der durch \nGutachter identifizierten \nfachlichen Mängel\n\n▪ Dokumentiertes Ergebnis (keine \n\nMängel, Nacharbeit nötig, Abbruch \nwegen schwerwiegender Mängel)\n\nSchiffmann, J. (2022). Qualitätssicherung im Softwareprozess – Statische Qualitätssicherung. IU.\n\n03.04.2024\n\n137\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_134.png');
INSERT INTO pdf_pages VALUES(1260,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',135,replace('ZUSAMMENFASSUNG VON ROLLEN UND AKTIVITÄTEN\n\nZiele\n\nErgebnis\n\nVerantwortlich\n\nNachbereitung ▪ Korrigieren der \n\nerkannten Mängel\n\n▪ Bei Inspektion: \n\nMängelbericht und \nErledigungsliste der \nKorrekturen erstellen\n\n▪ Überarbeiteter Prüfling\n▪ Mängelbericht, Erledigungsliste\n\n▪ Autor\n\nBewertung\n\n▪ Prüfen, ob alle \n\n▪ Reviewbericht\n\n▪ Moderator\n\nKorrekturen umgesetzt \nwurden\n\n▪ Abschließende \n\nBewertung des Prüflings\n\nSchiffmann, J. (2022). Qualitätssicherung im Softwareprozess – Statische Qualitätssicherung. IU.\n\n03.04.2024\n\n138\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_135.png');
INSERT INTO pdf_pages VALUES(1261,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',136,replace('GEGENÜBERSTELLUNG VON REVIEW-TECHNIKEN\n\nStellungnahme\n\nWalkthrough\n\nInspektion\n\nCharakteristik\n\n▪ schnelle, \n\n▪ informaler Review durch \n\n▪ formales Review durch \n\nAnwendungs-\nszenarien\n\ninformaler Review \nohne explizite \nOrganisation\n\nmehrere Gutachter\n\nmehrere Gutachter nach \ndefiniertem Schema\n\n▪ Schnelles, \ninformales \nFeedback zum \nPrüfling erhalten\n▪ Internes Feedback \nunter Kollegen \nohne Pflicht zum \nNachweis formaler \nReviewkriterien\n\n▪ Detaillierte Prüfung wichtiger \nArtefakte, in denen nicht \nidentifizierte Fehler ein \ndeutliche merkbaren Schaden \nverursachen können\n\n▪ Schaffung eines einheitlichen \n\nVerständnisses von \nSachverhalten und Förderung \nder Teamkommunikation\n\n▪ Prüfung wichtiger \n\nArtefakte, in denen nicht \nidentifizierte Fehler ein \nsehr hohes \nSchadenspotential haben\n▪ Pflicht zum Nachweis von \nformalen Kriterien der \nPrüfung\n\nSchiffmann, J. (2022). Qualitätssicherung im Softwareprozess – Statische Qualitätssicherung. IU.\n\n03.04.2024\n\n139\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_136.png');
INSERT INTO pdf_pages VALUES(1262,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',137,replace('INSPEKTIONSROLLEN\n\nName der Rolle\n\nStellungnahme\n\nWalkthrough\n\nInspektion\n\nModerator\n\nGutachter\n\nAutor (des Prüflings)\n\nProtokollführer\n\n-\n\nX\n\nX\n\n-\n\nX (bei Bedarf vom Autor \nzur übernehmen)\n\nX\n\nX\n\n-\n\nX\n\nX\n\nX\n\nX\n\nSchiffmann, J. (2022). Qualitätssicherung im Softwareprozess – Statische Qualitätssicherung. IU.\n\n03.04.2024\n\n140\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_137.png');
INSERT INTO pdf_pages VALUES(1263,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',138,replace('AUFGABEN DER ROLLEN\n\n▪ Moderator: \n▪ Erfahrungsgemäß haben Inspektionen eine Tendenz, nach der Findung einiger \n\nFehler in eine Diskussion der Lösungskonzepte abzugleiten. Dies ist \nunerwünscht, da es die Effizienz der Inspektion reduziert. \n\n▪ Der Moderator muss dies verhindern. \n▪ Diskussionen sind nur zu Fehlern und Fehlerarten erlaubt. \n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 311-312\n\n03.04.2024\n\n141\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_138.png');
INSERT INTO pdf_pages VALUES(1264,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',139,replace('AUFGABEN DER ROLLEN\n\n▪ Autor: \n▪ Der Autor ist der Ersteller des inspizierten Produkts. \n▪ Er ist für die Korrektur der während der Inspektion gefundenen Fehler \n\nzuständig.\n\n▪ Gutachter:\n▪ Führt das Inspektionsteam durch die Sitzung. \n▪ Er trägt die technischen Inhalte erläuternd vor. \n▪ Muss in der Lage sein, die unterschiedlichen Teile der Arbeit zu beschreiben. \n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 311-312\n\n03.04.2024\n\n142\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_139.png');
INSERT INTO pdf_pages VALUES(1265,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',140,replace('AUFGABEN DER ROLLEN\n\n▪ Protokollant: \n▪ Der Protokollführer notiert und klassifiziert alle Fehler, die in der Inspektion \n\ngefunden werden. \n\n▪ Darüber hinaus unterstützt er den Moderator bei der Anfertigung der \n\nInspektionsberichte.\n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 311-312\n\n03.04.2024\n\n143\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_140.png');
INSERT INTO pdf_pages VALUES(1266,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',141,replace('FALLSTRICKE VON REVIEWS\n\n1. Fehlende Zeit (Fehleinschätzung bei der Ressourcen-Planung) \n2. Fehlende Vorbereitung der Gutachter\n3. Fehlende oder unklare Ziele, Reviews nicht als Maßnahme zur Qualitätssicherung, \n\nsondern als ‚Anschwärzen‘\n\n4. Rollen und Verantwortlichkeiten werden missverstanden\n5. Fehlende oder unzureichende Dokumentation\n6. Benötigte Dokumente sind nicht verfügbar, wurden in der Vorbereitung übersehen\n7. Benötigtes Personal ist nicht in ausreichendem Maße vorhanden\n8. Personal verfügt nicht über die erforderliche Ausbildung\n9. Fehlende Unterstützung durch das Management\n\n03.04.2024\n\n144\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_141.png');
INSERT INTO pdf_pages VALUES(1267,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',142,replace('FEEDBACK GEBEN – DER FEEDBACK „BURGER“ – ZUR RICHTIGEN \nKOMMUNIKATION VON FEHLERN\n\n1. Positiver Einstieg\n- Du hast das gut gemacht, ...\n- Mir hat besonders gefallen...\n\n2.1 Konkrete Beobachtung\n- Ich habe gesehen/gelesen, dass...\n- Mir ist aufgefallen, dass...\n- Du hast gesagt/geschrieben, dass...\n\n2.2 Wirkung\n- Es hat auf mich gewirkt, als ob... \n- Ich hab verstanden, dass...\n- Ich fand es merkwürdig...\n- Ich habe bemerkt, dass...\n2.3 Wunsch bzw. Vorschlag\n- Ich schlage vor, dass...\n- Ich hätte gerne... \n- Vielleicht kannst du...\n- Warum versuchst\n3. Positiver Abschluss\n- Insgesamt hat mir das gut gefallen,...\n- Mit kleinen Änderungen bin ichsicher, dass die Sache noch runder wird...\n\ndu nicht,...\n\n03.04.2024\n\n145\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\nLob\n\nKritik\n\nLob\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_142.png');
INSERT INTO pdf_pages VALUES(1268,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',143,replace('CODE-REVIEW-ÜBUNG\n\npublic static void main(String[] args) {\nScanner scan = new Scanner(System.in);\nList<Product> products;\nint input = 0; int i = 0;\ndo {\n\ni ++;\nSystem.out.println("Was möchten Sie tun? 1 = Produkt anlegen, \n2 = kommentieren, 3 = beenden");\ninput = scan.nextInt();\nif(input == 1) {\n\nSystem.out.println("Name: ");\nString n = scan.next();\nproducts.add(new Product(i, n, ""));\n\n}\nif(input == 2) {\n\nSystem.out.println("Welches Produkt? (ID)");\nlong id = scan.nextLong();\nProduct p = getProduct(products, id);\nSystem.out.println("Kommentar: ");\nString c = scan.next();\np.comments.add(new Comment(c));\n\n}\n\n}while(input != 3);\n\n}\n\n03.04.2024\n\n146\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\nComment.java\npublic class Comment {\n\npublic Comment(String c) {\ncomment = c;\n}\npublic long ProduktID;\npublic long commentID;\npublic String comment;\npublic String autor;\n\n}\n\nProduct.java\npublic class Product {\n\nprivate long id;\nprivate String name;\nprivate String description;\npublic List<Comment> comments;\npublic Product(long pId, String pName, String description) {\n\nsetId(pId);\nname = pName;\ndescription = description;\n\n}\npublic void setId(long id) {\n\nthis.id = id;\n\n}\n\n}\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_143.png');
INSERT INTO pdf_pages VALUES(1269,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',144,replace('GRUPPENARBEIT\n\n▪Führt eine Inspektion/Walkthrough in eurer Gruppe (4 Personen) durch \n▪Übernehmt die Rollen in der Gruppe (Moderator, Gutachter, Autor -des Prüflings-, \nProtokollführer)\n▪Wir diskutieren und vergleichen am Ende das Protokoll (bzw. den Inspektionsbericht)\n\n▪Lass auch Chat GPT o. ä. den Quellcode prüfen und vergleicht die Ergebnisse mit euren\n\n▪30 Minuten Zeit (+10 Min Prüfung Chat GPT)\n\n03.04.2024\n\n147\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_144.png');
INSERT INTO pdf_pages VALUES(1270,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',145,replace('ERGEBNISSE\n\n▪Gutachter: \n▪Protokollant: \n▪Moderator: \n▪Autor: \n\n03.04.2024\n\n148\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_145.png');
INSERT INTO pdf_pages VALUES(1271,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',146,replace('DISKURS\n\n▪Was sind eure allgemeinen Learning aus dem Vorgehen?\n\n▪Wie würde ein solcher Vorgang im Referat aussehen?\n▪Wie viel Zeit wäre notwendig? \n▪Wie solltet ihr das planen?\n▪Wie kann vorher/nachher sinnvoll dargestellt werden?\n\n03.04.2024\n\n152\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_146.png');
INSERT INTO pdf_pages VALUES(1272,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',147,replace('REMINDER: DEFINITION DER QUALITÄTSZIELBESTIMMUNG\n\n▪„Qualitätsmerkmal, Eigenschaft einer Funktionseinheit, anhand derer ihre Qualität \nbeschrieben und beurteilt wird, die jedoch keine aussage über den Grad der \nAusprägung enthält“ \n\n▪„Qualitätszielbestimmung, Definition der für ein Produkt zu erreichenden Qualität \ndurch Festlegung der angestrebten Ausprägungen der Qualitätseigenschaften. Die \nQualitätszielbestimmung wird zu Beginn der Entwicklung durchgeführt.“ \n\n→ Festlegung erfolgt z. B. im Lasten- / Pflichtenheft\n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 515.\n\n03.04.2024\n\n153\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_147.png');
INSERT INTO pdf_pages VALUES(1273,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',148,replace('METRIK BEGRIFFSDEFINITION\n\n▪Grundsätzlich können Eigenschaften, sowohl von im Softwareprozess \nerzeugten Artefakten als auch von Eigenschaften des Softwareprozesses,  \nselbst gemessen werden. \n\n▪Die Funktion, die aus den Ergebnissen der Vermessung eines Prüflings \neinen Zahlenwert ermittelt, wird Metrik genannt.\n\n03.04.2024\n\n154\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_148.png');
INSERT INTO pdf_pages VALUES(1274,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',149,replace('METRIK PROZESS\n\nBestimmen \nvon \nKenngrößen\n\nMessen der \nWerte zu den \nKenngrößen\n\nBewertung \nder Messung\n\n03.04.2024\n\n155\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_149.png');
INSERT INTO pdf_pages VALUES(1275,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',150,replace('METRIK TYPEN\n\n03.04.2024\n\n156\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\nProzessmetrik\n\nMisst \nEigenschaften des \nErstellungsprozess\n\nProduktmetrik\n\nMisst Eigenschaft \neines Produkts, \nnicht unbedingt \nnur Quellcode\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_150.png');
INSERT INTO pdf_pages VALUES(1276,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',151,replace('METRIK BEISPIELE\n\n▪Verbrauchte Ressourcen (Zeit, Geld, Personal)\n▪Anzahl der identifizierten Fehler im Projekt\n▪Dauer einzelner Aktivitäten\n▪mittlere Behebungszeit von identifizierten Fehlern \n▪Anzahl von Änderungen an einer Systemkomponente\n▪Umfang (Anzahl Seiten, LOC, Anzahl Modellelemente)\n▪Komplexität, \n▪Qualität (Anzahl identifizierter fachlicher Fehler, Anzahl identifizierter formaler Fehler)\n▪Stil (Einhaltung von Konventionen, Lesbarkeit, Redundanzfreiheit)\n\n03.04.2024\n\n157\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_151.png');
INSERT INTO pdf_pages VALUES(1277,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',152,replace('VOR- UND NACHTEILE VON METRIKEN\n\nVorteile\nRelativ einfache Ermittlung\n\nProgrammiersprachenunabhängig\n\nMittel um vielfältige Eigenschaften zu \n\nErgebnisartefakten\n\nNachteile\nNur messbare Ereignisse können \nermittelt werden\n\nKeine Aussagemöglichkeit für Akzeptanz \ndurch Kunden\nEher Indiz für Qualität\n\n03.04.2024\n\n158\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_152.png');
INSERT INTO pdf_pages VALUES(1278,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',153,replace('DISKURS\n\nSie sind für die Qualitätssicherung bei Ihrer Software aus Ihrem \nSoftware Engineering Projekt zuständig:\n\n▪Welche Metriken können Sie während der Entwicklung  zur \nQualitätssicherung nutzen?\n▪Welche Metriken können Sie später zum Reporten zum Management \nnutzen?\n\n03.04.2024\n\n159\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_153.png');
INSERT INTO pdf_pages VALUES(1279,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',154,replace('DATENFLUSSANOMALIEANALYSE\n\n▪Datenflussanomalieanalyse → Datenflussdarstellungen von \nKontrollflussgraphen\n\n▪Es werden Datenflussattribute hinzugefügt, die den Knoten bzw. Kanten des \nKontrollflussgraphen zugeordnet sind und angeben, welche Variablen in \nwelcher Weise zugegriffen werden. \n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 280\n\n03.04.2024\n\n160\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_154.png');
INSERT INTO pdf_pages VALUES(1280,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',155,replace('DATENFLUSSANOMALIEANALYSE\n\n▪Zugriffssequenzen auf eine Variable:\n\n1. u r d r u u\n2. u d d r d u\n\nSequenz 1 beginnt mit u r. Die \nbetrachtete Variable besitzt zum \nZeitpunkt der Referenz einen zufälligen \nWert, da sie nicht zuvor definiert \nwurde. […]\n\n(u: Undefinition, d: Definition, r: Referenz) \n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 295\n\n03.04.2024\n\n161\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_155.png');
INSERT INTO pdf_pages VALUES(1281,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',156,replace('DATENFLUSSANOMALIEANALYSE\n\n▪Zugriffssequenzen auf eine Variable:\n\n1. u r d r u u\n2. u d d r d u\n\nSequenz 2 enthält zwei aufeinanderfolgende \nVariablendefinitionen. Die erste Definitionen \nbesitzt keine Wirkung, da der Wert stets durch \ndie zweite Definition überschrieben wird. Die \nDatenflussanomalie ist vom Typ dd.\n\n(u: Undefinition, d: Definition, r: Referenz) \n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 295\n\n03.04.2024\n\n162\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_156.png');
INSERT INTO pdf_pages VALUES(1282,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',157,replace('CODEBEISPIEL EINER DATENFLUSSANOMALIEANALYSE\n\n▪Bei der Datenflussanomalienanalyse\nreicht es aus, die Pfade bis zum zweiten \nSchleifendurchlauf zu analysieren. \n\n▪Sind bis dort keine Datenflussanomalien \naufgetreten, so ist sichergestellt, dass \nauch auf den Pfaden mit einer höheren \nAnzahl von Schleifeniterationen keine \nAnomalien auftreten werden.\n\npublic static void main(String[] args) {\n\n// varA --> Undefiniert (u)\n\n{\n\n// Definition (d)  \n\ndouble varA = 3.0;\n\n// Referenz (r) und Definition (d)\n\nvarA = varA * 2.0;\n\n// Referenz (r) und Definition (d) \n\nvarA = varA - 3.0;\n\nSystem.out.println(varA);\n\n}\n\n// varA --> Undefiniert (u)\n\nSystem.out.println(varA);\n\n}\n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 298\n\n03.04.2024\n\n163\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_157.png');
INSERT INTO pdf_pages VALUES(1283,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',158,replace('ÜBUNG DATENFLUSSANOMALIEANALYSE -\nKLEINGRUPPENDISKURS\n\nRechenprogramm.java\npublic static void main(String[] args) {\n\ndouble a = Double.parseDouble(args[0]);\ndouble b = Double.parseDouble(args[1]);\ndouble ergebnis = a + b;\nergebnis = a * b;\nSystem.out.println(ergebnis);\nc = a * b;\nergebnis = b / c;\nSystem.out.println(ergebnis);\nergebnis = a + b;\n\n}\n\n03.04.2024\n\n164\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n▪Untersuchen Sie den Datenfluss des \nRechenprogramms auf Anomalien\n▪Welche Anomalien stellen Sie fest?\n▪Wie bewerten Sie diese?\n\n▪10 Min. in Referatsgruppen\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_158.png');
