INSERT INTO pdf_pages VALUES(1286,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',161,replace('LERNZIELE\n\n▪Grundlagen zu Testfall und Teststufen \n▪typischen Techniken zur Testfallerstellung \n▪anwendungsfallbasierte Testfallerstellung \n▪Äquivalenzklassenbildung \n▪zustandsbasierten Testfallerstellung \n▪Zufallsdaten \n\n03.04.2024\n\n167\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_161.png');
INSERT INTO pdf_pages VALUES(1287,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',162,replace('TESTSTUFEN\n\n▪Testen erfolgt (soll erfolgen) \nentwicklungsbegleitend \n▪Teststufen orientieren sich an \nEntwicklungsstufen \n\n▪Qualitätssicherung von Software \ngeschieht in mehreren Teststufen\n▪Welche Eigenschaften getestet \nwerden hängt auch von den \nTeststufen ab\n\nSchiffmann, J. (2022). Qualitätssicherung im Softwareprozess – Dynamische Qualitätssicherung. IU.\n\n03.04.2024\n\n168\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\nKomponententest\n\nIntegrationstest\n\nSystemtest\n\nAbnahmetest\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_162.png');
INSERT INTO pdf_pages VALUES(1288,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',163,replace('TESTFALLSPEZIFIKATION: GRUNDGRÖßE TESTFALL\n\nHandlungsanweisung zur Durchführung von Softwaretests\n\nbesteht (wenigstens) aus folgenden Elementen\n\nTestaktionen \n\nTestdaten \n\nNachbedin-\ngungen \n\nBeschreiben \nDaten zum \nTestfall (z.B. \nName, ID, …) \n\nVorbedin-\ngungen\n\nSchiffmann, J. (2022). Qualitätssicherung im Softwareprozess – Dynamische Qualitätssicherung. IU.\n\n03.04.2024\n\n169\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_163.png');
