INSERT INTO pdf_pages VALUES(1291,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',166,replace('TESTENTWURFSVERFAHREN: WHITE- UND BLACKBOXTESTS\n\nTestentwurfsverfahren\n\nBlackbox Test\n\nWhitebox Test\n\nSpezifikationsorientierte\n\nStrukturorientierte\n\nErfahrungsbasierte\n\nTestentwurfsverfahren\n\nTestentwurfsverfahren\n\nTestentwurfsverfahren\n\n03.04.2024\n\n172\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_166.png');
INSERT INTO pdf_pages VALUES(1292,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',167,replace('TESTENTWURFSVERFAHREN: WHITEBOXTESTS\n\nZiel: Testabdeckung des (bekannten) Quellcodes \n▪meist implementierungsbegleitend / durch Entwickler \n▪auf Komponentenebene \n\nSchritte: \n1. Programmcode implementieren  \n2. Struktur des erzeugten Codes ermitteln\n3. Testfälle auf Basis der bekannte Codestruktur schreiben & ausführen \n4. Testergebnisse beurteilen\n\n03.04.2024\n\n173\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_167.png');
INSERT INTO pdf_pages VALUES(1293,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',168,replace('TESTENTWURFSVERFAHREN: WHITE- UND BLACKBOXTESTS\n\nTestentwurfsverfahren\n\nBlackbox Test\n\nWhitebox Test\n\nSpezifikationsorientierte\n\nStrukturorientierte\n\nErfahrungsbasierte\n\nTestentwurfsverfahren\n\nTestentwurfsverfahren\n\nTestentwurfsverfahren\n\n03.04.2024\n\n174\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_168.png');
INSERT INTO pdf_pages VALUES(1294,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',169,replace('TESTENTWURFSVERFAHREN: BLACKBOXTESTS\n\n▪Anwendernah\n▪Prüfpunkte aus der Spezifikation\n\n1. Spezifikation erhalten\n2. Spezifikation interpretieren (was kann getestet werden)\n3. Testfälle schreiben \n4. Testfälle in der Software ausführen\n5. Testergebnisse beurteilen (ggf. Wiedereinstieg bei 3.)\n\n03.04.2024\n\n175\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_169.png');
