INSERT INTO pdf_pages VALUES(1298,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',173,replace('TESTABDECKUNG\n\n▪Testabdeckung ist das Maß für die Vollständigkeit von Testfällen. \n\n▪Der konkrete Messwert ist abhängig von der Art des Tests und der \ngewählten Technik der Testfallerstellung.\n\n03.04.2024\n\n179\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_173.png');
INSERT INTO pdf_pages VALUES(1299,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',174,replace('ANWENDUNGSFALLBASIERTE TESTFALLERSTELLUNG\n\n▪Ansatzpunkt zur Testfallentwicklung: fachliche Anwendungsfälle \n\n▪Dokumentation z.B. über Use Case Diagramme (ein Use Case \nDiagramm kann dabei mehrere Anwendungsfälle beinhalten) oder \nSequenzdiagramme \n\n▪Mindestanforderung: pro Anwendungsfall ein Testfall \n\n03.04.2024\n\n180\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_174.png');
INSERT INTO pdf_pages VALUES(1300,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',175,replace('TESTFALLAUSWAHL\n\nPrüfen der Anwendungsfälle nach folgenden Kriterien: \n\n▪Wertbeitrag\n▪Nutzungshäufigkeit\n▪Schadenspotential\n▪Typische Fehler\n▪Geforderte Testabdeckung\n\n03.04.2024\n\n181\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_175.png');
INSERT INTO pdf_pages VALUES(1301,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',176,replace('FUNKTIONSORIENTIERTE TESTTECHNIKEN\n\n▪Die funktionsorientierten Testtechniken stellen die Software-\nSpezifikation in den Mittelpunkt des Testgeschehens → Es ist daher \ninsbesondere darauf zu achten, dass eine zum jeweiligen Test \npassende Spezifikation zur Verfügung steht und auch verwendet \nwird. \n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 50\n\n03.04.2024\n\n183\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_176.png');
INSERT INTO pdf_pages VALUES(1302,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',177,replace('FUNKTIONSORIENTIERTE TESTTECHNIKEN\n\n▪Die Spezifikation  dient zur Beurteilung der Vollständigkeit des Tests \nsowie zur Herleitung der Testfälle und der Beurteilung der \nReaktionen der Software. \n\n▪Aufgrund ihrer Herleitung aus der Spezifikation sind die Testfälle \neines funktionsorientierten Tests systematisch an der Überprüfung \nder Soll-Funktionalität ausgerichtet. Daher kann insbesondere \ngeprüft werden, ob die Spezifikation vollständig in Software \numgesetzt wurde.\n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 50\n\n03.04.2024\n\n184\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_177.png');
INSERT INTO pdf_pages VALUES(1303,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',178,replace('FUNKTIONALE ÄQUIVALENZKLASSENBILDUNG (BLACK BOX)\n\n▪Die funktionale Äquivalenzklassenbildung versucht die Komplexität \neines Testproblems durch fortgesetztes Zerlegen so weit zu \nreduzieren, dass schließlich eine sehr einfache Wahl von Testfällen \nmöglich wird. \n\n▪Am Ende der Zerlegung steht eine Menge von so genannten \nÄquivalenzklassen. \n\n▪Alle Werte einer Äquivalenzklasse sollen durch die zu testende \nSoftware gleichartig bearbeitet werden. \n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 58\n\n03.04.2024\n\n185\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_178.png');
INSERT INTO pdf_pages VALUES(1304,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',179,replace('FUNKTIONALE ÄQUIVALENZKLASSENBILDUNG (BLACK BOX)\n\n▪Eingabewerte der Funktionen werden in Bereiche, die gleiches, d.h. \nfachlich identisches Systemverhalten, Verhalten hervorrufen sollen, \neingeteilt. \n\n▪Die funktionale Äquivalenzklassenbildung ist gut geeignet für den \nTest von Software, die nicht zustandsbasiert ist und bei der die \nReaktionen nicht abhängig sind von komplizierten \nEingabeverknüpfungen.\n\nLiggesmeyer, Peter (2009): Software-Qualität. Testen, Analysieren und Verifizieren von Software. 2. Auflage. Heidelberg: Spektrum Akademischer Verlag, Springer. S. 58\n\n03.04.2024\n\n186\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_179.png');
INSERT INTO pdf_pages VALUES(1305,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',180,replace('ÄQUIVALENZKLASSEN - BEISPIEL\n\n▪Gültige Äquivalenzklasse B1: Alle Zahlen \nzwischen 7 und 20: {x |7 <= x <=20 und x \nist ganze Zahl}\n▪Ungültige Äquivalenzklasse B2: Alle \nZahlen kleiner als 7: {x |x < 7}\n▪Ungültige Äquivalenzklasse B3: Alle \nZahlen größer als 20: {x | 20 > x}\n\nSchiffmann, J. (2022). Qualitätssicherung im Softwareprozess – Dynamische Qualitätssicherung. IU.\n\n03.04.2024\n\n187\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_180.png');
INSERT INTO pdf_pages VALUES(1306,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',181,replace('GRENZWERTANALYSE\n\n▪Die der Grenzwertanalyse zu Grunde liegend Annahme ist \ndie Beobachtung, dass Softwarefehler häufig in dem \nGrenzbereich liegen, der den Übergang von gültigen zu \nungültigen Eingabewerten darstellt. \n\n▪Die Fehler liegen an den Grenzen von Äquivalenzklassen.\n\n03.04.2024\n\n188\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_181.png');
INSERT INTO pdf_pages VALUES(1307,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',182,replace('GRENZWERTANALYSE – BEISPIEL ZIGARETTENAUTOMAT\n\nGruppenarbeit: \n\nWie kann die Äkquivalenzklasse für einen Zigarettenautomaten \naussehen?\n\n15 Minuten, Gruppenarbeit\n\nDiskurs-Bonus-Frage: Habt ihr in euren Software Engineering \nProjekten auch Bereich, wo Äkquivalenzklassen möglich sind?\n\n03.04.2024\n\n189\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_182.png');
