INSERT INTO pdf_pages VALUES(1322,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',197,replace('FEEDBACK REFERATE\n\n▪Feedback aus den Gruppen (10 Min. Vorbereitung): \n\n▪Reflexionsfragen:\n▪Welche Fragen zum Leistungsnachweis sind offen?\n▪Welche Methoden wollt ihr einsetzen?\n▪Wie ist der Stand zum Qualitätsplan?\n\n03.04.2024\n\n205\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_197.png');
