INSERT INTO pdf_pages VALUES(1324,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',199,replace('LERNZIELE\n\n1. Aktivitäten zum methodischen Testen \n\n2. Komponententests, inkl. automatische Unit Tests und Test Driven \n\nDevelopment \n\n3.\n\nIntegrationstests, Integrationsstrategien \n\n4. System- und Abnahmetests\n\n03.04.2024\n\n207\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_199.png');
INSERT INTO pdf_pages VALUES(1325,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',200,replace('AKTIVITÄTEN ZUM METHODISCHEN TESTEN\n\n▪Aktivitäten sind in allen Teststufen \nvorhanden \n\n▪Anpassungen je Teststufe bei Inhalt und \nAusgestaltung\n\nQuelle\n\n03.04.2024\n\n208\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\nTestanforderungsanalyse\n\nTestplanung\n\nTestfallspezifikation\n\nTestdatenerstellung\n\nTestausführung \n\nTestauswertung\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_200.png');
INSERT INTO pdf_pages VALUES(1326,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',201,replace('DIE TESTPLANUNG: WELCHE FRAGEN MÜSSEN GEKLÄRT \nWERDEN\n\nWas?\n\nWelche Objekte und Funktionen sind zu testen?\n\nWarum?\n\nWelche Ziele verfolgt der Test?\n\nDer Test\n\nWann?\nWo?\n\nWie?\n\nWomit?\nWer?\n\nWelche Termine sind einzuhalten?\nWo soll getestet werden?\n\nUnter welchen Bedingungen soll getestet werden?\n\nMit welche Mitteln und Werkzeugen soll getestet werden?\nWelche Mitarbeiter führen den Test durch?\n\n03.04.2024\n\n209\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_201.png');
INSERT INTO pdf_pages VALUES(1327,'pdfs/Qualitätssicherung im Softwareprozess SoSe 24.pdf',202,replace('REMINDER: TESTFALLSPEZIFIKATION: GRUNDGRÖßE TESTFALL\n\nHandlungsanweisung zur Durchführung von Softwaretests\n\nbesteht (wenigstens) aus folgenden Elementen\n\nVorbedingun-\ngen \n\nTestaktionen \n\nTestdaten \n\nNachbedin-\ngungen \n\nBeschreiben \nDaten zum \nTestfall (z.B. \nName, ID, …) \n\nSchiffmann, J. (2022). Qualitätssicherung im Softwareprozess – Dynamische Qualitätssicherung. IU.\n\n03.04.2024\n\n210\n\nK. Linke, DSBQSSP: Qualitätssicherung im Softwareprojekt\n\n','\n',char(10)),'imgs/Qualitätssicherung im Softwareprozess SoSe 24_202.png');
