INSERT INTO pdf_pages VALUES(1445,'pdfs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen.pdf',7,replace('EINFÜHRUNG\n\n−Prism ist ein System, mit dessen Hilfe staatliche Einrichtungen die Internetaktivitäten \n\nvon Menschen überwachen können\n− die Daten all jener, die Produkte und Dienstleitungen von u. a. Google, YouTube, \n\nFacebook, Microsoft, Skype, AOL, Yahoo und Apple nutzen\n\n− Aufgedeckt hat Prism der Whistleblower Edward Snowden im Jahre 2013\n\n− staatliche Einrichtung schickt einfach einen Gerichtsbeschluss mit der Datenanforderung, \nin dem der Firma zugleich unter Strafandrohung verboten wird, diese Datenübermittlung \npublik zu machen\n\n04.07.2024\n\n7\n\nJ. Appel, Datenschutz und IT-Sicherheit, SoSe 2024\n\n','\n',char(10)),'imgs/DSBDSITS01_7_Ausgewählte Schutz- und Sicherheitskonzepte für IT-Infrastrukturen_7.png');
